cptr do_cmd_class_power_aux_momoyo(int num, bool only_info)
{
    int i;
	int plev = p_ptr->lev;

	switch (num)
	{


	case 0:
	{
		bool flag = FALSE;
		int count = 2 + plev / 6;
		if (only_info) return "";


		for (i = 0; i<count; i++)
		{
			int s_mode;
			s_mode = one_in_(4) ? SUMMON_ANT : SUMMON_SPIDER;

			if ((summon_specific(-1, py, px, p_ptr->lev, s_mode, (PM_ALLOW_GROUP | PM_FORCE_FRIENDLY))))flag = TRUE;
		}
		if (flag)
			msg_print(_("大量の蟲達を呼び出した！", "You summon a lot of insects!"));
		else
			msg_print(_("何も現れなかった。", "Nobody has appeared."));

		break;
	}

	case 1:
	case 3:
	case 5:
	{
		int v;
		int base = plev / 3;
#ifdef JP
		if (only_info) return format("期間:%d+1d%d", base, base);
#else
        if (only_info) return format("dur: %d+1d%d", base, base);
#endif

		v = base + randint1(base);

		if (num == 1)
		{
			set_tim_general(0, TRUE, 2, 0);
			set_tim_general(0, TRUE, 3, 0);
			set_tim_general(v, FALSE, 1, 0);
		}
		else if(num == 3)
		{
			set_tim_general(0, TRUE, 1, 0);
			set_tim_general(0, TRUE, 3, 0);
			set_tim_general(v, FALSE, 2, 0);
		}
		else
		{
			set_tim_general(0, TRUE, 1, 0);
			set_tim_general(0, TRUE, 2, 0);
			set_tim_general(v, FALSE, 3, 0);

		}

		break;
	}


	case 2:
	{
		int damage;

		if (p_ptr->lev < 40 )
		{
			damage = p_ptr->chp;

			if (damage > 1600) damage = 1600;

#ifdef JP
			if (only_info) return format("損傷:〜%d", damage/2);
#else
            if (only_info) return format("dam: ~%d", damage/2);
#endif

			msg_format(_("あなたの体から毒素が噴き出した！", "You spray toxins out of your body!"));
			project(0, 6, py, px, damage, GF_POLLUTE, (PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL), -1);

		}
		else
		{
			damage = p_ptr->chp / 3;
#ifdef JP
			if (only_info) return format("損傷:%d", damage);
#else
            if (only_info) return format("dam: %d", damage);
#endif

			msg_print(_("ムカデの毒が大地を侵した！", "Centipede poison floods the earth!"));
			project_hack2(GF_POLLUTE, 0, 0, damage);
		}

	}
	break;


	case 4:
	{
		int v;
		int base = 20;
#ifdef JP
		if (only_info) return format("期間:%d+1d%d", base, base);
#else
        if (only_info) return format("dur: %d+1d%d", base, base);
#endif

		v = base + randint1(base);

		set_tim_general(v, FALSE, 0, 0);
		set_magicdef(v, FALSE);

		break;
	}


	case 6:
	{
		int rad = 2;
#ifdef JP
		if (only_info) return format("半径:%d", rad);
#else
        if (only_info) return format("rad: %d", rad);
#endif

		msg_print(_("大量の岩石が積み上がった！", "You stack up tons of rocks!"));
		project(0, rad, py, px, 10, GF_STONE_WALL, (PROJECT_GRID), -1);
		break;
	}

	case 7:
	{
		int dam = p_ptr->csp * 2;
#ifdef JP
		if (only_info) return format("損傷:%d", dam);
#else
        if (only_info) return format("dam: %d", dam);
#endif

		msg_print(_("膨大な魔力を解き放った！", "You unleash overwhelming magical power!"));
		project_hack2(GF_MANA, 0, 0, dam);

		break;
	}


	default:
		if (only_info) return format(_str_unimp);

		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}



