cptr do_cmd_class_power_aux_orin2(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;

	switch (num)
	{
	case 0:
		{
			int rad = 15;
			if (plev > 24) rad += 10;
			if (plev > 34) rad += 15;
			if (only_info) return format(_str_eff_area, rad);

			if(plev < 25)
				msg_print(_("あなたは周囲に漂う幽霊に聞き込みを行った。", "You eavesdrop on nearby floating spirits."));
			else if(plev < 35)
				msg_print(_("あなたは配下の霊たちを偵察に出した。", "You send your spirits to collect information."));
			else
				msg_print(_("無数の霊たちがあなたの元に集い情報を届けた！", "Countless spirits gather information for you!"));

			detect_monsters_normal(rad);
			detect_monsters_invis(rad);
			if (plev > 14) detect_traps(rad, TRUE);
			if (plev > 24) detect_objects_normal(rad);
			if (plev > 34) map_area(rad);
		}
		break;


	case 1:
	{
		if (only_info) return format(_("期間:最長1000ターン", "dur: up to 1000"));
		if (!set_mimic(1000, MIMIC_CAT, FALSE)) return NULL;

		break;
	}

	//追跡
	case 2:
	{
		monster_type *m_ptr;
		int y, x;

		if (only_info) return  format("");

		//隣接モンスター指定
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];

			monster_desc(m_name, m_ptr, 0);

			if (cave[y][x].m_idx == p_ptr->magic_num1[0])
			{
				msg_format(_("すでに追跡中だ。", "You are already in pursuit."));
				return NULL;
			}
			else
			{
				msg_format(_("あなたは%sの追跡を開始した。", "You start pursuing %s."), m_name);
				p_ptr->magic_num1[0] = cave[y][x].m_idx;
			}

		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}


	}
	break;



	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}




