cptr do_cmd_class_power_aux_keiki(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;

	switch (num)
	{

	case 0:
	{
		if (only_info) return "";
		screen_save();
		clear_bldg(2, 22);
		do_cmd_kaji(FALSE);

		screen_load();

	}
	break;
	case 1:
	{
		if (only_info) return format("");

		if (plev < 30)
		{
			if (!ident_spell(FALSE)) return NULL;
		}
		else
		{
			if (!identify_fully(FALSE)) return NULL;
		}
		break;
	}
	case 2:
	{
		bool success = FALSE;
		if (only_info) return format("");

		if (plev < 35)
		{
			if (remove_curse()) success = TRUE;
		}
		else
		{
			if (remove_all_curse())  success = TRUE;
		}
		if (success) msg_print(_("装備品の呪縛を消し去った。", "You remove curses from your equipment."));
		else msg_print(_("何も起こらなかった。", "Nothing happens."));

		break;
	}

	case 3:
	{
		bool flag = FALSE;
		int i;
		int count = 2 + plev / 8;

		if (only_info) return "";

		if (p_ptr->magic_num1[TR_ES_AC] < 500)
		{
			msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
			return NULL;
		}
		p_ptr->magic_num1[TR_ES_AC] -= 500;

		for (i = 0; i<count; i++)
		{
			int r_idx;

			switch (randint1(3))
			{
			case 1:
				r_idx = plev > 39 ? MON_HANIWA_F2 : MON_HANIWA_F1;
				break;
			case 2:
				r_idx = plev > 39 ? MON_HANIWA_A2 : MON_HANIWA_A1;
				break;
			default:
				r_idx = plev > 39 ? MON_HANIWA_C2 : MON_HANIWA_C1;
				break;
			}

			if (summon_named_creature(0, py, px, r_idx, PM_FORCE_PET)) flag = TRUE;

		}
		if (flag)
			msg_print(_("埴輪たちを創り出した。", "You create haniwa."));
		else
			msg_print(_("埴輪を作れる場所がない。", "There's no space to create haniwa."));

		break;
	}

	case 4:
	{
		int rad = 1;
		if (only_info) return format("");

		if (p_ptr->magic_num1[TR_ES_ATTACK] < 500)
		{
			msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
			return NULL;
		}
		p_ptr->magic_num1[TR_ES_ATTACK] -= 500;

		flag_friendly_attack = TRUE;

		if(p_ptr->do_martialarts)
			msg_format(_("あなたの腕は美しい弧を描いた。", "Your hands draw a beautiful arc."));
		else
			msg_format(_("あなたの武器が美しい弧を描いた。", "Your weapon draws a beautiful arc."));


		project(0, rad, py, px, 100, GF_SOULSCULPTURE, PROJECT_KILL, -1);
		flag_friendly_attack = FALSE;
	}
	break;

	case 5:
	{
		int rad = DETECT_RAD_DEFAULT;
		if (only_info) return "";

		msg_format(_("あなたは周囲を見回した。", "You observer your surroundings."));
		map_area(rad);
		detect_traps(rad, TRUE);
		detect_objects_normal(rad);
		detect_monsters_normal(rad);
		detect_monsters_invis(rad);
		probing();
		break;
	}

	case 6:
	{
		if (only_info) return format("");

		if (!use_itemcard)
		{
			if (p_ptr->magic_num1[TR_ES_AC] < 1000)
			{
				msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
				return NULL;
			}
			p_ptr->magic_num1[TR_ES_AC] -= 1000;
		}

		msg_format(_("あなたは瞬く間に壁を築いた。", "You build a wall of stone in an instant."));
		wall_stone();
	}
	break;


	case 7:
	{
		int damage = 0;
		int range = 5 + plev / 10;

		if (object_has_a_blade(&inventory[INVEN_RARM])) damage += calc_weapon_dam(0);
		if (object_has_a_blade(&inventory[INVEN_LARM])) damage += calc_weapon_dam(1);

		if (only_info) return format(_str_eff_range_dam, range, damage);

		if (!damage)
		{
			msg_print(_("刃のついた武器を装備していない。", "You're not wielding an edged weapon."));
			return NULL;
		}
		if (p_ptr->magic_num1[TR_ES_ATTACK] < 1000)
		{
			msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
			return NULL;
		}

		project_length = range;
		if (!get_aim_dir(&dir)) return NULL;

		p_ptr->magic_num1[TR_ES_ATTACK] -= 1000;


		msg_print(_("大地を一直線に切り裂いた！", "You carve out a line in the ground!"));
		fire_beam(GF_MISSILE, dir, damage);
		break;
	}

	case 8: //ランダムユニーク配下生成
			//フロアをまたいで連れ歩けるようにしたかったが、それだとフロア移動のときにランダムユニークが生成されたら上書きされて消えるらしい。対応がちょっとややこしそうなのでパス
	{
		int r_idx;
		int i;
		bool flag1 = TRUE;
		bool flag2 = TRUE;
		bool flag3 = TRUE;
		u32b mode = PM_EPHEMERA;
		int cand_num = 0;

		if (only_info) return "";

		//使われていないランダムユニークIDXを探す
		for (i = 1; i < m_max; i++)
		{
			if (m_list[i].r_idx == MON_RANDOM_UNIQUE_1) flag1 = FALSE;
			if (m_list[i].r_idx == MON_RANDOM_UNIQUE_2) flag2 = FALSE;
			if (m_list[i].r_idx == MON_RANDOM_UNIQUE_3) flag3 = FALSE;

		}
		if (flag1)
			r_idx = MON_RANDOM_UNIQUE_1;
		else if (flag2)
			r_idx = MON_RANDOM_UNIQUE_2;
		else if (flag3)
			r_idx = MON_RANDOM_UNIQUE_3;
		else
			r_idx = 0;

		//消費するエッセンスを決定し、エッセンスを消費してランダムユニーク生成タイプ決定
		if (r_idx)
		{
			int tmp_sum = 0;


			for (i = TR_RES_ACID; i <= TR_RES_TIME; i++)
			{
				if (p_ptr->magic_num1[i] < 50) continue;
				tmp_sum += p_ptr->magic_num1[i];
				cand_num++;
			}
			tmp_sum = randint1(tmp_sum);
			for (i = TR_RES_ACID; i < TR_RES_TIME; i++)
			{
				if (p_ptr->magic_num1[i] < 50) continue;
				tmp_sum -= p_ptr->magic_num1[i];

				if (tmp_sum <= 0) break;
			}

			if(cand_num)special_idol_generate_type = i;

		}

		if(!cand_num)
		{
			msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
			return NULL;
		}

		msg_print(_("あなたは新作の製作に取り掛かった...", "You start working on your next art piece..."));

		if (r_idx && special_idol_generate_type && summon_named_creature(0, py, px, r_idx, mode))
		{
			cptr adj;
			char name_total[256];

			switch (special_idol_generate_type)
			{
			case TR_RES_ACID:
				adj = _("爛れた", "scalding"); break;
			case TR_RES_ELEC:
				adj = _("火花を散らす", "shocking"); break;
			case TR_RES_FIRE:
				adj = _("燃え盛る", "blazing"); break;
			case TR_RES_COLD:
				adj = _("凍り付いた", "freezing"); break;
			case TR_RES_POIS:
				adj = _("毒々しい", "poisonous"); break;
			case TR_RES_LITE:
				adj = _("眩い", "dazzling"); break;
			case TR_RES_DARK:
				adj = _("漆黒の", "pitch-black"); break;
			case TR_RES_SHARDS:
				adj = _("水晶の", "crystal"); break;
			case TR_RES_SOUND:
				adj = _("震える", "trembling"); break;
			case TR_RES_NETHER:
				adj = _("不吉な", "ominous"); break;
			case TR_RES_WATER:
				adj = _("湿った", "moist"); break;
			case TR_RES_CHAOS:
				adj = _("ねじれた", "warped"); break;
			case TR_RES_DISEN:
				adj = _("さえない", "dull"); break;
			case TR_RES_HOLY:
				adj = _("神々しい", "divine"); break;
			default:
				adj = _("奇妙な", "weird");
			}
			msg_format(_("あなたは%s偶像を造り上げた！", "You create a %s sculpture!"),adj);
			//ランダムユニークの名前変更
			sprintf(name_total, _("%s偶像", "%s sculpture"), adj);
			my_strcpy(random_unique_name[r_idx - MON_RANDOM_UNIQUE_1], name_total, sizeof(random_unique_name[r_idx - MON_RANDOM_UNIQUE_1]) - 2);

			p_ptr->magic_num1[special_idol_generate_type] -= 50;
		}
		else
		{
			msg_print(_("偶像の制作に失敗した。", "You fail to create a sculpture."));
		}
		special_idol_generate_type = 0;

		break;

	}




	case 9:
	{
		int i;
		int percentage;
		int base = 20;
		int time;
		bool flag_ok = TRUE;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);


		//腕力〜魅力のエッセンスの必要数をチェック
		for (i = 0; i < 6; i++)
		{
			if (p_ptr->magic_num1[i] < 50)flag_ok = FALSE;
		}
		if (!flag_ok)
		{
			msg_print(_("その特技を使う準備が整っていない。", "You're not ready to use this special ability."));
			return NULL;
		}
		for (i = 0; i < 6; i++)
		{
			p_ptr->magic_num1[i] -= 50;
		}

		msg_print(_("あなたは戦いの準備を整えた。", "You prepare for battle."));

		percentage = p_ptr->chp * 100 / p_ptr->mhp;
		time = base + randint1(base);
		set_tim_addstat(A_STR, 107, time, FALSE);
		set_tim_addstat(A_INT, 107, time, FALSE);
		set_tim_addstat(A_WIS, 107, time, FALSE);
		set_tim_addstat(A_DEX, 107, time, FALSE);
		set_tim_addstat(A_CON, 107, time, FALSE);
		set_tim_addstat(A_CHR, 107, time, FALSE);
		set_mimic(time, MIMIC_DEMON_LORD, FALSE);

		p_ptr->chp = p_ptr->mhp * percentage / 100;
		p_ptr->redraw |= (PR_HP);
		redraw_stuff();


		break;
	}
	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}



