cptr do_cmd_class_power_aux_yachie(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{
	case 0:
	{
		int rad = DETECT_RAD_DEFAULT;
		if (only_info) return format(_str_eff_area, rad);
		detect_monsters_mind(rad);
	}
	break;


	case 1: //勧誘
	{
		int y, x;
		int power = plev + chr_adj;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (only_info) return format(_str_eff_power, power);

		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
			return NULL;
		}

		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);


			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			msg_format(_("あなたは%sを勧誘した。", "You attempt to persuade %s."), m_name);

			if (r_ptr->flagsr & RFR_RES_ALL || r_ptr->flags2 & RF2_EMPTY_MIND || r_ptr->flags1 & RF1_QUESTOR)
			{
				msg_format(_("%sは聞く耳を持たない！", "%s does not listen at all!"), m_name);
			}
			else if (!(m_ptr->mflag & MFLAG_SPECIAL) && (randint1(power) < r_ptr->level || r_ptr->flags1 & (RF1_UNIQUE) || r_ptr->flags7 & RF7_UNIQUE2))
			{
				msg_format(_("%sに断られた。", "%s refuses."), m_name);

				//確率で次の勧誘が確定成功
				if (randint1((r_ptr->flags1 & (RF1_UNIQUE) || r_ptr->flags7 & RF7_UNIQUE2) ? r_ptr->level * 3 : r_ptr->level) < plev)
				{
					msg_format(_("しかし%sの意志が弱くなってきたのを感じる。", "However, you feel the willpower of %s growing weaker."), m_name);
					m_ptr->mflag |= MFLAG_SPECIAL;
				}
			}
			else
			{
				msg_format(_("%sはあなたに従った。", "%s now obeys you."), m_name);
				set_pet(m_ptr);
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;

	case 2://搦手の畜生
	{
		int y, x;
		monster_type *m_ptr;

		if (only_info) return format("");


		if (!get_aim_dir(&dir)) return NULL;
		if (dir != 5 || !target_okay() || !projectable(target_row, target_col, py, px))
		{
			msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                        "You have to pick a target in sight."));
			return NULL;
		}
		y = target_row;
		x = target_col;

		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			int chance = plev * 2 + chr_adj * 4;
			monster_race *r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);

			if (r_ptr->flags1 & RF1_QUESTOR) chance /= 2;
			else if (r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) chance = chance * 2 / 3;

			if (m_ptr->mflag & MFLAG_ISOLATION)
			{
				msg_format(_("既に効いている。", "The ability already is in effect."));

			}
			else if (chance <= r_ptr->level)
			{
				msg_format(_("%sに対して付け込む余地はないようだ。", "Doesn't look like it will work against %s."), m_name);
			}
			else if (randint1(chance) < r_ptr->level)
			{
				msg_format(_("%sに対する工作に失敗した！", "You fail to stir up aggression against %s!"), m_name);
			}
			else
			{
				if(chance > 200)
					msg_format(_("%sを巨大な陰謀の黒幕に仕立て上げた！", "You set up %s as the mastermind of a grand conspiracy!"), m_name);
				else if(chance > 100)
					msg_format(_("%sの事実無根のスキャンダルを流布した！", "You spread false scandalous accusations against %s!"), m_name);
				else
					msg_format(_("フロアに%sの悪評を流した！", "The dungeon level is filled with hostility towards %s!"), m_name);

				m_ptr->mflag |= MFLAG_ISOLATION;
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}

		break;
	}


	case 3:
	{
		int i;
		int summon_num = 1;
		int mode = PM_FORCE_PET;
        if (plev > 34) summon_num += 1 + (plev - 35) / 2;
		if (only_info) return format(_("召喚数:%d", "sum amt: %d"), summon_num);

		msg_print(_("あなたは配下の動物霊を招集した！", "You call for your animal ghost subordinates!"));

		for (i = 0; i < summon_num; i++)
		{
			int attempt = 10;
			int my, mx;
			int r_idx = MON_ANIMAL_G_KIKETSU;

			while (attempt--)
			{
				scatter(&my, &mx, py, px, 3, 0);

				/* Require empty grids */
				if (cave_empty_bold2(my, mx)) break;
			}
			if (attempt < 0) continue;
			summon_named_creature(0, my, mx, r_idx, (mode));
		}

	}
	break;

	case 4:
	{
		int dam;
		dam = p_ptr->chp / 4;
		if (dam<1) dam = 1;
		if (dam > 1600) dam = 1600;

		if (only_info) return format(_str_eff_dam, dam);
		if (!get_aim_dir(&dir)) return NULL;
		msg_print(_("あなたは炎を吐いた！", "You breathe fire!"));
		fire_ball(GF_FIRE, dir, dam, (p_ptr->lev > 35 ? -3 : -2));
		break;
	}

	case 5:
	{
		int rad = plev / 7 - 1;
		int base = p_ptr->lev * 3 + chr_adj * 3;

		if (use_itemcard && base < 100) base = 200;

		if (only_info) return format(_str_eff_dam, base / 2);
		msg_format(_("あなたは周囲の動きを封じようと試みた。", "You attempt to slow down everything around you."));
		project(0, rad, py, px, base, GF_INACT, PROJECT_KILL, -1);
		project(0, rad, py, px, base/4, GF_NO_MOVE, PROJECT_HIDE|PROJECT_KILL, -1);

	}
	break;

	case 6:
	{
		if (only_info) return format("");

		if (p_ptr->magic_num1[0])
		{
			msg_print(_("買収工作を止めた。", "You stop bribing."));
			p_ptr->magic_num1[0] = 0;
		}
		else
		{
			msg_print(_("あなたは配下の動物霊たちに買収工作を命じた...", "You order your animal ghosts subordinates to set up a bribery scheme..."));
			p_ptr->magic_num1[0] = 1;
		}
		p_ptr->redraw |= PR_STATUS;
	}
	break;

	case 7:
	{
		int dice = 5 + chr_adj / 7;
		int sides = plev * 2;

		if (only_info) return format(_str_eff_dam_dice_sides, dice,sides);
		if (!get_aim_dir(&dir)) return NULL;

		msg_print(_("巨大な水弾を叩きつけた！", "You hurl a huge globe of water!"));

		fire_ball(GF_WATER, dir, damroll(dice,sides), 4);

		break;
	}

	case 8:
	{
		int mon_max = 6 ;//最大6体
		int pick_num = 0;
		int i;
		int cnt = 0;
		int idx[6]; //最大6体
		if (only_info) return format(_("対象:%d体", "max %d"),mon_max);

		//do_new_spell_necromancy()の「使嗾」の魔法をコピペして少し改変
		for (i = 1; i < m_max; i++)
		{
			monster_type *m_ptr = &m_list[i];
			if (!m_ptr->r_idx) continue;
			if (!is_pet(m_ptr)) continue;
			pick_num++;
			//呼び出せる以上の数の配下がいたら候補から適当に入れ替える
			if (cnt<mon_max)
				idx[cnt++] = i;
			else if(randint1(pick_num) > cnt)
				idx[randint0(mon_max)] = i;
		}
		if (!pick_num)
		{
			msg_format(_("フロアに配下がいない。", "You don't have followers on this level."));
			return NULL;
		}
		if (!get_aim_dir(&dir)) return NULL;

		msg_format(_("あなたは配下たちに奇襲を命じた！", "You order your follower to attack!"));
		for (i = 0; i<cnt&&i<mon_max; i++)
		{
			monster_type *m_ptr = &m_list[idx[i]];
			teleport_monster_to(idx[i], target_row, target_col, 100, TELEPORT_PASSIVE);
			m_ptr->energy_need = 0;

		}

	}
	break;

	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}


