cptr do_cmd_class_power_aux_saki(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{
	case 0:
	{
		int rad = DETECT_RAD_DEFAULT;
		if (only_info) return format(_str_eff_area, rad);
		detect_monsters_mind(rad);
	}
	break;

	case 1: //勧誘
	{
		int y, x;
		int power = plev * 2 + chr_adj * 2;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (use_itemcard && power < 100) power = 100;

		if (only_info) return format(_str_eff_power, power);

		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You can't use this special ability right now."));
			return NULL;
		}

		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);

			//恐怖しているモンスターに効果増
			if (MON_MONFEAR(m_ptr)) power *= 3;

			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			if(!one_in_(3) || use_itemcard)
				msg_format(_("あなたは%sを勧誘した。", "You persuade %s."), m_name);
			else
				msg_print(_("どうだ、勁牙組に入って貰えないだろうか？", "How about you join the Keiga family?"));

			if (r_ptr->flagsr & RFR_RES_ALL || r_ptr->flags1 & (RF1_UNIQUE | RF1_QUESTOR) || r_ptr->flags7 & RF7_UNIQUE2)
			{
				msg_format(_("%sは聞く耳を持たない！", "%s does not listen at all!"), m_name);
			}
			else if (randint1(power) < r_ptr->level)
			{
				msg_format(_("%sに断られた。", "%s refuses."), m_name);
			}
			else
			{
				msg_format(_("%sはあなたに従った。", "%s now obeys you."), m_name);
				set_pet(m_ptr);
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;

	//ライトニングネイ
	case 2:
	{
		if (only_info) return format("");

		if (!rapid_fire(99,1))
		{
			return NULL;
		}

	}
	break;

	case 3: //デンスクラウド
	{
		int base = 15;
		int v;
		if (only_info) return format(_("期間:%d+1d%dターン", "dur: %d+1d%d"), base, base);

		msg_format(_("濃密な雲が現れ、あなたを包み込んだ...", "Dense clouds appear and surround you..."));
		v = base + randint1(base);
		set_oppose_acid(v, FALSE);
		set_oppose_elec(v, FALSE);
		set_oppose_fire(v, FALSE);
		set_oppose_cold(v, FALSE);
		set_oppose_pois(v, FALSE);
		if (plev > 39) set_shield(v, FALSE);

		break;
	}

	case 4:
	{
		int i;
		int summon_num = plev / 6;
		int mode = PM_FORCE_PET;
        if (only_info) return format(_("召喚数:%d", "sum amt: %d"), summon_num);

		if (plev > 39) mode |= PM_HASTE;

		msg_print(_("あなたは配下の動物霊を招集した！", "You call for your animal ghost subordinates!"));

		for (i = 0; i < summon_num; i++)
		{
			int attempt = 10;
			int my, mx;
			int r_idx = MON_ANIMAL_G_KEIGA;

			while (attempt--)
			{
				scatter(&my, &mx, py, px, 3, 0);

				/* Require empty grids */
				if (cave_empty_bold2(my, mx)) break;
			}
			if (attempt < 0) continue;
			summon_named_creature(0, my, mx, r_idx, (mode));
		}

	}
	break;


	case 5: //町移動
	{
		if (only_info) return format("");




		if (!dun_level)
		{
			msg_print(_("あなたは天高く飛び上がった！", "You jump up high into the sky!"));

			if (!tele_town(TRUE)) return NULL;

		}
		else
		{
			msg_print(_("その技は地上でしか使えない。", "You can only use this ability on surface."));
			return NULL;

		}

	}
	break;

	//スリリングショット　内部的にはラピッドファイアと同じにする
	case 6:
	{
		int power = plev * 3 + chr_adj * 5;
		if (only_info) return format(_str_eff_power,power);

		msg_print(_("あなたは威圧的に銃を構えた...", "You hold your gun with an obvious threat..."));

		if (!rapid_fire(99, 0))
		{
			return NULL;
		}
		turn_monsters(power);

	}
	break;
	//トライアングルチェイス
	case 7:
	{
		int len = plev / 6;
		if (only_info) return format(_str_eff_range, len);
		if (!rush_attack(NULL, len, HISSATSU_TRIANGLE)) return NULL;
		break;
	}
	break;

	//マッスルエクスプロージョン
	case 8:
	{
		int dam = p_ptr->chp / 2;
		if (only_info) return format(_str_eff_dam,dam);

		msg_print(_("必殺の気合いが大地を砕いた！", "The sheer force of your spirit crushes the earth!"));
		project_hack2(GF_FORCE, 0, 0, dam);
		set_tim_addstat(A_STR, 120, 10, FALSE);

		break;
	}


	case 9:
	{
		if (only_info) return _("倍率:2.0", "mult: 2.0");

		special_shooting_mode = SSM_BLACK_PEGASUS;

		if (!do_cmd_fire())
		{
			special_shooting_mode = 0L;
			return NULL;
		}
	}
	break;



	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}


