//石の赤子作成用石塊指定ルーチン
static bool item_tester_hook_stone(object_type *o_ptr)
{
	if (o_ptr->tval == TV_MATERIAL && o_ptr->sval == SV_MATERIAL_STONE) return TRUE;
	else return FALSE;
}

cptr do_cmd_class_power_aux_urumi(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{
	case 0:
		{
			int mult = 1 + (plev + 10) / 20;
			if (only_info) return format(_("倍率:%d", "mult: %d"), mult);
			if (!do_cmd_throw_aux(mult, FALSE, -2)) return NULL;
		}
		break;
	case 1:
		{
			int i;
			int inven_idx;
			bool flag_ok = FALSE;
			monster_type *m_ptr;
			int y, x;
			int power = plev * 3 + chr_adj * 3 + 10;

			if (use_itemcard && power < 150) power = 150;

			if (only_info) return  format(_str_eff_power, power);

			//周囲に水があるかどうか確認
			for (i = 1; i <= 9; i++)
			{
				y = py + ddy[i];
				x = px + ddx[i];

				if (!in_bounds(y, x)) continue;
				if (cave_have_flag_bold(y, x, FF_WATER)) flag_ok = TRUE;
			}

			if (!flag_ok)
			{
				msg_print(_("この技は水辺でないと使えない。", "You have to stand next to water in order to use this ability."));
				return NULL;
			}

			//石の赤子を持っているか確認
			inven_idx = check_have_specific_item(TV_SOUVENIR, SV_SOUVENIR_STONE_BABY);
			if (!use_itemcard && inven_idx < 0)
			{
				msg_print(_("石の赤子が必要だ。", "You require a stone baby."));
				return NULL;
			}

			//隣接モンスター指定
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			//モンスターを沈める
			if (cave[y][x].m_idx && (m_ptr->ml))
			{
				int damage;
				int o_idx;
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];

				monster_desc(m_name, m_ptr, 0);

				if (use_itemcard || !one_in_(4))
					msg_format(_("%sに石の赤子を押し付けた。", "You give a stone baby to %s."),m_name);
				else
					msg_format(_("「ふっふっふ、私の代わりにこの子を見といてくれないか？」",
                                "'Fufufu, won't you carry this baby for me?'"));

				//モンスターが石の赤子を持っている扱いにする(アイテムカード使用時も同じ)
				o_idx = o_pop();
				if (o_idx)
				{
					object_type *o_ptr = &o_list[o_idx];

					object_prep(o_ptr, lookup_kind(TV_SOUVENIR, SV_SOUVENIR_STONE_BABY));

					/* Memorize monster */
					o_ptr->held_m_idx = cave[y][x].m_idx;
					/* Build a stack */
					o_ptr->next_o_idx = m_ptr->hold_o_idx;
					/* Carry object */
					m_ptr->hold_o_idx = o_idx;
				}

				//水没判定
				if ((r_ptr->flags7 & RF7_AQUATIC) || (r_ptr->flagsr & RFR_RES_WATE) ||
					(r_ptr->flags1 & RF1_QUESTOR) || (r_ptr->flags1 & RF1_UNIQUE) || (r_ptr->flags7 & RF7_UNIQUE2))
				{
					power = -1;
				}
				else
				{
					if (r_ptr->flags7 & RF7_CAN_SWIM) power /= 2;
					if (r_ptr->flags2 & RF2_POWERFUL) power /= 2;
					if (r_ptr->flags2 & RF2_GIGANTIC) power /= 2;
				}
				if (randint1(power) > r_ptr->level)
				{
					msg_format(_("%sを水底へ沈めた。", "%s sinks to the bottom."), m_name);
					damage = m_ptr->hp + 1;
					project(0, 0, m_ptr->fy, m_ptr->fx, damage, GF_WATER, PROJECT_KILL, -1);
				}
				else
				{
					msg_format(_("%sは水に沈まなかった！", "%s did not drown!"), m_name);
				}
				anger_monster(m_ptr);
				set_monster_csleep(cave[y][x].m_idx, 0);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			//アイテムカード使用時でないとき、石の赤子消費
			if (!use_itemcard)
			{
				inven_item_increase(inven_idx, -1);
				inven_item_describe(inven_idx);
				inven_item_optimize(inven_idx);
			}

		}
		break;

		//石の赤子生成
		case 2:
		{
			cptr q, s;
			int item;
			object_type forge;
			object_type *q_ptr = &forge;

			if (only_info) return format("");
			item_tester_hook = item_tester_hook_stone;

			q = _("どれを加工しますか? ", "Use what item?");
			s = _("石塊が必要だ。", "You require a stone block.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;

			msg_print(_("あなたは石を削り始めた..", "You start cutting away at the stone..."));
			object_prep(q_ptr, lookup_kind(TV_SOUVENIR, SV_SOUVENIR_STONE_BABY));
			drop_near(q_ptr, -1, py, px);

			if (item >= 0)
			{
				inven_item_increase(item, -1);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -1);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}
		}
		break;

		case 3://武器重量操作 tim_general[0]を使う
		{
			int mult = plev * 8 / 5;
			int time = 10 + plev / 5;
			if (only_info) return format(_("期間:%d 倍率:x%d.%d", "dur: %d mult: x%d.%d"), time,mult/10,mult%10);

			set_tim_general(time, FALSE, 0, 0);

		}
		break;
		case 4: //デーモンシージ(水棲モンスター召喚)
		{
			int i;
			int y, x;
			int summon_num = 2 + plev / 10;
			int type = SUMMON_AQUATIC;
			bool flag_ok = FALSE;
			u32b mode = PM_FORCE_PET;

			if (only_info) return format(_("召喚回数:%d", "sum amt: %d"), summon_num);

			if (!get_aim_dir(&dir)) return NULL;
			if (dir != 5 || !target_okay() || !projectable(target_row, target_col, py, px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			for (i = 0; i < summon_num; i++)
			{
				if (summon_specific(-1, y, x, plev+20, type, mode)) flag_ok = TRUE;
			}

			if (flag_ok)
				msg_print(_("飼い馴らした魚たちを嗾けた！", "You call for your aquatic minions!"));
			else
				msg_print(_("何も現れなかった...", "Nobody has appeared..."));

		}
		break;

		case 5:
		{
			int rad = 1;
			int base = p_ptr->lev * 10 + chr_adj * 10;
			if (only_info) return format(_str_eff_dam, base / 2);
			msg_format(_("近くの全てのものを超重量にした！", "You make everything around yourself extremely heavy!"));
			project(0, rad, py, px, base, GF_INACT, PROJECT_KILL, -1);
			project(0, rad, py, px, base, GF_DEC_ATK, PROJECT_KILL|PROJECT_HIDE, -1);

		}
		break;

		//ヘビーストーンベイビー
		case 6:
		{
			int inven_idx;
			int dec_speed = 15;
			monster_type *m_ptr;
			int y, x;

			if (only_info) return  format(_("減速:%d", "slow: %d"),dec_speed);

			//石の赤子を持っているか確認
			inven_idx = check_have_specific_item(TV_SOUVENIR, SV_SOUVENIR_STONE_BABY);
			if (!use_itemcard && inven_idx < 0)
			{
				msg_print(_("石の赤子が必要だ。", "You require a stone baby."));
				return NULL;
			}

			//隣接モンスター指定
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			//モンスターを減速させる
			if (cave[y][x].m_idx && (m_ptr->ml))
			{
				int o_idx;
				char m_name[80];

				monster_desc(m_name, m_ptr, 0);

				if (m_ptr->mflag & MFLAG_SPECIAL)
				{
					msg_format(_("すでに効いている。", "The ability already is in effect."));
					return NULL;
				}
				else
				{
					msg_format(_("%sに石の赤子が貼り付き、どんどん重くなっていく！", "The stone baby sticks to %s, and starts growing heavier and heavier!"), m_name);
				}

				//モンスターが石の赤子を持っている扱いにする
				o_idx = o_pop();
				if (o_idx)
				{
					object_type *o_ptr = &o_list[o_idx];

					object_prep(o_ptr, lookup_kind(TV_SOUVENIR, SV_SOUVENIR_STONE_BABY));

					/* Memorize monster */
					o_ptr->held_m_idx = cave[y][x].m_idx;
					/* Build a stack */
					o_ptr->next_o_idx = m_ptr->hold_o_idx;
					/* Carry object */
					m_ptr->hold_o_idx = o_idx;
				}

				m_ptr->mflag |= MFLAG_SPECIAL; //特殊フラグを立てて二度目以降を無効に
				if(m_ptr->mspeed > 20) m_ptr->mspeed -= dec_speed;

				anger_monster(m_ptr);
				set_monster_csleep(cave[y][x].m_idx, 0);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			//アイテムカード使用時でないとき、石の赤子消費
			if (!use_itemcard)
			{
				inven_item_increase(inven_idx, -1);
				inven_item_describe(inven_idx);
				inven_item_optimize(inven_idx);
			}

		}
		break;


	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}


