cptr do_cmd_class_power_aux_kutaka(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{

	case 0://周辺調査
	{
		int rad = DETECT_RAD_DEFAULT;
		if (only_info) return format(_str_eff_area, rad);

		msg_print(_("あなたは敵の気配を探った...", "You search for presence of enemies..."));
		detect_monsters_normal(rad);
		detect_monsters_invis(rad);
		if (plev > 14)
		{
			detect_traps(rad, TRUE);
		}
		if (plev > 24)
		{
			detect_objects_gold(rad);
			detect_objects_normal(rad);
		}
		if (plev > 34)
		{
			map_area(rad);
		}


		break;
	}
	case 1://鳥頭1
	{
		if (only_info) return "";


		if (p_ptr->afraid || p_ptr->stat_cur[A_INT] != p_ptr->stat_max[A_INT] || p_ptr->stat_cur[A_WIS] != p_ptr->stat_max[A_WIS])
		{
			msg_format(_("あなたは嫌なことを忘れてしまうことにした。",
                        "You decide to forget what you don't like."));
			set_afraid(0);
			do_res_stat(A_INT);
			do_res_stat(A_WIS);

		}
		else if(plev > 29)
		{
			msg_format(_("あなたは頭を空っぽにした...", "You make your head empty..."));
			if (player_gain_mana(randint1(3 + plev / 7))) msg_print(_("頭がスッキリした。", "Your head feels clear."));
		}

		if (randint1(plev+5) > plev)
		{
			if (lose_all_info()) msg_print(_("...？　うっかり色々忘れすぎたようだ。", "...? You accidentally forgot too much."));
		}

		break;
	}


	case 2://水配り
	{
		int dist = 2 + plev / 5;
		if (only_info) return format(_(" 射程:%d", "rng: %d"), dist);
		project_length = dist;
		if (!get_aim_dir(&dir)) return NULL;
		msg_format(_("あなたはこの地に水路を引いた。", "You carve a waterway on the ground."));
		fire_beam(GF_WATER_FLOW, dir, 10);
		break;
	}
	case 3://見渡し
	{
		int power = 10 + p_ptr->lev * 3;
		if (only_info) return format(_str_eff_power, power);

		msg_format(_("あなたは幻惑的な光を放った。", "You emit mesmerising light."));
		lite_area(damroll(2,15), 3);
		confuse_monsters(power);
		break;
	}

	case 4://鬼渡し
	{
		int power = p_ptr->lev * 4;
		if (only_info) return format(_str_eff_power, power);

		msg_format(_("あなたは無法者たちを追い払った。", "You expel the outlaws."));
		banish_monsters(power);

	}
	break;

	case 5:
	{
		int dam = plev * 5;
		int summon_num = randint1(3);
		if (only_info) return format(_str_eff_dam, dam);

		if (one_in_(2))
			msg_print(_("「コ ケ コ ッ コ ー ！」", "'Cock-a-doodle-doo!'"));
		else
			msg_print(_("あなたは死者すら飛び起きるような大音声を放った！",
                        "You shout loudly enough to wake the dead!"));

		project_hack2(GF_SOUND, 0, 0, dam);
		aggravate_monsters(0, FALSE);
		for (; summon_num; summon_num--)
		{
			summon_specific(0, py, px, dun_level, SUMMON_GHOST, (PM_FORCE_FRIENDLY | PM_ALLOW_GROUP));
		}

	}
	break;

	case 6://選択的記憶除去
	{
		if (only_info) return "";

		msg_print(_("あなたは複雑な忘却のために集中を始めた...", "You intently focus on forgetting..."));
		flag_spell_forget = TRUE;

		do_cmd_cast();

		flag_spell_forget = FALSE;

	}
	break;


	case 7: //血の分水嶺
	{
		int v;
		bool base = 10;
		int percentage = p_ptr->chp * 100 / p_ptr->mhp;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);

		v = base + randint1(base);
		if(one_in_(3))
			msg_print(_("あなたは不退転の覚悟を決めた！", "You feel the resolve to stay undefeated!"));
		else if (one_in_(2))
			msg_print(_("あなたはここを死守する決意を固めた！", "You find the resolve to stay here to the end!"));
		else
			msg_print(_("「コケー！」「ピヨ！」", "'Cock-a-doodle-doo!' 'Chirp!'"));

		set_hero(v, FALSE);
		set_tim_addstat(A_STR, 104, v, FALSE);
		set_tim_addstat(A_CON, 104, v, FALSE);
		set_tim_addstat(A_DEX, 104, v, FALSE);
		set_nennbaku(v, FALSE);
		p_ptr->chp = p_ptr->mhp * percentage / 100;
		p_ptr->redraw |= (PR_HP);
		redraw_stuff();

		break;
	}

	case 8: //獄界視線
	{

		int power = p_ptr->lev * 2 + chr_adj * 5;
		if (only_info) return format(_str_eff_power, power);

		msg_format(_("あなたの鋭い眼光が周囲を射竦めた！", "You glare intently around you!"));
		project_hack(GF_REDEYE, power);
		stasis_monsters(power);

		break;
	}
	case 9://全霊鬼渡り
	{

		int rad = plev;
		int power = p_ptr->lev * 2 + chr_adj * 5;
		if (only_info) return format(_("範囲:%d 効力:%d", "rad: %d pow: %d"),rad, power);

		msg_format(_("あなたは招かれざる者たちをこの地から放逐した！", "You banish the uninvited beings from this land!"));
		mass_genocide_2(power, rad, 1);

		break;
	}

	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}



