cptr do_cmd_class_power_aux_mayumi(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch (num)
	{

	case 0:
	{
		int rad = DETECT_RAD_DEFAULT;
		if (only_info) return format(_str_eff_area, rad);

		msg_print(_("あなたは敵の気配を探った...", "You search for presence of enemies..."));
		detect_monsters_normal(rad);
		detect_monsters_invis(rad);

		break;
	}
	case 1:
	{
		if (only_info) return "";

		msg_format(_("あなたは眼前の敵を注意深く見定めた...", "You carefully analyse the enemies before you..."));
		probing();
		break;
	}
	case 2:
	case 3:
	case 4:
	{
		bool flag = FALSE;
		int i;
		int count = 2 + plev / 8;


		if (only_info) return "";
		for (i = 0; i<count; i++)
		{
			int r_idx;

			if (num == 2) r_idx = plev > 39 ? MON_HANIWA_F2 : MON_HANIWA_F1;
			else if (num == 3) r_idx = plev > 39 ? MON_HANIWA_A2 : MON_HANIWA_A1;
			else r_idx = plev > 39 ? MON_HANIWA_C2 : MON_HANIWA_C1;

			if (summon_named_creature(0, py, px, r_idx, PM_FORCE_PET)) flag = TRUE;

		}
		if (flag)
			msg_print(_("部下達を召集した。", "You call your subordinates."));
		else
			msg_print(_("誰も現れなかった...", "Nobody has appeared..."));



		break;
	}

	case 5:
	{
		int i,v;
		if (only_info) return "";

		v = 100 + randint1(100);

		msg_print(_("あなたは兵団に突撃命令を下した！", "You order your troops to start an assault!"));
		//周囲の配下の埴輪を加速、それ以外を恐怖にする
		crusade();
		//周囲に埴輪軍団を召喚する
		for (i = 0; i < 30; i++)
		{
			int attempt = 10;
			int my, mx;
			int r_idx;

			if ((i % 3) == 0) r_idx = MON_HANIWA_F2;
			else if ((i % 3) == 1) r_idx = MON_HANIWA_A2;
			else r_idx = MON_HANIWA_C2;

			while (attempt--)
			{
				scatter(&my, &mx, py, px, 5, 0);

				/* Require empty grids */
				if (cave_empty_bold2(my, mx)) break;
			}
			if (attempt < 0) continue;
			summon_named_creature(0, my, mx, r_idx, (PM_FORCE_PET | PM_HASTE));
		}
		set_hero(v, FALSE);
		set_blessed(v, FALSE);
		set_fast(v, FALSE);
		set_protevil(v, FALSE);
		set_afraid(0);
		set_broken(-10000);
		hp_player(5000);

	}
	break;

	default:
	{
		if (only_info) return _str_unimp;

		msg_format(_str_unimp_error, num);
		return NULL;
	}

	}
	return "";
}






