cptr do_cmd_class_power_aux_oni_kasen(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	switch (num)
	{
	case 0:
		{
			object_type forge;
			object_type *tmp_o_ptr = &forge;
			int ty, tx;

			if (only_info) return "";

			if (!get_aim_dir(&dir)) return NULL;
			tx = px + 99 * ddx[dir];
			ty = py + 99 * ddy[dir];
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			msg_print(_("地面から石塊を掘り出して投げつけた！","You rip out a large block of stone from the ground and throw it!"));
			object_prep(tmp_o_ptr, lookup_kind(TV_MATERIAL, SV_MATERIAL_STONE));
			do_cmd_throw_aux2(py, px, ty, tx, 3, tmp_o_ptr, 0);

		}
		break;
	case 1: //強力投擲
		{
			int mult = 2 + plev / 15;
			if (only_info) return format(_("倍率:%d", "mult: %d"), mult);
			if (!do_cmd_throw_aux(mult, FALSE, -1)) return NULL;
		}
		break;
	case 2: //スケルトン召喚
		{
			int i;
			int summon_num = 2 + plev / 10;
			int type = SUMMON_SKELETON;
			bool flag_ok = FALSE;
			u32b mode = PM_FORCE_PET;
			if (plev > 34) mode |= PM_ALLOW_GROUP;

			if (only_info) return format(_("召喚回数:%d", "sum amt: %d"), summon_num);


			for (i = 0; i < summon_num; i++)
			{
				if (summon_specific(-1, py, px, plev, type, mode)) flag_ok = TRUE;
			}

			if(flag_ok)
				msg_print(_("地面から骸骨が這い出し、あなたに従った。", "Skeletons rise up from the ground to serve you."));
			else
				msg_print(_("地面が少し動いた気がするが、何も起こらなかった。", "You feel the ground move slightly, but nothing else happens."));

		}
		break;
	case 3:
		{
			int damage;

			if (p_ptr->do_martialarts)
			{
				damage = (calc_martialarts_dam_x100(0) * p_ptr->num_blow[0] + calc_martialarts_dam_x100(1) * p_ptr->num_blow[1]) / 100;
			}
			else
			{
				damage = (calc_weapon_dam(0) + calc_weapon_dam(1));
			}

			if (p_ptr->lev < 45)
			{
				if (only_info) return format(_("損傷:およそ〜%d", "dam: ~%d"), damage);

				msg_format(_("あなたは地面に%sを打ち付けた！", "You slam the ground with your %s!"), (p_ptr->do_martialarts ?
                                                                                                        _("拳", "fist"):_("武器", "weapon")));
				project(0, 3, py, px, damage, GF_SOUND, (PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL), -1);

			}
			else
			{
				if (only_info) return format(_("損傷:およそ%d", "dam: about %d"), damage);
				msg_print(_("怪腕の一撃が大地を揺るがした！","You make the earth tremble with a mighty blow!"));
				project_hack2(GF_SOUND, 0, 0, damage);
			}
			aggravate_monsters(0,FALSE);
		}
		break;
	case 4:
		{
			int power = plev + chr_adj * 5;

			if (only_info) return format(_str_eff_power, power);

			msg_print(_("あなたは冷たい目で周囲を睨みつけた...", "You glare around with cold eyes..."));
			charm_undead(power);

		}
		break;
	case 5:
	{

		int dir,damage;
		int rad = 6;

		damage = plev * 4 + chr_adj * 10;

		if (only_info) return format(_str_eff_dam, damage);

		if (!get_aim_dir(&dir)) return NULL;
		if (!fire_ball_jump(GF_NETHER, dir, damage, rad, _("山のような骸骨が降り注いだ！", "A mountain of skeletons pours down!"))) return NULL;

	}
	break;
	case 6:
	{
		int power = plev * 7 + chr_adj * 5;
		if (power < 300) power = 300;
		if (only_info) return format(_str_eff_power, power / 2);


		msg_print(_("無間地獄への門が開いた！", "You open the gates to Hell of Avici!"));

		project(0, 1, py, px, power, GF_SOULSTEAL, PROJECT_KILL, -1);


	}
	break;


	default:
		if (only_info) return _str_unimp;
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}



