/*:::瓔花用特技*/
cptr do_cmd_class_power_aux_eika(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;

	switch (num)
	{
	case 0:
	{
		if (only_info) return "";
		msg_print(_("あなたは心を込めて一つ一つ石を積んだ...", "You put your heart into stacking one stone onto another..."));
		warding_glyph();
	}
	break;
	case 1:
	{
		int rad = plev / 5;
		if (only_info) return format(_("半径:%d", "rad: %d"), rad);
		msg_format(_("ダンジョンが揺れた！", "The dungeon trembles!"));
		earthquake(py, px, rad);

		break;
	}

	case 2:
	{
		int y, x;

		if (only_info) return format("");
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];

		if (!cave_naked_bold(y, x))
		{
			msg_print(_("ここには石を積めない。", "You can't stack stones here."));
			return NULL;
		}
		msg_print(_("あなたは瞬く間に石壁を積み上げた！", "You pile up a wall of stones in an instant!"));
		cave_set_feat(y, x, f_tag_to_index_in_init("RUBBLE"));

		break;
	}


	case 3:
	{
		int rad = plev / 5 - 6;

		if (only_info) return format(_("半径:%d", "rad: %d"), rad);
		msg_format(_("あなたは子供霊たちと石積みコンテストを始めた...", "You start a stone stacking contest with infant spirits..."));
		project(0, rad, py, px, rad*2, GF_MAKE_GLYPH, PROJECT_GRID, -1);
		break;
	}

	default:
		if (only_info) return _str_unimp;
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}




