// スーパー貧乏神への変身カウンタにtim_general[0]を使用する
// 前回変身したときのターン数をmagic_num1[0]に記録する
// 前回変身してから周囲で倒れたモンスターの経験値をmagic_num1[1]に記録する
// モンスターに憑依した回数をmagic_num1[2]に記録する
// スーパー貧乏神の状態でJを倒したフラグにmagic_num2[0]を使う
cptr do_cmd_class_power_aux_shion(int num, bool only_info)
{
	int dir, base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{

	case 0:

	{
		int x, y;
		int dist = 6 + plev / 8;
	//	int time = 50 + plev;
		monster_type *m_ptr;
		char m_name[80];


		if (only_info) return format(_str_eff_range, dist);
		project_length = dist;
		if (!get_aim_dir(&dir)) return NULL;

		x = target_col;
		y = target_row;

		if (dir != 5 || !target_okay() || !projectable(y, x, py, px))
		{
			msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                        "You have to pick a target in sight."));
			return NULL;
		}
		project_length = 0;

		m_ptr = &m_list[cave[y][x].m_idx];

		if (!m_ptr->r_idx || !m_ptr->ml)
		{
			msg_print(_str_mess_nobody_here);
			return NULL;
		}
		else
		{
			int r_idx = m_ptr->r_idx;
			int chance;
			monster_race *r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);


			chance = (plev + chr_adj) * 100 / MAX(r_ptr->level,1);
			if (MON_CSLEEP(m_ptr)) chance = chance * 2;
			if (r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) chance /= 2;

			if (p_ptr->wizard) chance = 100;
			if (chance > 100) chance = 100;
			if (r_ptr->flags1 & RF1_QUESTOR) chance = 0;
			if (r_ptr->flagsr & RFR_RES_ALL) chance = 0;

			if (m_ptr->r_idx == MON_TENSHI) chance = 0; //天子は心に隙がないそうなので成功率0にしとく。それなら他の天人月人神様連中はどうなのと思うが気にしないことにする

			if (!get_check_strict(format(_("取り憑きますか？(成功率:%d%%)",
                                            "Possess? (success rate: %d%%)"), chance), CHECK_DEFAULT_Y)) return NULL;
			//成功するとモンスターを削除しそのモンスターに変身
			if (randint0(100) < chance)
			{
				int mon_old_hp = m_ptr->hp;

				check_quest_completion(m_ptr);
				delete_monster_idx(cave[y][x].m_idx);
				move_player_effect(y, x, MPE_DONT_PICKUP);
				metamorphose_to_monster(r_idx,-1);

				if (p_ptr->chp < mon_old_hp)
				{
					p_ptr->chp = MIN(p_ptr->mhp,mon_old_hp);
					p_ptr->redraw |= PR_HP;
					redraw_stuff();
				}

				//憑依成功したら打倒済みにする
				if (r_ptr->r_akills < MAX_SHORT) r_ptr->r_akills++;
				if (r_ptr->r_pkills < MAX_SHORT) r_ptr->r_pkills++;
				if (r_ptr->flags1 & RF1_UNIQUE) r_ptr->max_num = 0;


				gain_exp(r_ptr->mexp);
				//運気を吸い取ったものとし、不運パワーを蓄積する
				p_ptr->magic_num1[1] += r_ptr->mexp * 3;
				//憑依回数カウント
				p_ptr->magic_num1[2]++;



			}
			else
			{
				if (chance < 1)
					msg_format(_("%sの心には全く隙がない！", "You can't find a single crack in heart of %s!"), m_name);
				else
					msg_print(_("憑依に失敗した！", "You fail to perform the possession!"));
				//起こす
				set_monster_csleep(cave[y][x].m_idx, 0);
				if (is_friendly(m_ptr))
				{
					msg_format(_("%sは怒った!", "%s gets angry!"),m_name);
					set_hostile(m_ptr);
				}

			}
		}
	}
	break;

	case 1:
	{
		if (only_info) return "";
		if (!shion_begging()) return NULL;
		break;
	}
	case 2:
	{
		if (only_info) return "";
		if (!word_of_recall()) return NULL;
		break;
	}

	case 3:
	{
		if (only_info) return format(_("効果:不定", "eff: undef"));

		if (one_in_(4) && !p_ptr->inside_quest && dun_level)
		{
			teleport_level(0);
		}
		else
		{
			teleport_player(randint1(200), 0L);
		}

		break;
	}

	case 4:
	{
		int v;
		base = plev / 5;
		if (only_info) return format(_str_eff_dur_plus_1d, base,base);

		msg_print(_("あなたの姿がぼやけた...", "Your form becomes hazy..."));
		v = randint1(base) + base;
		set_tim_stealth(v, FALSE);
		if (plev > 39) set_kabenuke(v, FALSE);

	}
	break;

	case 5:
	{
		int v = CALC_SUPER_SHION_TURNS;


		if (only_info) return format(_("期間:%d turn", "dur: %d"), v);

		if(SUPER_SHION)
		{
			msg_print(_("すでに変身している。", "You already are transformed."));
			return NULL;
		}

		if (v < 5)
		{
			msg_print(_("まだ十分な不運パワーが溜まっていない。", "You haven't gathered enough misfortune power yet."));
			return NULL;
		}

		set_tim_general(v, FALSE, 0, 0);

		//変身時にmagic_num1[0]を現在ターン数にし[1]をリセット
		p_ptr->magic_num1[0] = turn;
		p_ptr->magic_num1[1] = 0;

		//HP全回復
		hp_player(9999);


		break;
	}
	case 6:
	{
		int pows = p_ptr->magic_num1[1] / SHION_BINBOUDAMA;
		int dam = 250 + plev * 5;


		if (only_info) return format(_("損傷:%d 使用回数:%d", "dam: %d uses: %d"), dam,pows);

		if (!pows)
		{
			msg_print(_("十分な不幸パワーが溜まっていない。", "You don't have enough misfortune power."));
			return NULL;
		}

		if (!get_aim_dir(&dir)) return NULL;

		msg_format(_("皆から集めた不幸パワーを叩き付けた！","You unleash power of misfortune gathered from everyone!"));
		fire_ball(GF_DARK, dir, dam, 4);

		p_ptr->magic_num1[1] -= SHION_BINBOUDAMA;

	}
	break;

	case 7:
	{
		int power = p_ptr->lev * 10;
		if (power < 300) power = 300;

		if (only_info) return format(_str_eff_power, power);

		if (use_itemcard) msg_print(_("カードの中の鉢から破滅のオーラが噴き出した！", "A ruinous aura erupts from the bowl in the card!"));
		else msg_print(_("あなたの持つ鉢から破滅のオーラが噴き出した！", "Ruins aura erupts from your bowl!"));

		project_hack(GF_HAND_DOOM, power);

		break;
	}





	default:
		if (only_info) return format(_str_unimp);
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}



