cptr do_cmd_class_power_aux_jyoon(int num, bool only_info)
{
	int dir, damage;
	int plev = p_ptr->lev;

	switch (num)
	{

	case 0:
	{
		if (only_info) return format("");
		if (!put_item_into_inven2()) return NULL;

		p_ptr->update |= PU_BONUS;
		update_stuff();
		break;

	}
	case 1:
	{
		if (only_info) return format("");
		if (!takeout_inven2()) return NULL;
		p_ptr->update |= PU_BONUS;
		update_stuff();
		break;
	}
	case 2:
	{
		if (only_info) return format("");
		if (!activate_inven2()) return NULL;
		break;
	}

	case 3:
	{
		cptr q, s;
		object_type *o_ptr;
		int item;
		int value;
		if (only_info) return "";

		q = _("どのアイテムを確認しますか？", "Appraise which item?");
		s = _("アイテムがない。", "You don't have items.");
		if (!get_item(&item, q, s, (USE_INVEN | USE_EQUIP | USE_FLOOR))) return NULL;
		if (item >= 0)
		{
			o_ptr = &inventory[item];
		}
		else
		{
			o_ptr = &o_list[0 - item];
		}
		value = object_value_real(o_ptr);

		if (!value)
		{
			msg_format(_("このアイテムは無価値だ。", "This item is worthless."));
			if (object_is_cursed(o_ptr))
				msg_format(_("その上呪われている。", "Also, it's cursed."));
		}
		else
		{
#ifdef JP
			msg_format("このアイテムには%s$%dの価値があるようだ。", ((o_ptr->number>1) ? "一つあたり" : ""), value);
#else
            msg_format("%s costs $%d.", ((o_ptr->number>1) ? "Each of those items" : "This item"), value);
#endif
			if (object_is_cursed(o_ptr))
				msg_format(_("しかし呪われている。", "However, it's cursed."));

		}

		//if (object_is_cheap_to_jyoon(o_ptr))
		//	msg_format("あなたにとっては身につけるに値しない安物だ。");

		break;
	}


	case 4:
	{
		int range = 25 + plev / 2;
		if (only_info) return format(_str_eff_area, range);

		detect_objects_gold(range);
		detect_objects_normal(range);

		if (plev > 29)
			detect_monsters_rich(range);

		break;
	}


	case 5:
	{
		if (only_info) return format("");

		if (p_ptr->special_attack & ATTACK_WASTE_MONEY)
		{
			msg_print(_("散財を止めた。", "You stop wasting money."));
			p_ptr->special_attack &= ~(ATTACK_WASTE_MONEY);
		}
		else if (p_ptr->au < 1000) //v1.1.47 条件追加
		{
			msg_print(_("先立つものがない。もっと稼がないと。",
                        "You have to acquire more money first."));

		}
		else
		{
			msg_print(_("あなたは黄金色のオーラをまとった！", "You are surrounded by a golden aura!"));
			p_ptr->special_attack |= ATTACK_WASTE_MONEY;
		}
		p_ptr->redraw |= PR_STATUS;

		return NULL; //行動順消費なし
	}
	break;

	case 6:
	{
		int len = p_ptr->lev / 5;
		if (len < 5) len = 5;
		damage = p_ptr->lev * 3;
		if (damage < 50) damage = 50;
		if (only_info) return format(_str_eff_range_dam, len, damage);
		if (!rush_attack2(len, GF_FORCE, damage,0)) return NULL;
		break;
	}


	case 7://スレイブロバー
	{
		int y, x;
		monster_type *m_ptr;

		if (only_info) return format(" ");
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			int tim;
			bool flag_success = FALSE;
			char m_name[80];
			monster_desc(m_name, m_ptr, 0);

			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			msg_format(_("あなたは%sへ飛び掛かった！", "You leap at %s!"), m_name);

			tim = MON_FAST(m_ptr);

			if (tim)
			{
				msg_print(_("加速効果を奪い取った！", "You steal the speed increasing effect!"));
				set_monster_fast(cave[y][x].m_idx, 0);
				set_monster_slow(cave[y][x].m_idx, tim);
				set_fast(tim, FALSE);
				flag_success = TRUE;

			}

			tim = MON_INVULNER(m_ptr);
			if (tim && (use_itemcard || plev > 44))
			{
				msg_print(_("無傷の球を奪い取った！", "You steal the globe of invulnerability!"));
				set_monster_invulner(cave[y][x].m_idx, 0,TRUE);
				set_invuln(tim, FALSE);
				flag_success = TRUE;
			}

			if (!flag_success) msg_print(_("...しかし何も奪えなかった。", "...You couldn't steal anything."));

		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;

	case 8: //プラックピジョン
	{
		int base = 10;
		int time;

		if (only_info) return format(_str_eff_dur_plus_1d, base, base);
		time = base + randint1(base);
		set_tim_general(time, FALSE, 0, 0);

	}
	break;

	case 9://アブソリュートルーザー
	{
		int y, x;
		int i;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (only_info) return format(" ");

		for (i = 1; i < m_max; i++)
		{
			m_ptr = &m_list[i];
			if (!m_ptr->r_idx) continue;
			if (m_ptr->mflag & MFLAG_POSSESSED_BY_SHION)
			{
				msg_print(_("今姉は他のモンスターに憑りついている。", "Your sister is possessing another monster right now."));
				return NULL;
			}
		}
		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
			return NULL;
		}

		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);

			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			if ((r_ptr->flags1 & RF1_QUESTOR) || (r_ptr->flagsr & RFR_RES_ALL))
			{
				msg_format(_("姉は%sに憑依できなかった！", "Your sister couldn't possess %s!"), m_name);
			}
			else
			{
				msg_format(_("姉が%sに憑依し支配した！", "Your sister possesses and controls %s!"), m_name);
				set_pet(m_ptr);
				m_ptr->mflag |= (MFLAG_EPHEMERA | MFLAG_POSSESSED_BY_SHION);
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;



	default:
		if (only_info) return format(_str_unimp);
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}



