cptr do_cmd_class_power_aux_udonge_d(int num, bool only_info)
{
	int dir, dice, sides, base, damage;
	int plev = p_ptr->lev;
	switch (num)
	{


	case 0:
	{
		if (only_info) return format("");
		if (!put_item_into_inven2()) return NULL;
		break;

	}
	case 1:
	{
		if (only_info) return format("");
		display_list_inven2();
		return NULL; //見るだけなので行動順消費なし

	}
	case 2:
	{
		if (only_info) return format("");
		if (!takeout_inven2()) return NULL;
		break;
	}

	case 3:
	case 11:
	//ルナティッククランプとルナティックエコー
	{
		int hand;
		int dam = 0;
		int range = 2 + plev / 16;

		//両手のルナティックガンのダメージ合計値を計算 しようと思ったがやはり片腕だけにする
		for (hand = 0; hand <= 0; hand++)
		{
			int mult;
			object_type *o_ptr = &inventory[INVEN_RARM + hand];
			int timeout_base, bullets;

			if (o_ptr->tval != TV_GUN || o_ptr->sval != SV_FIRE_GUN_LUNATIC) continue;

			timeout_base = calc_gun_timeout(o_ptr) * 1000;
			bullets = calc_gun_load_num(o_ptr);

			mult = (timeout_base * bullets - o_ptr->timeout) / timeout_base; //銃の残弾数
			if (num == 3) mult = MIN(1,mult); //ルナティッククランプは一発しか使わない

			if (!mult) continue;

			dam += (o_ptr->dd * (o_ptr->ds + 1) / 2 + o_ptr->to_d) * mult;

		}
		if (dam > 9999) dam = 9999;


		if (only_info)
		{
			if (num == 3)
				return format(_str_eff_range_dam,range, dam);
			else
				return format(_str_eff_dam, dam);
		}

		if (dam <= 0)
		{
			msg_print(_("この装備では技を使えない。", "You can't use this ability with your current equipment."));
			return NULL;
		}


		if (num == 3)
		{
			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("ルナティックガンを口元に構えて大声を出した！", "You put your lunatic gun in front of your mouth and shout!"));
			fire_ball(GF_SOUND, dir, dam, -2);
		}
		else
		{
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("狂気の叫びがダンジョンを震わせた！", "A maddening shout shakes the dungeon!"));
			fire_spark(GF_SOUND, dir, dam, 2);
		}

		//残弾減少処理
		for (hand = 0; hand <= 0; hand++)
		{
			int mult;
			object_type *o_ptr = &inventory[INVEN_RARM + hand];
			int timeout_base, bullets, timeout_max;

			if (o_ptr->tval != TV_GUN || o_ptr->sval != SV_FIRE_GUN_LUNATIC) continue;

			timeout_base = calc_gun_timeout(o_ptr) * 1000;
			bullets = calc_gun_load_num(o_ptr);
			timeout_max = timeout_base * (bullets - 1);

			mult = (timeout_base * bullets - o_ptr->timeout) / timeout_base; //銃の残弾数
			if (num == 3) mult = MIN(1, mult); //ルナティッククランプは一発しか使わない

			if (!mult) continue;

			o_ptr->timeout += timeout_base * mult;
			p_ptr->window |= PW_EQUIP;
			//ガンカタ残弾判定のため弾切れチェック
			if (o_ptr->timeout > timeout_max)
				p_ptr->update |= PU_BONUS;


		}


	}
	break;
	case 4:
	{
		int rad = p_ptr->lev / 2 + 10;
		if (only_info) return format(_str_eff_area, rad);

		if (use_itemcard)
			msg_format(_("周囲の色々なことが分かった気がする...", "You feel you know more about nearby area..."));
		else
			msg_format(_("周囲の波長を分析した・・", "You analyse wavelengths in your vicinity..."));
		detect_doors(rad);
		if (p_ptr->lev > 4) detect_monsters_normal(rad);
		if (p_ptr->lev > 9) detect_traps(rad, TRUE);
		if (p_ptr->lev < 15)detect_stairs(rad);
		else map_area(rad);
		if (p_ptr->lev > 19) detect_objects_normal(rad);
		break;
	}
	case 5:
	{
		dice = p_ptr->lev / 5;
		sides = 5 + p_ptr->lev / 10;
		base = p_ptr->lev + 10;
		if (only_info) return format(_str_eff_dam_base_dice_sides, base, dice, sides);

		if (!get_aim_dir(&dir)) return NULL;
		msg_format(_("あなたは精神波を放った。", "You release a mental wave."));
		fire_beam(GF_REDEYE, dir, base + damroll(dice, sides));
		break;
	}
	case 6:
	{
		if (only_info) return "";

		msg_format(_("周囲の存在から放たれる波長を読み取った。", "You read wavelengths of other beings in your vicinity."));
		probing();
		break;
	}
	case 7:
	{
		object_type forge;
		object_type *tmp_o_ptr = &forge;
		int ty, tx;
		if (only_info) return "";

		if (!get_aim_dir(&dir)) return NULL;
		tx = px + 99 * ddx[dir];
		ty = py + 99 * ddy[dir];
		if ((dir == 5) && target_okay())
		{
			tx = target_col;
			ty = target_row;
		}
		//ダミーアイテムを生成し特殊投擲ルーチンへ渡す
		msg_print(_("あなたは薬籠をぶん投げた！", "You hurl your medicine chest!"));
		object_prep(tmp_o_ptr, lookup_kind(TV_COMPOUND, SV_COMPOUND_MEDICINE_CHEST));

		do_cmd_throw_aux2(py, px, ty, tx, 2, tmp_o_ptr, 0);

		break;
	}


	case 8: //ルナティックダブル
	{
		int shoot_num = (plev -1) / 7;
		if (only_info) return format(_("連射数:最大%d", "amt: up to %d"), shoot_num);

		if (!rapid_fire(shoot_num,0))
		{
			return NULL;
		}

	}
	break;
	case 9:
	{
		int x, y;
		int range = plev / 6 + 2;
		if (only_info) return format(_("距離:%d", "dist: %d"),range);

		project_length = range;
		if (!get_aim_dir(&dir)) return NULL;
		x = px + range * ddx[dir];
		y = py + range * ddy[dir];
		if ((dir == 5) && target_okay())
		{
			x = target_col;
			y = target_row;
		}

		project_length = 0;
		if (!cave_player_teleportable_bold(y, x, 0L) ||
			(distance(y, x, py, px) > range) ||
			!projectable(py, px, y, x))
		{
			msg_print(_("そこへは移動できない。", "You can't move there."));
			return NULL;
		}
		else if (p_ptr->anti_tele)
		{
			msg_print(_("不思議な力がテレポートを防いだ！", "A mysterious force prevents you from teleporting!"));
		}
		else
		{
			msg_print(_("あなたは離れた場所に一瞬で現れた。", "You instantly appear in a distant location."));
			teleport_player_to(y, x, TELEPORT_NONMAGICAL);
			new_class_power_change_energy_need = 100 - plev;
		}
		break;
	}
	case 10:
	{
		damage = p_ptr->lev * 4 + adj_general[p_ptr->stat_ind[A_WIS]] * 5 + adj_general[p_ptr->stat_ind[A_CHR]] * 5;
		if (only_info) return format(_str_eff_dam, damage);
		msg_format(_("あなたの視界は赤く染まった！", "Everything in your sight gets dyed red!"));
		project_hack2(GF_REDEYE, 0, 0, damage);

		break;
	}
	case 12:
	{
		if (only_info) return format(_("効力:3回", "3 times"));

		evil_undulation(TRUE, FALSE);

	}
	break;

	default:
		if (only_info) return format("");

		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}




