cptr do_cmd_class_power_aux_vfs_clownpiece(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch (num)
	{

	case 0:
	{
		monster_type *m_ptr;
		int y, x;
		int power = plev * 2 + chr_adj * 2 + 20;

		if (use_itemcard) power += 60;

		if (only_info) return  format(_str_eff_power, power);
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			monster_desc(m_name, m_ptr, 0);

			if(use_itemcard)
				msg_format(_("篝火が狂気の光を放った！", "The bonfire emits light of lunacy!"));
			else
				msg_format(_("あなたは%sの前に松明を近づけた……", "You hold your torch in front of %s..."), m_name);

			lunatic_torch(cave[y][x].m_idx, power);
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;
	case 1:
	{
		int power = 10 + p_ptr->lev * 3;
		if (only_info) return format(_str_eff_power, power);
		msg_format(_("松明が明るさを増した。", "Your torch glows brighter."));

		lite_area(randint1(power), 3);
		confuse_monsters(power);
		if (plev > 24) stun_monsters(power);
		if (plev > 34) turn_monsters(power);
		if (plev > 44) project_hack(GF_BERSERK,power);

	}
	break;


	case 2:
	{
		int base = p_ptr->chp / 3;

		if (base < 1) base = 1;

		if (only_info) return format(_str_eff_dam_around, base);

		if (plev < 40)
		{
			msg_format(_("周囲に火を放った。", "You spread flames around."));
			project(0, 4, py, px, base * 2, GF_FIRE, PROJECT_JUMP | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM, -1);
		}
		else
		{
			msg_print(_("熱波が周囲を焼き払った！", "Heat waves spread around you!"));
			project_hack2(GF_FIRE, 0, 0, base);
		}

	}
	break;
	case 3:
	{
		int base = plev * 2 + chr_adj * 2 - 10;

		if (only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"), base, base);

		if (!get_aim_dir(&dir)) return NULL;
		msg_format(_("地獄のパワーを放った！", "You unleash the power of Hell!"));
		fire_beam(GF_HELL_FIRE, dir, base + randint1(base));
		break;
	}


	case 4:
	{
		int power = plev * 2 + chr_adj * 2;

		if (only_info) return  format(_str_eff_power, power);

		msg_format(_("地獄の松明が煌々と輝いた！", "The torch of Hell glitters brightly!"));
		project_hack2(GF_LUNATIC_TORCH, 0, 0, power);

	}
	break;

	case 5:
	{
		int x, y;
		int dist = 7;
		int rad = 5;
		monster_type *m_ptr;
		int dice = 5 + chr_adj / 10;
		int sides = plev * 2;

		if (only_info) return format(_("射程:%d 損傷:%dd%d", "rng: %d dam: %dd%d"), dist, dice, sides);
		project_length = dist;
		if (!get_aim_dir(&dir)) return NULL;
		if (dir != 5 || !target_okay() || !projectable(target_row, target_col, py, px))
		{
			msg_print(_("そこを狙うことはできない。",
                        "You can't aim there."));
			return NULL;
		}
		y = target_row;
		x = target_col;

		m_ptr = &m_list[cave[y][x].m_idx];

		if (!m_ptr->r_idx || !m_ptr->ml)
		{
			msg_print(_str_mess_nobody_here);
			return NULL;
		}
		else
		{
			char m_name[80];
			monster_desc(m_name, m_ptr, 0);

			msg_format(_("%sを目掛けて隕石が落ちた！", "You drop a meteor on top of %s!"), m_name);
			project(0, rad, y, x, damroll(dice, sides), GF_METEOR, (PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM), -1);
		}

		break;
	}




	default:
		if (only_info) return format(_str_unimp);
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}

