cptr do_cmd_class_power_aux_mai(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch (num)
	{

	case 0:
	{
		int chr = p_ptr->stat_ind[A_CHR] + 3;
		int mult = 100 + MAX(0, 100 * (chr - 5) / 15);
		if (only_info)
		{
			if(p_ptr->pclass == CLASS_MAI)
				return format(_("近接強化:x%d.%02d", "melee: x%d.%02d"), mult / 100, mult % 100);
			else
				return format(_("魔法使用率:+%d", "spell rate: +%d"), chr);
		}

		if(!do_riding(FALSE)) return NULL;


	}
	break;
	case 1://スカウト
	{
		int y, x;
		int power = 20 + plev + chr_adj;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (only_info) return format(_str_eff_power, power);

		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You can't use this ability here."));
			return NULL;
		}


		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];
			monster_desc(m_name, m_ptr, 0);

			//起こす
			set_monster_csleep(cave[y][x].m_idx, 0);

			if (one_in_(64))
			{
				msg_format(_("あなたは、ぐるぐるとおどった。", "You spin and dance."));
				power *= 3;
			}
			else if (one_in_(4))
			{
				if(p_ptr->pclass == CLASS_MAI)
					msg_format(_("「君のようなアグレッシブな人材を探していたんだ！」", "'We're looking for aggressive people like you!'"));
				else
					msg_format(_("「ようこそ後戸の国へ。」", "'Welcome to the Land of Backdoor.'"));
			}
			else
				msg_format(_("あなたは%sを後戸の国へスカウトした。", "You attempt to recruit %s for the Land of Backdoor."), m_name);

			if (r_ptr->flags2 & (RF2_EMPTY_MIND | RF2_WEIRD_MIND))
			{
				msg_format(_("%sは無反応だ。", "%s does not respond,"), m_name);
			}
			else if (is_pet(m_ptr))
			{
				msg_format(_("%sはすでにスカウト済みだ。", "You already have recruited %s."), m_name);
			}
			else if (r_ptr->flagsr & RFR_RES_ALL || r_ptr->flags1 & (RF1_QUESTOR) || r_ptr->flags7 & RF7_VARIABLE || power < r_ptr->level)
			{
				msg_format(_("%sはあなたの誘いに耳を貸さない！", "%s doesn't listen to your invitation!"), m_name);
			}
			//ユニークモンスターを仲間にするにはweird_luck要
			else if (randint1(power) < r_ptr->level || (r_ptr->flags1 & RF1_UNIQUE) && !weird_luck())
			{
				msg_format(_("%sはあなたの誘いに乗らなかった。", "%s didn't go along with your invitation."), m_name);
			}
			else
			{
				msg_format(_("%sはあなたに従った。", "%s now obeys you."), m_name);
				set_pet(m_ptr);
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
	}
	break;
	case 2:
	if(!use_itemcard && p_ptr->pclass == CLASS_MAI)//バンブースピアダンス
	{
		int damage = 0;
		int range = 2 + plev / 15;

		if(inventory[INVEN_RARM].tval == TV_SPEAR)
			damage = calc_weapon_dam(0) / p_ptr->num_blow[0];
		else if (inventory[INVEN_LARM].tval == TV_SPEAR)
			damage = calc_weapon_dam(1) / p_ptr->num_blow[1];

		if (only_info) return format(_str_eff_range_dam, range, damage);

		if (damage < 1)
		{
			msg_print(_("槍を装備していないとこの技を使うことはできない。", "You have to wield a spear to use this ability."));
			return NULL;
		}

		project_length = range;
		if (!get_aim_dir(&dir)) return NULL;
		//msg_print("突きかかった！");
		fire_beam(GF_ARROW, dir, damage);
	}
	else//フォゲットユアネーム、もしくはアイテムカードの茗荷
	{
		int power = 100 + plev * 4 + chr_adj * 10;
		if (only_info) return format(_str_eff_power,power/2);

		msg_print(_("あなたは茗荷を揺らして踊った...", "You dance as you scatter ginger around..."));
		project(0, 1, py, px, power, GF_GENOCIDE, PROJECT_HIDE | PROJECT_JUMP | PROJECT_KILL, -1);

	}
	break;
	case 3:
	{
		if (only_info) return "";

		msg_print(_("後戸の国への扉が開いた。", "A door to the Land of Backdoor opens."));

		if (EXTRA_MODE)
		{
			teleport_level(0);
		}
		else
		{
			recall_player(1);
		}
	}
	break;
	case 4:
	if(use_itemcard || p_ptr->pclass == CLASS_MAI) //舞のタナバタスター、あるいはアイテムカードの笹
	{
		int heal = 50 + chr_adj * 5;
		int heal_percen = 10 + chr_adj / 5;

		if (use_itemcard && heal < 100) heal = 100;

		if (only_info) return format(_("回復:%d/%d%%", "heal: %d/%d%%"),heal,heal_percen);

		msg_print(_("星神の力があなたを癒やす...", "The power of god of stars heals you..."));

		hp_player(heal);
		set_stun(0);
		set_cut(0);
		set_poisoned(0);
		set_image(0);

		if (p_ptr->riding)
		{
			monster_type *m_ptr = &m_list[p_ptr->riding];
			int heal2;
			char m_name[80];

			set_monster_stunned(p_ptr->riding, 0);
			set_monster_confused(p_ptr->riding, 0);
			set_monster_slow(p_ptr->riding, 0);
			if (m_ptr->hp < m_ptr->maxhp)
			{
				monster_desc(m_name, m_ptr, 0);
				heal2 = m_ptr->maxhp * heal_percen / 100;
				if (!heal2) heal2 = 1;
				m_ptr->hp += heal2;
				if (m_ptr->hp > m_ptr->maxhp) m_ptr->hp = m_ptr->maxhp;
				msg_format(_("%^sの傷が少し癒えた。", "The wounds of %s are somewhat healed."), m_name);
				p_ptr->redraw |= (PR_HEALTH);
				p_ptr->redraw |= (PR_UHEALTH);
			}
		}

	}
	else //里乃のビハインドユー
	{
		if (only_info) return format("　");

		if (!p_ptr->riding)
		{
			msg_print(_("今は踊っていない。", "You're not dancing right now."));
			return NULL;
		}

		if (!cast_monspell_new()) return NULL;

	}
	break;
	case 5:
	{
		int dam = plev * 3 + chr_adj * 3;
		if (only_info) return format(_str_eff_dam, dam);

		msg_print(_("あなたは狂おしく跳ね踊った！", "You dance in a frenzy!"));
		dispel_undead(dam);
		dispel_demons(dam);
		dispel_kwai(dam);

	}
	break;

	case 6:
	{
		if (p_ptr->pclass == CLASS_MAI)
		{
			if (only_info) return "";

			msg_print(_("あなたは地面を踏み鳴らした。竹が次々に生えてきて生垣になった！",
                        "You stomp on the ground. Bamboo sprouts grow and form a forest!"));

			tree_creation();
		}
		else
		{
			int power = plev + chr_adj * 5;
			if (only_info) return format(_str_eff_power, power);

			msg_print(_("あなたは幻惑的な踊りを披露した。", "You perform a beguiling dance."));
			confuse_monsters(power);
			stun_monsters(power);
			charm_monsters(power);

		}



	}
	break;

	case 7:
	{
		int v;
		bool base = 10;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);

		v = base + randint1(base);

		msg_print(_("あなたの踊りは激しさを増した！", "Your dance becomes more intensive!"));
		set_tim_addstat(A_CHR, 100 + plev / 5, v, FALSE);

		break;
	}

	case 8:
	{
		if (only_info) return format(_("距離:なし", "dist: n/a"));

		msg_print(_("あなたは後戸の国を覗き込んだ...", "You look into the Land of Backdoor..."));
		detect_monsters_normal(255);

		if (p_ptr->paralyzed || p_ptr->confused || p_ptr->image)
		{
			msg_print(_("あなたは移動に失敗した！", "You fail to teleport!"));
		}
		else
		{
			//テレポートに成功したらTRUEが帰って行動力二倍
			if (dimension_door(D_DOOR_BACKDOOR))
				new_class_power_change_energy_need = 200;
		}



	}
	break;


	case 9:
	{
		int v;
		bool base = 6;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);
		v = base + randint1(base);

		if (p_ptr->riding)
		{
			char m_name[80];
			monster_desc(m_name, &m_list[p_ptr->riding], 0);
			msg_format(_("%sは潜在能力が開放されて無敵になった！", "You unleash the potential powers of %s, granting invincibility!"),m_name);
			set_monster_invulner(p_ptr->riding, v, FALSE);
		}

	}
	break;

	default:
		if (only_info) return format(_str_unimp);
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}





