cptr do_cmd_class_power_aux_larva(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch (num)
	{

	case 0:
	{
		bool flag = FALSE;
		int power = 5 + plev/2;

		if (p_ptr->chp <= p_ptr->mhp / 2)
		{
			power *= 4;
			flag = TRUE;
		}

		if (only_info) return format(_str_eff_dam_around, power);

		if (flag)
		{
			msg_print(_("あなたは鼻が曲がりそうな悪臭を放った！", "You let out revolting bad breath!"));
			project(0, 5, py, px, power*2, GF_POIS, (PROJECT_KILL | PROJECT_JUMP), -1);
			confuse_monsters(power);
			stun_monsters(power);
			turn_monsters(power);
		}
		else
		{
			msg_print(_("あなたは頭の臭角から嫌な匂いを出した...", "You emit horrible stench..."));
			project(0, 3, py, px, power*2, GF_POIS, (PROJECT_KILL | PROJECT_JUMP), -1);
		}

	}
	break;
	case 1:
	{
		int dam = 10 + plev + chr_adj ;
		int rad = 1 + plev / 30;
		if (only_info) return format(_str_eff_dam_around, dam / 2);

		msg_print(_("鱗粉をまき散らした。", "You spread scales around."));
		project(0, rad, py, px, dam, GF_INACT, (PROJECT_KILL | PROJECT_JUMP), -1);

	}
	break;

	case 2://再生
	{
		int base = p_ptr->lev / 5;
		if (only_info) return format(_("回復:%d+1d%d", "heal: %d+1d%d"), base, base);

		if (p_ptr->food < PY_FOOD_WEAK)
		{
			msg_print(_("お腹が空いて集中できない。", "You can't concentrate while you're hungry."));
			return NULL;
		}

		msg_print(_("あなたは自分の体に意識を集中した..", "You concentrate on your body..."));
		hp_player(base + randint1(base));
		set_poisoned(p_ptr->poisoned - base);
		set_stun(p_ptr->stun - base);
		set_cut(p_ptr->cut - base);
		set_image(p_ptr->image - base);
		set_food(p_ptr->food - 50);

	}
	break;
	case 3://ミニットスケールス
	{
		int dam = plev * 2 + chr_adj * 2;
		int rad = plev / 15;
		if (only_info) return format(_str_eff_dam_around, dam / 2);

		msg_print(_("鱗粉をまき散らした。", "You spread scales around."));
		project(0, rad, py, px, dam, GF_CONFUSION, (PROJECT_KILL | PROJECT_JUMP), -1);

	}
	break;

	case 4://変異強力発動
	{
		if (only_info) return "";

		msg_print(_("あなたは力強いオーラをまとった..", "You are covered in a powerful aura..."));

		hack_flag_powerup_mutation = TRUE;
		do_cmd_racial_power(FALSE);
		hack_flag_powerup_mutation = FALSE;

		//-hack- レイシャル発動をキャンセルしたかどうかを行動力消費で判定
		if (!energy_use) return NULL;

	}
	break;

	case 5://アゲハの鱗粉
	{
		int dam = plev * 4 + chr_adj * 4;
		int rad = 1 + plev / 15;
		if (only_info) return format(_str_eff_dam_around, dam / 2);

		msg_print(_("妖しく輝く鱗粉をまき散らした...", "You spread bewitchingly glowing scales around..."));
		project(0, rad, py, px, dam, GF_CHAOS, (PROJECT_KILL | PROJECT_JUMP | PROJECT_GRID | PROJECT_ITEM), -1);

	}
	break;

	case 6:
	{
		if (only_info) return "";

		if (p_ptr->inside_arena)
		{
			msg_print(_("今その特技は使えない。", "You can't use this special ability right now."));
			return NULL;
		}

		msg_print(_("あなたは自分の存在に疑問を持った...", "You start doubting your existence..."));

		alter_reality();
	}
	break;


	case 7:
	{
		if (only_info) return "";

		msg_print(_("あなたは体を丸めて目を閉じた...", "You curl up and close your eyes..."));
		gain_random_mutation(0);

	}
	break;
	case 8:
	{
		int base = 7;
		int time;
		if (only_info) return format(_str_eff_dur_plus_1d, base, base);

		time = randint1(base) + base;

		set_mimic(time, MIMIC_GOD_OF_NEW_WORLD, FALSE);
		set_oppose_fire(time, FALSE);
		set_hero(time, FALSE);
	}
	break;

	default:
		if (only_info) return format(_str_unimp);
		msg_format(_str_unimp_error, num);
		return NULL;
	}
	return "";
}



