cptr do_cmd_class_power_aux_kosuzu_hyakki(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int dir;

	switch(num)
	{

	case 0: //付喪神召喚
		{
			int i;
			bool flag = FALSE;
			int base = plev / 10;
			if (only_info) return "";

			i = base + randint1(base);

			for(;i>0;i--)
			{
				if(summon_specific(0,py,px,(dun_level/2+plev/2),SUMMON_TSUKUMO,(PM_ALLOW_GROUP | PM_FORCE_FRIENDLY))) flag = TRUE;
			}
			if(flag)
				msg_print(_("どこからともなく付喪神たちが現れた。", "Tsukumogami appear out of nowhere."));
			else
				msg_print(_("特に何も起こらなかった。", "Nothing unusual happens."));

		}
		break;
	case 1://付喪神吸収
		{
			if(only_info) return "";

			msg_print(_("影の鬼が大口を開けて吠えた！", "A shadowy demon opens up its mouth and howls!"));
			absorb_tsukumo(0);
		}
		break;
	case 2:
		{
			int dam = p_ptr->chp / 6;
			if(dam<1) dam = 1;
			if(dam > 600) dam=600;
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;

			msg_print(_("付喪神たちを吹きつけた！", "You breathe tsukumogami!"));

			fire_ball(GF_SHARDS, dir, dam, -2);
			break;
		}



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

