cptr do_cmd_class_power_aux_kosuzu_ghost(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{

	case 0: //魅了
		{
			int y, x;
			int power = 10 + plev + chr_adj * 2;
			monster_type *m_ptr;
			monster_race *r_ptr;

			if (only_info) return format(_str_eff_power,power);

			if (p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				if(r_ptr->flags1 & RF1_MALE) power *= 2;
				if(MON_CSLEEP(m_ptr)) power *= 2;
				if(r_ptr->flags3 & RF3_NO_CONF) power /= 2;

				msg_format(_("あなたは%sに向けて微笑んで手招きした...", "You smile and beckon %s..."),m_name);

				if(r_ptr->flagsr & RFR_RES_ALL || r_ptr->flags1 & (RF1_UNIQUE | RF1_QUESTOR) || r_ptr->flags7 & RF7_UNIQUE2)
				{
					msg_format(_("%sには効果がない！", "%s is unaffected!"),m_name);
				}
				else if(randint1(power) < r_ptr->level)
				{
					msg_format(_("%sは抵抗した。", "%s resists."),m_name);
				}
				else
				{
					msg_format(_("%sはあなたに従った。", "%s now obeys you."),m_name);
					set_pet(m_ptr);
				}
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
		}
		break;
	case 1://テレポート
		{
			int dist = 25 + plev / 2;
			if(only_info) return format(_("距離:%d", "dist: %d"),dist);
			msg_print(_("あなたは瞬時に消えた。", "You teleport away."));
			teleport_player(dist, 0L);
		}
		break;

	case 2: //生命力吸収
		{
			int y, x;
			int dam = plev + chr_adj * 5;
			monster_type *m_ptr;
			monster_race *r_ptr;

			if (only_info) return format(_str_eff_power,dam);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];

				r_ptr = &r_info[m_ptr->r_idx];

				monster_desc(m_name, m_ptr, 0);
				if(!monster_living(r_ptr))
				{
					msg_format(_("%sからは生命力を奪えなかった。", "You couldn't drain life force from %s."),m_name);
				}
				else
				{
					msg_format(_("あなたは%sへ触れ、生命力を吸収した！", "You touch %s and drain life force!"),m_name);

					if(m_ptr->hp < dam) dam = m_ptr->hp+1;

					if(p_ptr->food < PY_FOOD_MAX) set_food(MIN(PY_FOOD_MAX - 1, (p_ptr->food + dam * 10)));
					hp_player(dam);
					project(0,0,y,x,dam,GF_DISP_ALL,PROJECT_KILL,0);
				}
				touch_zap_player(m_ptr);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
		}
		break;
	case 3:
		{
			if(only_info) return "";
			if (!(p_ptr->special_defense & NINJA_KAWARIMI))
			{
				msg_print(_("あなたの周囲に恋文が舞い始めた...", "Red characters dance around you..."));

				p_ptr->special_defense |= NINJA_KAWARIMI;
				p_ptr->redraw |= (PR_STATUS);
			}
			break;
		}
	case 4:
		{
			int base = 20;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);


			set_kabenuke(base + randint1(base), FALSE);

			break;
		}
	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


