cptr do_cmd_class_power_aux_kosuzu(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0: //巻物鑑定
		{
			if (only_info) return format("");
			if (!ident_spell_2(4)) return NULL;
		}
		break;

	case 1: //
		{
			if(only_info) return "";
			if(p_ptr->food >= PY_FOOD_FULL)
			{
				msg_print(_("今は満腹だ。", "You are already full."));
				return NULL;
			}

			msg_print(_("どこからともなく「絶品野菜コロッケ」が出現した！",
                        "You take out some exquisite vegetables out of nowhere!"));
			set_food(PY_FOOD_MAX - 1);

			break;
		}
	case 2:
		{
			int rad = DETECT_RAD_DEFAULT;
			if(only_info) return format(_str_eff_area,rad);

#ifdef JP
			msg_print("あなたは筮竹を取り出し、記憶を頼りに卦を立て始めた...");
#endif
			p_ptr->feeling_turn = 0;

			if(plev > 19) detect_traps(rad,TRUE);
			if(plev > 29) detect_monsters_normal(rad);
			if(plev > 29) detect_monsters_invis(rad);

			break;
		}
	case 3:
		{
			int max_num = (plev-10) / 10;
			int i,cnt=0;
			monster_type *m_ptr;
			if(only_info) return format(_("最大:%d", "up to %d"),max_num);

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_ENENRA) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_num)
			{
				msg_format(_("もう本は空白だ。", "The book is blank right now."),num);
				return NULL;
			}

			if(summon_named_creature(0, py, px, MON_ENENRA, PM_EPHEMERA))
			{
				msg_print(_("本の中から朦々と煙が湧き出した...", "Smoke billows out from the book..."));

			}

		}
		break;

	case 4:
		{
			int base = 1000;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			set_mimic(base + randint1(base), MIMIC_KOSUZU_GHOST, FALSE);

			break;
		}
	case 5:
		{
			int max_num = 1;
			int i,cnt=0;
			monster_type *m_ptr;
			if(only_info) return format(_("最大:%d", "up to %d"),max_num);

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				//カメレオンフラグと一時的フラグを持つモンスターで小狐を判定。何かアイテムカード使えば他のルートでもこのモンスターが出るかもしれないがまあ解釈上問題はないだろう
				if( (m_ptr->mflag2 & MFLAG2_CHAMELEON) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_num)
			{
				msg_format(_("もう小狐を呼んでいる。", "You already have called for the kitsune."),num);
				return NULL;
			}

			if(summon_named_creature(0, py, px, MON_CHAMELEON, PM_EPHEMERA))
			{
				msg_print(_("馴染みの小狐が変身してあなたを助けに来た！",
                            "A familiar young kitsune transforms and comes to your aid!"));
			}

		}
		break;

	case 6:
		{
			int base = 100;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			set_mimic(base + randint1(base), MIMIC_KOSUZU_HYAKKI, FALSE);

			break;
		}




	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


