cptr do_cmd_class_power_aux_narumi2(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{

	case 0:
		{
			int rad = 25 + plev / 2;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("あなたはその場に佇んで生命の気配を探した..", "You stand still and search for presence of life..."));
			detect_monsters_living(rad);

			break;
		}
	case 1:
		{
			int rad = DETECT_RAD_DEFAULT;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("あなたは周囲の魔力を探った..", "You search for magical power in your vicinity..."));
			detect_objects_magic(rad);
			break;
		}
	case 2:
		{
			int y, x;
			int dam = plev * 3;
			if(only_info) return format(_str_eff_power,dam);

			if (!get_aim_dir(&dir)) return NULL;

			/* v1.1.36 fire_ball_jumpの戻り値はproject()の戻り値ではなく、成否にかかわらずMPが回復していたので修正
			if(fire_ball_jump(GF_OLD_DRAIN,dir,dam,0,"あなたは敵から生命力を吸い取ろうと試みた.."))
			{
				if(player_gain_mana(dam/3))
					msg_print("あなたの魔力が回復した！");
			}
			*/
			x = target_col;
			y = target_row;

			if (dir != 5 || !target_okay() || !projectable(y, x, py, px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			if (!cave[y][x].m_idx)
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}


			//GF_OLD_DRAINは効果がなかったときobvious=FALSEとなりproject_hookがFALSEを返す
			if (project_hook(GF_OLD_DRAIN, dir, dam, (PROJECT_KILL | PROJECT_HIDE | PROJECT_JUMP)))
			{
				if (player_gain_mana(dam/3))
					msg_print(_("あなたの魔力が回復した！", "You recover magical power!"));

			}


			break;
		}
	case 3:
		{
			int dam = 160 + plev * 4 + chr_adj * 3;

			if(only_info) return format(_("損傷:最大%d", "dam: up to %d"),dam);
			msg_print(_("あなたは念仏を唱え始めた...", "You start reciting a prayer..."));
			project(0,6,py,px,dam,GF_PUNISH_4,(PROJECT_KILL|PROJECT_JUMP|PROJECT_HIDE),-1);

			break;
		}

	case 4:
		{
			int y, x;
			int m_idx;
			monster_type *m_ptr;
			monster_race *r_ptr;
			char m_name[80];
			int dam=0;

			if(only_info) return format("");
			if (!get_aim_dir(&dir)) return NULL;

			x = target_col;
			y = target_row;

			if(dir != 5 || !target_okay() || !projectable(y,x,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			m_idx = cave[y][x].m_idx;
			if(!m_idx)
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			m_ptr = &m_list[m_idx];
			r_ptr = &r_info[m_ptr->r_idx];

			monster_desc(m_name, m_ptr, 0);
			msg_format(_("あなたは%sを本気で無力化しようとした..",
                        "You drain power from %s with full intent..."),m_name);

			if(r_ptr->flagsr & RFR_RES_ALL)
			{
				msg_format(_("%sには完全な耐性がある！", "%s is immune!"),m_name);
				break;
			}

			//妖精には無条件で効く
			if(r_ptr->flags3 & RF3_FAIRY)
			{

				dam = m_ptr->hp +1;
			}
			//ユニークと無生物には効かない
			else if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2 || !monster_living(r_ptr))
			{
				;
			}
			//破滅の手と同じ判定にしておく
			else if(((plev*2 ) + randint1(plev*2)) > (r_ptr->level  + randint1(120)))
			{
				dam = m_ptr->hp / 2 + 1;
			}

			if(dam)
			{
				project(0,0,m_ptr->fy,m_ptr->fx,dam,GF_MISSILE,(PROJECT_JUMP|PROJECT_KILL),-1);
			}
			else
			{
				msg_format(_("%sは抵抗した！", "%s is defeated!"),m_name);
				//起こす
				set_monster_csleep(m_idx, 0);
				anger_monster(m_ptr);
			}

			break;
		}

	case 5:

		{
			int range = 4 + plev / 40;
			int base = p_ptr->ac + p_ptr->to_a;
			int tx, ty, dam;

			if (base < 0) base = 0;

			if (only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"), base / 2, base / 2);

			if (base < 1)
			{
				msg_print(_("今のあなたの柔らかさでは虫一匹潰せないだろう。",
                            "With how soft you are now, you're not going to crush even an insect."));
				return NULL;
			}

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			dam = base / 2 + randint1(base / 2);
			ty = target_row;
			tx = target_col;
			if (!fire_ball_jump(GF_ARROW, dir, dam, 0, _("あなたは飛び上がり、標的目掛けて落下した！", "You jump up, and the drop down on the target position!"))) return NULL;
			teleport_player_to(ty, tx, TELEPORT_NONMAGICAL);
			//周りがみっしり敵のときどうなるのかチェック要
			break;
		}


	case 6:
		{
			bool flag = FALSE;
			int i;
			int dice = 4 + plev / 6;
			int sides = 11 + chr_adj/ 6;
			int num = (plev+12) / 10;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);
			msg_print(_("巨大な弾幕のゴーレムが辺りの敵を攻撃した！",
                        "A giant danmaku golem attacks nearby enemies!"));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_FORCE, damroll(dice,sides),4, 2,3)) flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_("しかし近くに敵がいなかった。", "However, there were no enemies nearby."));
					break;
				}
			}

		}
		break;

	case 7:
		{
			int base = (plev * 5 + chr_adj * 5) / 2;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,base);

			msg_print(_("あなたは自重せずに能力を開放した！", "You unleash your power showing no restraint!"));
			dispel_living(base+randint1(base));

		}
		break;
	case 8:
		{

			int base = plev * 6;
			int sides = chr_adj * 12;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
#ifdef JP
			fire_ball_jump(GF_HOLY_FIRE,dir,base+randint1(sides),5,(randint0(4)?"あなたは有り余る力の全てをぶつけた！":"「残念無念、また来世ー！」"));
#else
            fire_ball_jump(GF_HOLY_FIRE,dir,base+randint1(sides),5,"You let all of your power out!");
#endif

		}
		break;

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




