cptr do_cmd_class_power_aux_aunn(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int rad = 255;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("あなたはその場に佇んで感覚を研ぎ澄ませた..", "You stand still and sharpen your senses..."));
			detect_monsters_xxx(rad,RF3_DEITY);

			break;
		}
	case 1:
		{
			int rad = 25 + plev / 2;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("あなたは敵の存在を探った..", "You search for presence of enemies..."));
			detect_monsters_xxx(rad,RF3_KWAI);
			detect_monsters_evil(rad);
			break;
		}
	case 2:
		{
			int len = plev / 12;
			if(len < 2) len = 2;
			if(only_info) return format(_str_eff_range,len);
			if (!rush_attack(NULL,len,0)) return NULL;
			break;
		}
		//石像化　オカルト魔法のエイボンの処理に便乗する。アイテムカードからも使われる。
	case 3:
		{
			int i;
			bool flag_ng = FALSE;
			if(only_info) return format(_("期間:--", "dur: --"));

			for (i = m_max - 1; i >= 1; i--)
			{
				monster_type *m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (!projectable(py,px,m_ptr->fy,m_ptr->fx)) continue;
				if (!is_hostile(m_ptr)) continue;
				if(MON_CSLEEP(m_ptr)) continue;
				if(MON_CONFUSED(m_ptr)) continue;

				flag_ng = TRUE;
				break;
			}

			if(flag_ng)
			{
				msg_print(_("敵に見られているので石像化できない。", "You cannot assume statue form while being observed by enemies."));
				return NULL;
			}

			msg_print(_("あなたは石像に変身した！", "You turn into a stone statue!"));
			p_ptr->special_defense |= (SD_EIBON_WHEEL | SD_STATUE_FORM);
			break_eibon_flag = FALSE;
			p_ptr->redraw |= (PR_STATUS);
			break;
		}

	case 4:
		{
			if(only_info) return format("");

			msg_print(_("あなたは目まぐるしく跳ね回った！", "You spin arounding at blinding speed!"));
			whirlwind_attack();
		}
		break;
		case 5:
		{
			int level;
			int num = 10;
			bool flag = FALSE;
			level = plev / 2 + chr_adj / 2;
			if (only_info) return format(_("召喚レベル:%d", "sum lvl: %d"), level);
			for (; num>0; num--)
			{
				if (summon_specific(-1, py, px, level, SUMMON_HOUND, (PM_FORCE_FRIENDLY | PM_ALLOW_GROUP))) flag = TRUE;
			}
			if (flag) msg_format(_("野良犬たちが現れた！", "Wild dogs appear!"));
			else msg_format(_("何も現れなかった・・", "Nobody has appeared..."));

		}
		break;

	case 6:
		{
			int base = 6;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			set_multishadow(base + randint1(base),FALSE);
			break;
		}


	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


