cptr do_cmd_class_power_aux_nemuno(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			object_type forge, *q_ptr = &forge;
			if(only_info) return _("食料を生成", "create food");
			msg_print(_("あなたは手際よく食物を集め始めた..", "You start carefully collection food..."));
			object_prep(q_ptr, lookup_kind(TV_FOOD, SV_FOOD_RATION));
			q_ptr->discount = 99;
			drop_near(q_ptr, -1, py, px);
			break;
		}

	case 1:
		{
			if(only_info) return "";

			if (IS_NEMUNO_IN_SANCTUARY)
			{
				msg_print(_("ここはすでにあなたの縄張りだ。", "This already is your sanctuary."));
				return NULL;
			}
			if (!(cave[py][px].info & CAVE_ROOM))
			{
				msg_print(_("部屋でない場所を縄張りにすることはできない。", "You have to be in a room to set up a sanctuary."));
				return NULL;
			}
			make_nemuno_sanctuary();

			break;
		}
	case 2:
		{
			int base = (plev + chr_adj) / 2;
			int sides = (plev + chr_adj) / 2;
			if (only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"), base, sides);


			//縄張り内にしか効果がない
			msg_print(_("冷たい雨が降り注いだ...", "Freezing rain starts falling..."));
			floor_attack(GF_INACT, 1, sides, base, 1);
		}
		break;

	case 3:
		{
			int base = 4 + p_ptr->lev / 4;
			int sides = 4 + p_ptr->lev / 4;
			if (only_info) return format(_str_eff_dur_plus_1d, base, sides);

			if (!object_has_a_blade(&inventory[INVEN_RARM]) && !object_has_a_blade(&inventory[INVEN_LARM]))
			{
				msg_print(_("刃物を持っていないと使えない。", "You have to be holding an edged weapon."));
				return NULL;
			}
			msg_print(_("あなたは一心不乱に武器を研ぎ始めた...", "You start sharpening your blade with all of your heart in it..."));
			set_ele_attack(ATTACK_VORPAL, base + randint1(sides));

			break;

		}
	case 4: //聖域U
	{
		if (only_info) return "";
		msg_print(_("あなたは奇怪な呪物を作り始めた..", "You start making strange hexes..."));
		warding_glyph();
		break;
	}

	case 5:
	{
		int base = plev * 4 + chr_adj * 5;
		if (only_info) return format(_str_eff_dam, base);

		//縄張り内にしか効果がない
		msg_print(_("豪雨がこの領域を外界から閉ざした。", "Torrential rain obscures this territory from outside."));
		floor_attack(GF_WATER, 0, 0, base, 1);
	}
	break;

	case 6:
	{
		if (only_info) return "";

		if (!IS_NEMUNO_IN_SANCTUARY)
		{
			msg_print(_("縄張りの中でないと使えない。", "You have to be in your sanctuary to use this ability."));
			return NULL;
		}

		msg_print(_("辺りは人を寄せ付けない雰囲気に満ちた。", "There suddenly is a hostile feeling towards unwelcome guests in your vicinity."));

		p_ptr->special_defense |= SD_UNIQUE_CLASS_POWER;
		p_ptr->redraw |= (PR_STATUS);


	}
	break;


	case 7:
	{
		int rad = 4;
		if (only_info) return format("");
		sakuya_time_stop(FALSE);
		flag_friendly_attack = TRUE;

		if(one_in_(3))
			msg_format(_("狂乱したように得物を振り回した！", "You frenziedly lash out at your prey!"));
		else if (one_in_(2))
			msg_format(_("山姥のやり方でよそ者を迎えた！", "You give the strangers a yamanba's welcome!"));
		else
			msg_format(_("手当たり次第に掻っ捌いた！", "You slash widly!"));


		project(0,rad,py,px,100,GF_SOULSCULPTURE, PROJECT_KILL,-1);
		flag_friendly_attack = FALSE;
	}
	break;

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




/*:::弾丸作成用*/
static bool item_tester_hook_make_bullet(object_type *o_ptr)
{
	int i;

	for(i=0;soldier_bullet_making_table[i].shooting_mode;i++)
	{
		if(soldier_bullet_making_table[i].mat_tval == o_ptr->tval
			&& soldier_bullet_making_table[i].mat_sval == o_ptr->sval)
		{
			return TRUE;
		}
	}

	return FALSE;
}



