cptr do_cmd_class_power_aux_junko(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!junko_make_nameless_arts()) return NULL;
			msg_print(_("あなたは名もなき純粋な力を様々に操った..", "You manipulate nameless pristine power..."));

		}
		break;
	case 1:
		{
			if(only_info) return format("");
			if(!activate_nameless_art(FALSE)) return NULL;
		}
		break;
	case 2:
		{
			if (only_info) return format("");
			if (!activate_nameless_art(TRUE)) return NULL;
		}
		break;

	case 3:
		{
			int dice = 2 + plev / 3;
			int sides = 9;
			int base = plev + chr_adj * 2;
			int range = 2 + plev / 6;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("あなたの掌から眩い光が放たれた。", "Dazzling light shoots out from your palm."));
			fire_beam(GF_PURIFY, dir, base + damroll(dice,sides));
			break;
		}
	case 4:
		{
			int y, x;
			monster_type *m_ptr;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				if(!rankup_monster(cave[y][x].m_idx,1))
					msg_format(_("何も起こらなかった。", "Nothing happens."));
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}
	case 5:
		{
			if(only_info) return format(" ");
			if(!convert_item_to_mana()) return NULL;
		}
		break;
	case 6:
		{
			int i;
			bool flag = FALSE;
			int max = 1 + p_ptr->lev / 7;
			if(only_info) return format("");

			for(i=0;i<max;i++) if(summon_named_creature(0, py, px, MON_HELL_FAIRY, (PM_EPHEMERA))) flag = TRUE;
			if(flag) msg_print(_("友人の配下が現れた。", "Your allies appear."));
			else msg_print(_("何も現れなかった..", "Nobody has appeared..."));

			break;
		}

	case 7:
		{
			if(only_info) return format(" ");
			if(!purify_equipment()) return NULL;
		}
		break;

	case 8:
		{
			int base = plev * 3 + chr_adj * 5;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,base);
			msg_format(_("純粋な狂気を解き放った。", "You unleash pristine lunacy."));
			project_hack2(GF_COSMIC_HORROR,1,base,base);
			charm_monsters(base);

			break;
		}
		break;

	case 9:
		{
			int i;
			bool flag = FALSE;
			if(only_info) return format("");

			for (i = 1; i < m_max; i++)
			{
				monster_type *m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;

				//v1.1.38 視界内でなかったので修正
				if(!projectable(m_ptr->fy,m_ptr->fx,py,px)) continue;

				if(rankup_monster(i,0))	flag = TRUE;
			}

			if(!flag)
				msg_print(_("何も起こらなかった..", "Nothing happens."));
		}
		break;

	case 10:
		{
			int dam = plev * 6 + chr_adj * 10;

			if(only_info) return format(_str_eff_dam,dam);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("純光がダンジョンを埋め尽くした！", "Pure light spreads across the dungeon!"));
			fire_spark(GF_PURIFY, dir, dam, 2);
			break;
		}

	case 11:
		{

			int i;
			bool flag = FALSE;
			if(only_info) return format("");
			msg_print(_("あなたは周囲のモンスターたちを純化しようと試みた...",
                        "You attempt to purify nearby monsters..."));

			for (i = 1; i < m_max; i++)
			{
				char m_name[80];

				monster_type *m_ptr = &m_list[i];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];

				if(!m_ptr->r_idx) continue;
				if(!projectable(m_ptr->fy,m_ptr->fx,py,px)) continue;
				if(r_ptr->flags1 & RF1_QUESTOR) continue;

				if(m_ptr->hp > m_ptr->maxhp / 2)
				{
					monster_desc(m_name, m_ptr, 0);
					msg_format(_("%sは完全に治った！", "%s is fully healed!"),m_name);
					m_ptr->hp = m_ptr->maxhp;
					if (p_ptr->health_who == i) p_ptr->redraw |= (PR_HEALTH);
					if (p_ptr->riding == i) p_ptr->redraw |= (PR_UHEALTH);
					redraw_stuff();
				}
				else
				{
					bool dummy;
					mon_take_hit(i,32767,&dummy,_("は倒れた。", " is defeated."));
				}
				flag = TRUE;

			}

			if(!flag)
				msg_print(_("何も起こらなかった。", "Nothing happens."));

			break;
		}



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





