cptr do_cmd_class_power_aux_hecatia(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{
	case 0:
		{
			if(only_info) return "";
			msg_print(_("あなたは周囲を無遠慮に睨め回した。", "You intently observe your surroundings without hesitation."));
			probing();
		}
		break;
	case 1:
		{
			int level = plev * 2;
			int num = 2 + plev / 12;
			bool flag = FALSE;
			if(only_info) return format(_("召喚レベル:%d", "sum lvl: %d"),level);
			ignore_summon_align = TRUE;
			if(one_in_(3))
			{
				if(summon_named_creature(-1,py,px,MON_CLOWNPIECE,(PM_FORCE_PET))) flag = TRUE;
			}
			for(;num>0;num--)
			{
				if(summon_specific(-1, py, px, level, SUMMON_DEATH, (PM_ALLOW_UNIQUE | PM_FORCE_PET | PM_ALLOW_GROUP))) flag = TRUE;

			}
			if(flag) msg_format(_("地獄の住人を呼び出した。", "You summon residents of Hell."));
			else msg_format(_("何も現れなかった・・", "Nobody has appeared..."));
			ignore_summon_align = FALSE;

		}
		break;
	case 2://体交代
		{
			if(only_info) return format(" ");
			if(!hecatia_change_body()) return NULL;
		}
		break;
	case 3:
		{
			int dir;
			bool flag_ok = TRUE;
			int count_roads=0;
			if(only_info) return format(" ");

			if(is_daytime())
			{
				msg_print(_("昼間には使えない。", "You can't use this ability during daytime."));
				return NULL;
			}
			//十字路にいるかどうか判定
			for(dir = 1 ; dir <= 9 ; dir++)
			{
				int x,y;
				y = py + ddy[dir];
				x = px + ddx[dir];
				if(!in_bounds(y,x))
				{
					flag_ok = FALSE;
				}
				else if(y==py && x==px)
				{
					if(!cave_have_flag_bold(y,x,FF_FLOOR)) flag_ok = FALSE;
				}
				else if(!ddx[dir] || !ddy[dir])
				{
					if(cave_have_flag_bold(y,x,FF_FLOOR)) count_roads++;
				}
				else
				{
					if(!cave_have_flag_bold(y,x,FF_WALL)) flag_ok = FALSE;
				}
			}
			if(count_roads < 3) flag_ok = FALSE;

			if(!flag_ok)
			{
				msg_print(_("十字路か三叉路でないと使えない。", "You have to be standing at crossroads or at a 3-way junction."));
				return NULL;
			}

			msg_print(_("あなたは自分の居所から力を行使した..", "You tap into the power of your location..."));
			hack_flag_access_home = TRUE;
			do_cmd_store();
			hack_flag_access_home = FALSE;
		}
		break;
	case 4:
		{
			int power = plev * 16;
			if(power < 200) power = 200;
			if(only_info) return format(_str_eff_power,power);

			msg_print(_("あなたは威圧的な雰囲気を発した。", "You emit oppressive atmosphere."));
			charm_undead(power);

		}
		break;

	case 5:
		{
			int base = 9 + plev / 3;
			int time;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			set_tim_general(time,FALSE,0,0);
			set_tim_reflect(time,FALSE);
			set_resist_magic(time,FALSE);

		}
		break;

	case 6:
		{
			int dice = plev/2;
			int sides = 15 + chr_adj / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			msg_format(_("地獄の業火が周囲を焼き払った！", "Hellfire scorches everything around you!"));
			project_hack2(GF_HELL_FIRE,dice,sides,0);
			break;
		}

	case 7:
		{
			bool flag = FALSE;
			int i;
			int dice = 6 + plev / 12;
			int sides = 3 + chr_adj / 10;
			int num = 8 + plev / 6;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);
			sakuya_time_stop(FALSE);
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_WATER, damroll(dice,sides),2, 0,0)) flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_("周囲に敵が見当たらなかった。", "You don't see any nearby enemies."));
					break;
				}
			}

		}
		break;

	case 8:
		{
			int base = plev * 10;
			int sides = chr_adj * 20;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			fire_ball_jump(GF_METEOR,dir,base+randint1(sides),6,_("巨大隕石が大地を穿った！","A massive meteor strikes the earth!"));


		}
		break;
	case 9:
		{
			int i;
			int sp[3];
			int x, y;
			monster_type *m_ptr;
			char m_name[80];

			if(only_info) return format(_("損傷:不定", "dam: undef"));
			//すべての体の今のMPを得る
			for(i=0;i<3;i++)
				sp[i] = p_ptr->magic_num1[9+i] * p_ptr->magic_num1[3+i] / 10000;
			sp[p_ptr->magic_num2[0]] = p_ptr->csp;

			if(sp[0] < 100 || sp[1] < 100 || sp[2] < 100)
			{
				msg_print(_("他の体のMPが足りない。", "You don't have enough MP in your other bodies."));
				return NULL;
			}

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}

			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}

			monster_desc(m_name, m_ptr, 0);
			msg_format(_("あなたは三体全てを使って%sを地獄に落とすことにした！",
                        "You use all of your three bodies to drop %s into Hell!"),m_name);

			for(i=0;;i++)
			{
				int typ,dam;
				if(!m_ptr->r_idx) break;
				if(sp[i%3] < (i/3+1) * 100) break;//攻撃回数*100のMPを下回る体が出たら終了。最大18発出るはず

				switch(randint1(4))
				{
				case 1://巨大ボール発生
					{
						int rad = 5 + randint1(3);

						dam = 200+chr_adj*5+randint1(100);

						if(one_in_(2)) typ = GF_METEOR;
						else if(one_in_(2)) typ = GF_DISINTEGRATE;
						else if(one_in_(4)) typ = GF_DARK;
						else if(one_in_(3)) typ = GF_WATER;
						else if(one_in_(2)) typ = GF_HELL_FIRE;
						else typ = GF_TIME;

						if(typ == GF_WATER)
							project(0,4+randint1(3),y,x,10,GF_WATER_FLOW,(PROJECT_JUMP|PROJECT_GRID|PROJECT_ITEM),-1);

						project(0,rad,y,x,dam,typ,(PROJECT_JUMP|PROJECT_GRID|PROJECT_ITEM|PROJECT_KILL),-1);
					}
					break;
				case 2: //巨大レーザー
					{
						dam = 200+chr_adj*5+randint1(100);
						if(one_in_(7))		typ = GF_PSY_SPEAR;
						else if(one_in_(6))	typ = GF_LITE;
						else if(one_in_(5)) typ = GF_DISINTEGRATE;
						else if(one_in_(4)) typ = GF_DISENCHANT;
						else if(one_in_(3)) typ = GF_MANA;
						else if(one_in_(2)) typ = GF_NETHER;
						else  typ = GF_CHAOS;

						fire_spark(typ,dir,dam,randint1(2));

					}
					break;
				case 3: //小型ボール周囲連続発生
					{
						int num = 5 + randint1(3);
						int j;

						if(one_in_(2)) typ = GF_METEOR;
						else if(one_in_(2)) typ = GF_FIRE;
						else if(one_in_(3)) typ = GF_SHARDS;
						else if(one_in_(2)) typ = GF_CHAOS;
						else typ = GF_DARK;

						for(j=num;j>0;j--)
						{
							int rad = 1+randint1(2);
							int tx,ty;

							dam = 100 + chr_adj*2+ randint1(50);

							tx = rand_spread(x,4);
							ty = rand_spread(y,4);
							if(!in_bounds(ty,tx) || !cave_have_flag_bold(ty,tx,FF_PROJECT)) continue;
							if(!projectable(py,px,ty,tx)) continue;

							if(typ == GF_FIRE)
								project(0,randint1(3),ty,tx,5,GF_LAVA_FLOW,(PROJECT_JUMP|PROJECT_GRID|PROJECT_ITEM),-1);

							project(0,rad,ty,tx,dam,typ,(PROJECT_JUMP|PROJECT_GRID|PROJECT_ITEM|PROJECT_KILL),-1);

						}
					}
					break;
				case 4: //連続ロケット
					{
						int num = 2 + randint1(3);

						int j;

						if(one_in_(6)) typ = GF_METEOR;
						else if(one_in_(5)) typ = GF_DARK;
						else if(one_in_(4)) typ = GF_NETHER;
						else if(one_in_(3)) typ = GF_HELL_FIRE;
						else if(one_in_(2)) typ = GF_NUKE;
						else typ = GF_SOUND;

						for(j=num;j>0;j--)
						{
							dam = 50 + chr_adj + randint1(50);
							fire_rocket(typ,dir,dam,2);
						}
					}
					break;
				}
			}

			//全ての体のMPを0にする
			for(i=0;i<3;i++)p_ptr->magic_num1[3+i] = 0;

		}
		break;

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




