/*:::依姫の「八百万の神々降神」で出てくる神格*/
static bool monster_hook_yorihime(int r_idx)
{
	monster_race *r_ptr = &r_info[r_idx];

	//自分と姉は呼ばない
	if(r_idx == MON_YORIHIME) return FALSE;
	if(r_idx == MON_TOYOHIME) return FALSE;
	//純狐とヘカーティアも止めとく
	if(r_idx == MON_JUNKO) return FALSE;
	if(r_idx == MON_HECATIA1) return FALSE;
	if(r_idx == MON_HECATIA2) return FALSE;
	if(r_idx == MON_HECATIA3) return FALSE;

	//神格フラグを持たないものは対象外
	if(!(r_ptr->flags3 & RF3_DEITY)) return FALSE;


	//特技を持たない神格はFALSE
	if(!(r_ptr->flags4 | r_ptr->flags5 | r_ptr->flags6 | r_ptr->flags9)) return FALSE;

	return TRUE;

}

cptr do_cmd_class_power_aux_yorihime(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int dir;

	switch(num)
	{
	case 0://八百万の神々降神
		{
			int r_idx = 0;
			if(only_info) return format("");

			//神格のr_idxを選定
			get_mon_num_prep(monster_hook_yorihime,NULL);//打倒済み神格がちゃんと出るかチェック
			r_idx = get_mon_num(MAX(dun_level,p_ptr->lev));
			get_mon_num_prep(NULL, NULL);

			if(!r_idx)
			{
				msg_print(_("何も降りてこなかった...", "Nobody comes down..."));
				break;
			}
			else
			{
				u32b rflags;
				int j,new_num;
				int mlist_num = 0;
				int memory = 20; //魔法記憶数　常に20
				int shift;
				//初期化
				for(j=0;j<20;j++)p_ptr->magic_num1[j]=0;

				//選定された神格r_idxのもつ特技を一覧にしてmagic_num1[0-19]に登録
				for(j=0;j<4;j++)
				{
					int spell;
					monster_race *r_ptr = &r_info[r_idx];

					if(j==0) rflags = r_ptr->flags4;
					else if(j==1) rflags = r_ptr->flags5;
					else if(j==2) rflags = r_ptr->flags6;
					else rflags = r_ptr->flags9;

					for(shift=0;shift<32;shift++)
					{
						if((rflags >> shift) % 2 == 0) continue;
						spell = shift+1 + j * 32;

						if(spell < 1 || spell > 128)
						{
							msg_format(_("ERROR:依姫神格特技設定ルーチンで不正なspell値(%d)が出た",
                                        "ERROR: Incorrect spell value (%d) in Yorihime's deity special ability routine"),spell);
							return NULL;
						}
						if(!monspell_list2[spell].level) continue; //特別な行動や未実装の技は非対象

						if(mlist_num < memory) new_num = mlist_num;
						else
						{
							new_num = randint0(mlist_num+1);
							if(new_num >=memory) continue;
						}
						p_ptr->magic_num1[new_num] = spell;
						mlist_num++;
					}
				}
			}
			p_ptr->magic_num1[20] = r_idx;
			set_kamioroshi(KAMIOROSHI_YAOYOROZU, 0);

			 //キャンセルしても行動順を消費する
			if(!cast_monspell_new())
			{
				cptr mname;

				//v1.1.30 ランダムユニークの名前処理追加
				if(IS_RANDOM_UNIQUE_IDX(r_idx))
					mname = random_unique_name[r_idx - MON_RANDOM_UNIQUE_1];
				else
					mname = r_name + r_info[r_idx].name;

				//「中神霊の神霊は〜」とかメッセージ出たら変なのでstreqで分岐しようとしたがうまくいかない。全角に対応していないのか。
				//if(streq(r_name + r_info[r_idx].name, "神霊"))
				if(r_idx == MON_C_SHINREI || r_idx == MON_D_SHINREI)
					msg_format(_("%sは何もせずに帰っていった。", "%s left without doing anything."),mname);
				else
					msg_format(_("%sの神霊は何もせずに帰っていった。", "The divine spirit of %s left without doing anything."),mname);
			}

		}
		break;



	case 1: //祇園様の剣
		{
			int time = 25 + plev / 2;
			int range = plev/12;
			if(inventory[INVEN_RARM].name1 == ART_AMENOHABAKIRI ||inventory[INVEN_LARM].name1 == ART_AMENOHABAKIRI)
				range += 4;

			if(only_info) return format(_("期間:%d 範囲:%d", "dur: %d rad: %d"),time,range);
			do_cmd_concentrate(0);
			set_kamioroshi(KAMIOROSHI_GION, time);

		}
		break;
	//SLASH,SPEARの攻撃全てとSTINGの攻撃のうちHUMAN,DEMIHUMAN,is_gen_unique()のモンスターによる攻撃を無効化
	//ARROW系のボルトを無効化
	case 2: //金山彦
		{
			int time = 10 + plev / 5;

			if(only_info) return format(_str_eff_dur_plus_1d,time,time);

			if(use_itemcard)
				msg_print(_("何やら金属質な神気があなたを守るのを感じる..", "You feel as if you're protected by some metallic divinity..."));
			else
				msg_print(_("金山彦命があなたの身に降りてきた...", "Kanayamahiko-no-Mikoto comes down into your body..."));
			set_kamioroshi(KAMIOROSHI_KANAYAMAHIKO, time+randint1(time));

		}
		break;

	case 3: //火雷神
		{
			int dam = p_ptr->chp / 3;

			if(dam < 1) dam = 1;
			if(only_info) return format(_str_eff_dam,dam);

			if (!get_aim_dir(&dir)) return NULL;
			set_kamioroshi(KAMIOROSHI_HONOIKAZUTI, 0);

			msg_print(_("火雷神が猛火と落雷を巻き起こした！", "Honoikazuchi-no-Ookami summons fierce flames and lightning!"));
			fire_ball(GF_PLASMA, dir, dam, -2);
		}
		break;
	case 4: //愛宕様の炎　破邪魔法のと同じ
		{
			int base = 5 + plev / 5;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			msg_print(_("愛宕様の神火が身に宿った！", "Atago-sama's divine flame resides in your body!"));
			set_kamioroshi(KAMIOROSHI_ATAGO, randint1(base) + base);

		}
		break;
	case 5: //石凝姥命　破邪魔法のと同じ
		{
			int base = 15 + plev / 5;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_print(_("石凝姥命が現れ、神鏡を掲げた！", "Ishikori-dome-no-Mikoto appears, raising her sacred mirror!"));
			set_kamioroshi(KAMIOROSHI_ISHIKORIDOME, randint1(base) + base);
		}
		break;
	case 6: //天津甕星
		{
			int dam = plev * 200;
			if(dam > 9999) dam = 9999;
			if(only_info) return format(_str_eff_dam,dam);

			set_kamioroshi(KAMIOROSHI_AMATSUMIKABOSHI, 0);
			msg_print(_("天津甕星が明星の輝きを放った！", "Amatsu-Mikaboshi emits the light of the morning star!"));

			//使われてない関数があったのでいつか使いたかった:)
			deathray_monsters();

		}
		break;
	case 7: //伊豆能売　破邪魔法のと同じ
		{
			int dice = 1;
			int sides = plev * 3;
			int base = plev * 3;
			if (only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),dice, sides, base);

			set_kamioroshi(KAMIOROSHI_IZUNOME, 0);
			msg_print(_("鈴の音と共にダンジョンに清浄な空気が満ちた・・", "A bell rings, and the dungeon is filled with holiness..."));
			project_hack2(GF_HOLY_FIRE, dice,sides,base);
			if (remove_all_curse())
			{
				msg_print(_("装備品にかかった呪縛が解けた。", "The curses on your equipment have been removed."));
			}
		}
		break;
	case 8: //天宇受売命　破邪魔法のと同じ
		{
			int base = 10 + plev / 5;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_print(_("あなたは軽やかに舞い始めた。", "You start playfully dancing."));
			set_kamioroshi(KAMIOROSHI_AMENOUZUME, randint1(base) + base);
		}
		break;

	case 9: //天照大御神　破邪魔法のと同じ
		{

			if(only_info) return format(_("損傷:不定", "dam: undef"));

			call_amaterasu();
		}
		break;



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





