cptr do_cmd_class_power_aux_toyohime(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int dir;

	switch(num)
	{
	case 0://テレポート
		{
			int dist = 25 + plev / 2;
			if(only_info) return format(_("距離:%d", "dist: %d"),dist);
			msg_print(_("あなたは瞬時に消えた。", "You blink away."));
			teleport_player(dist, 0L);
		}
		break;

	case 1: //穢身感知
		{
			int rad = 25 + plev * 3 / 2;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("あなたは穢れの兆しを感じ取った・・", "You start tracking signs of impurity..."));
			detect_monsters_living(rad);
		}
		break;
	case 2: //軍団召喚
		{
			bool flag = FALSE;
			int i;
			int count = 2 + plev / 5;

			if(only_info) return "";
			for(i=0;i<count;i++)
			{
				int r_idx;
				if(randint1(p_ptr->lev) > 29)
					r_idx = MON_MOON_SENTINEL;
				else if(randint1(p_ptr->lev) > 19)
					r_idx = MON_GYOKUTO;
				else
					r_idx = MON_EAGLE_RABBIT;

				if(summon_named_creature(0,py,px,r_idx,PM_FORCE_PET)) flag = TRUE;

			}
			if(flag)
				msg_print(_("部下達を召集した。", "You summon your subordinates."));
			else
				msg_print(_("誰も現れなかった...", "Nobody has appeared..."));

		}
		break;

	case 3:
		{
			if(only_info) return format("");

			if (!get_check(_("本当に他の階にテレポートしますか？", "Really teleport to another level?"))) return NULL;
			teleport_level(0);
		}
		break;
	case 4:
		{
			int range = plev;
			if(only_info) return format(_("距離:%d", "dist: %d"),range);
			if (!dimension_door(D_DOOR_TOYOHIME)) return NULL;
		}
		break;

	case 5: //追放
		{
			int power = 100 + plev * 3;

			if(only_info) return format(_str_eff_power,power);

			if(plev < 40)
			{
				if (!get_aim_dir(&dir)) return NULL;
				fire_ball_hide(GF_GENOCIDE, dir, power, 0);
			}
			else
			{

#ifdef JP
				if(one_in_(7))
					msg_print("「穢土に生まれ、悪心に操られし穢身、お前の浄土はここではない！」");
#endif

				mass_genocide(power, TRUE);
			}


		}
		break;
	case 6: //次元の扉U
		{
			int x, y;
			monster_type *m_ptr;
			int range = plev * 2;

			if(only_info) return format(_("距離:%d", "dist: %d"),range);
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				int tx,ty;
				int dist;
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				if (!tgt_pt(&tx, &ty)) return NULL;

				dist = distance(px,py,tx,ty);

				if(is_hostile(m_ptr) && r_ptr->flagsr & RFR_RES_TELE)
				{
					msg_format(_("%sには耐性がある！", "%s resists!"),m_name);
				}
				else if( randint1(dist) > range)
				{
					msg_print(_("空間操作に失敗した！", "You fail to manipulate space-time!"));
					teleport_away(cave[y][x].m_idx, randint1(250), TELEPORT_PASSIVE);
				}
				else
				{

					msg_format(_("%sを離れた場所へ送り込んだ。", "You send %s to a distant location."),m_name);
					teleport_monster_to(cave[y][x].m_idx, ty, tx, range,TELEPORT_PASSIVE);
				}
			}
		}
		break;


	case 7:
		{
			int tx,ty,i;
			bool flag = FALSE;

			if(only_info) return format(_("距離:無制限", "dist: unlim"));

			if (!tgt_pt(&tx, &ty)) return NULL;

			msg_print(_("あなたは彼方の景色を目の前のものとし、配下たちを送り込んだ！",
                        "You send your followers to the position before your eyes!"));
			for (i = 1; i < m_max; i++)
			{
				monster_type *m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (!is_pet(m_ptr)) continue;
				teleport_monster_to(i, ty, tx, 100,0L);
				flag = TRUE;

			}

			if(!flag)
				msg_print(_("...しかし連れていく配下がいなかった。", "...However, no followers appeared."));

		}
		break;
	case 8: //公転周期の罠
		{
			if(only_info) return format("");

			if ((p_ptr->inside_quest && is_fixed_quest_idx(p_ptr->inside_quest)) || EXTRA_QUEST_FLOOR)
			{
				msg_print(_("ここでは使えない。", "You can't use this ability here."));
				return NULL;
			}

			if(use_itemcard)
				msg_print(_("フロアの何かが僅かに欠けた気がする...", "Something feels amiss from the level..."));
			else
				msg_print(_("あなたは師匠が月に仕掛けたトラップを起動した。", "You activate the trap your teacher has set up on the Moon."));

			p_ptr->special_flags |= SPF_ORBITAL_TRAP;
			p_ptr->redraw |= (PR_STATUS);
			redraw_stuff();

		}
		break;

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}







