cptr do_cmd_class_power_aux_kaguya(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{
	case 0:	case 1:
	case 2:	case 3:
	case 4:
		{
			bool nandai = FALSE;
			int dam;
			//numを配列添え字に使っているので変更時注意
			int gf_list[5] = {GF_FIRE,GF_COLD,GF_ELEC,GF_POIS,GF_ACID};

			if(inventory[INVEN_LITE].name1 == ART_KAGUYA_DRAGON) nandai = TRUE;

			dam = 25 + plev + chr_adj * 3/2  ;
			if(nandai) dam *= 3;

			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;

			if(nandai)
			{
				msg_format(_("龍の頸の玉から%sのブレスが放たれた！", "The Dragon's Neck Jewel breathes %s!"),gf_desc_name[gf_list[num]]);
				fire_ball(gf_list[num], dir, dam,-2);
			}
			else
			{
				msg_format(_("%sの玉を撃った。", "You throw a jewel of %s."),gf_desc_name[gf_list[num]]);
				fire_ball(gf_list[num], dir, dam,1);
			}



		}
		break;
	case 5:
		{
			bool nandai = FALSE;
			int base = 10 + p_ptr->lev/5;
			if(inventory[INVEN_RARM].name1 == ART_KAGUYA_HOTOKE || inventory[INVEN_LARM].name1 == ART_KAGUYA_HOTOKE) nandai = TRUE;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			if(nandai)
			{
				msg_print(_("仏の御石の鉢が強固な防壁を生み出した！", "Buddha's Stone Bowl forms a strong barrier!"));
				set_magicdef(randint1(base) + base, FALSE);
			}
			else
			{
				msg_print(_("あなたは心を落ち着かせた..", "You calm down your mind..."));
				set_resist_magic(randint1(base) + base,FALSE);
			}

		}
		break;
	case 6:
		{
			bool nandai = FALSE;
			if(inventory[INVEN_OUTER].name1 == ART_KAGUYA_HINEZUMI) nandai = TRUE;

			if(nandai)
			{
				int dam = plev * 3 + chr_adj * 5;
				if(only_info) return format(_str_eff_dam,dam);
				msg_print(_("火鼠の皮衣から猛火が放たれた！", "Intensive flames shoot out from the Robe of Fire Rat!"));
				project_hack2(GF_FIRE,0,0,dam);
			}
			else
			{
				int base = 20;
				int time;
				if(only_info) return format(_str_eff_dur_plus_1d,base,base);
				time = base + randint1(base);
				set_tim_sh_fire(time, FALSE);
				set_oppose_fire(time, FALSE);
			}
		}
		break;

	case 7:	//新難題　持ってるかどうかはほかで判定する
		{
			int base = plev * 6;
			int sides = chr_adj * 6;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_METEOR, dir, base + randint1(sides), 2,_("月のかけらが降ってきた。", "A piece of the Moon falls down."))) return NULL;
		}
		break;
	case 8:
		{
			bool nandai = FALSE;
			int heal;
			if(inventory[INVEN_NECK].name1 == ART_KAGUYA_TSUBAME) nandai = TRUE;

			if(nandai)
			{
				int v = 10 + randint1(10);
				heal = 300+plev*2+chr_adj*4;
				if(only_info) return format(_str_eff_heal,heal);
				set_tim_addstat(A_STR,105,v,FALSE);
				set_tim_addstat(A_DEX,105,v,FALSE);
				set_tim_addstat(A_CON,105,v,FALSE);
				restore_level();
				do_res_stat(A_STR);
				do_res_stat(A_INT);
				do_res_stat(A_WIS);
				do_res_stat(A_DEX);
				do_res_stat(A_CON);
				do_res_stat(A_CHR);
				msg_print(_("燕の子安貝から生命の力がとめどなく湧き出してくる！",
                            "Inexhaustible life force pours out of Swallow Cowrie's Shell!"));
			}
			else
			{
				heal = 100+plev + chr_adj*2;
				if(only_info) return format(_str_eff_heal,heal);
				msg_print(_("優しい光があなたを包んだ。", "You are surrounded by a gentle light."));

			}
			hp_player(heal);
			set_stun(0);
			set_cut(0);
			set_poisoned(0);
		}
		break;
	case 9:	//新難題　持ってるかどうかはほかで判定する
		{
			int base = plev/5;

			if(only_info) return format(_("回復:%d+1d%d", "rec: %d+1d%d"),base,base);

			msg_print(_("不思議な魔力が体に満ちる..", "Mysterious magical power fills your body..."));
			player_gain_mana(base + randint1(base));

		}
		break;
	case 10:
		{
			bool nandai = FALSE;
			int dice,sides,dam;
			int typ_list[7] = {GF_LITE,GF_DARK,GF_MANA,GF_WATER,GF_SHARDS,GF_FORCE,GF_DISP_ALL};

			if(inventory[INVEN_RARM].name1 == ART_KAGUYA_HOURAI || inventory[INVEN_LARM].name1 == ART_KAGUYA_HOURAI) nandai = TRUE;

			if(use_itemcard) nandai = TRUE;

			dice = 4 + plev / 4;
			sides = 25 + chr_adj / 2;

			if(nandai) sides *= 2;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			if (!get_aim_dir(&dir)) return NULL;

			dam = damroll(dice,sides);

			if(nandai)
			{
				msg_format(_("蓬莱の玉の枝が七色の光を放った！", "Rainbow light shoots out from the Jewel Branch of Hourai!"));
				fire_ball(typ_list[randint0(7)], dir, dam,5);
			}
			else
			{
				msg_format(_("きらめく球を放った。", "You throw a shining sphere."));
				fire_ball(typ_list[randint0(2)], dir, dam,1);
			}
		}
		break;
	case 11:
		{
			int xx, yy,i;
			bool flag = FALSE;
			if(only_info) return "";

			if ((p_ptr->inside_quest && !random_quest_number(dun_level)) || p_ptr->inside_arena || p_ptr->inside_battle)
			{
				msg_print(_("ここでは使えない。", "You can't use this ability here."));
				return NULL;
			}
			for(i=0;i<8;i++)
			{
				yy = py + ddy_cdd[i];
				xx = px + ddx_cdd[i];
				if(!in_bounds(yy,xx)) continue;
				if(!cave_have_flag_bold((yy), (xx), FF_WALL)) continue;
				if(cave_have_flag_bold((yy), (xx), FF_PERMANENT)) continue;
				if(cave[yy][xx].m_idx)continue;
				cave_set_feat(yy, xx, feat_permanent);
				flag = TRUE;
			}
			if(flag)
				msg_print(_("周囲の壁に永遠の術をかけた。", "You grant eternity to the nearby walls."));
			else
				msg_print(_("何も起こらなかった。", "Nothing happens."));
		}
		break;
	case 12:
		{
			int x, y;
			int dist = plev * 2;
			if(dist < 30) dist = 30;
			if(only_info) return format(_("移動コスト:%d", "%d spaces"),dist);
			if (!tgt_pt(&x, &y)) return NULL;

			if (!player_can_enter(cave[y][x].feat, 0) || !(cave[y][x].info & CAVE_KNOWN))
			{
				msg_print(_("そこには行けない。", "You can't use this ability here."));
				return NULL;
			}
			forget_travel_flow();
			travel_flow(y,x);
			if(dist < travel.cost[py][px])
			{
				if(travel.cost[py][px] >= 9999)
					msg_print(_("そこには道が通っていない。", "There's no known path to that place."));
				else
					msg_print(_("そこは遠すぎる。", "It's too far away."));
				return NULL;
			}

			msg_print(_("あなたは一瞬で移動した！", "You move instantaneously!"));
			teleport_player_to(y,x,TELEPORT_NONMAGICAL);
		}
		break;
	case 13:
		{
			int dice = p_ptr->lev;
			int sides = chr_adj;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("エイジャの赤石が太陽の輝きを放った！", "The Red Stone of Aja emits sunlight!"));
			fire_spark(GF_NUKE,dir, damroll(dice,sides),1);

			break;
		}
	case 14:
		{
			int time = 5;

			if(only_info) return format(_str_eff_dur,time);
			msg_format(_("永遠の魔法があなたを守った。", "You protect yourself with magic of eternity."));
			set_invuln(time,FALSE);

		}
		break;
	case 15:
		{
			int dam = 1600;
			bool flag_ok = TRUE;
			int i,j,tx,ty;

			if(only_info) return format(_str_eff_dam,dam);

			for (i = -3; i < 3; i++) for (j = -3; j < 3; j++)
			{
				ty = py + i;
				tx = px + j;
				if(!in_bounds2(ty,tx) || !cave_have_flag_bold(ty,tx,FF_HURT_DISI) && !cave_have_flag_bold(ty,tx,FF_PROJECT) || cave_have_flag_bold(ty,tx,FF_LESS))
				{
					flag_ok = FALSE;
					break;
				}
			}
			msg_print(_("金閣寺の一枚天井が上から降ってきた！", "The Seamless Ceiling of Kinkaku-Ji falls from above!"));
			if(!flag_ok)
			{
				msg_format(_("...しかし何かにつっかえて止まった。", "...However, it appears to be stuck on something."));
				break;
			}

			project_hack2(GF_KINKAKUJI,0,0,dam);
		}
		break;
	case 16:
		{
			if(only_info) return format(_("行動:5回", "5 actions"));
			if (world_player)
			{
				msg_print(_("既に時は止まっている。", "Time already has been stopped."));
				return NULL;
			}
			msg_print(_("あなたは須臾の術を発動した。", "You manipulate the instantaneous."));
			world_player = TRUE;
			msg_print(NULL);
			//v1.1.58
			flag_update_floor_music = TRUE;
			p_ptr->energy_need -= 1500;
			p_ptr->redraw |= (PR_MAP);
			p_ptr->update |= (PU_MONSTERS);
			p_ptr->window |= (PW_OVERHEAD | PW_DUNGEON);

			handle_stuff();
			msg_print(_("時が止まって見える！", "Time has stopped!"));
			break;
		}



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}






