cptr do_cmd_class_power_aux_seiran(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int dir;

	switch(num)
	{
	case 0:
		{
			int dice = 1 + plev / 16;
			int sides = 3;
			object_type forge, *q_ptr = &forge;
			if(only_info) return format(_("個数:%dd%d", "amt: %dd%d"),dice,sides);
			msg_print(_("あなたは団子を搗き始めた...", "You start making dango..."));
			object_prep(q_ptr, lookup_kind(TV_SWEETS, SV_SWEETS_DANGO));
			q_ptr->discount = 99;
			q_ptr->number = damroll(dice,sides);
			drop_near(q_ptr, -1, py, px);
		}
		break;
	case 1:
	case 3:
	case 4:
	case 5:
	case 7:
	case 8:
	{
			if(only_info) return "";

			if(num == 1)
				special_shooting_mode = SSM_SEIRAN1;
			else if(num == 3)
				special_shooting_mode = SSM_SEIRAN2;
			else if (num == 4) //v1.1.53追加
				special_shooting_mode = SSM_SLAY_HUMAN;
			else if(num == 5)
				special_shooting_mode = SSM_SEIRAN3;
			else if (num == 7) //v1.1.53追加
				special_shooting_mode = SSM_SLAY_FLYER;
			else if (num == 8)
				special_shooting_mode = SSM_SEIRAN5;

			if(!do_cmd_fire())
			{
				special_shooting_mode = 0L;
				return NULL;
			}
		}
		break;
	case 2:
		{
			int i;
			bool flag = FALSE;
			int num;
			if(only_info) return "";

			msg_print(_("あなたはメーデーを発信した！", "You signal 'Mayday'!"));
			num = randint1(6) + p_ptr->lev / 5 - dun_level / 3;
			if(p_ptr->inside_quest) num /= 3;

			for(i=0;i<num;i++)
			{
				if (summon_named_creature(0, py, px, MON_EAGLE_RABBIT, PM_FORCE_FRIENDLY)) flag = TRUE;
			}
			if(flag)
				msg_print(_("仲間が助けに現れた！", "Allies come to your aid!"));
			else
				msg_print(_("しかし誰も来なかった...", "However, nobody comes..."));

		}
		break;
	case 6:
		{
			int timeout_base, timeout_max;
			object_type *o_ptr;
			bool flag = FALSE;
			if(only_info) return "";

			if (!get_aim_dir(&dir)) return NULL;

			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}

			if(one_in_(3))
				msg_print(_("「見せてやるわ、月面最速ガンナーの妙技を！」",
                            "'I'll show you the skills of the swiftest gunner on the Moon!'"));
			else
				msg_print(_("残弾全てを乱射した！", "You start firing all of your remaining ammo!"));

			special_shooting_mode = SSM_SEIRAN4;
			while(1)
			{
				bool righthand = FALSE;
				bool lefthand = FALSE;

				if(inventory[INVEN_RARM].tval == TV_GUN)
				{
					o_ptr = &inventory[INVEN_RARM];
					timeout_base = calc_gun_timeout(o_ptr) * 1000;
					timeout_max = timeout_base * (calc_gun_load_num(o_ptr) - 1);
					if(o_ptr->timeout <= timeout_max) righthand = TRUE;
				}
				if(inventory[INVEN_LARM].tval == TV_GUN)
				{
					o_ptr = &inventory[INVEN_LARM];
					timeout_base = calc_gun_timeout(o_ptr) * 1000;
					timeout_max = timeout_base * (calc_gun_load_num(o_ptr) - 1);
					if(o_ptr->timeout <= timeout_max) lefthand = TRUE;
				}
				if(!righthand && !lefthand) break;
				else flag = TRUE;

				if(righthand)do_cmd_fire_gun_aux(INVEN_RARM,dir);
				if(!target_okay()) break;
				if(lefthand)do_cmd_fire_gun_aux(INVEN_LARM,dir);
				if(!target_okay()) break;
			}

			if(!flag) msg_print(_("..しかし元々弾がなかった。", "...But you didn't have any ammo."));
		}
		break;

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}

	//特殊射撃フラグをリセット
	special_shooting_mode = 0L;

	return "";
}


//所持品から指定個数の団子を減らす。足りないとき何もせずFALSEを返す。
bool ringo_dec_dango(int num)
{
	int i,cnt=0;
	object_type *o_ptr;

	//まずザック全ての団子の数を数える。銘や割引で細かく分かれている可能性を考慮。
	for(i=0;i<INVEN_PACK;i++)
	{
		o_ptr = &inventory[i];
		if(!o_ptr->k_idx) continue;
		if(o_ptr->tval != TV_SWEETS || o_ptr->sval != SV_SWEETS_DANGO) continue;
		cnt += o_ptr->number;
	}

	if(cnt < num)
	{
		msg_print(_("団子が足りない...", "You don't have enough dangos..."));
		return FALSE;
	}
	if(num == 1)
		msg_format(_("あなたは団子をひとつ食べた。", "You eat a dango."));
	else if(num < 6)
		msg_format(_("%d個の団子を頬張った！", "You munch on %d dangos!"),num);
	else
		msg_format(_("%d個の団子を一気に呑み込んだ！", "You swallow %d dangos in one go!"),num);

	//これまで食べた団子の個数カウント
	p_ptr->magic_num1[0] += num;
	if(p_ptr->magic_num1[0] > 10000) p_ptr->magic_num1[0] = 10000;


	//団子を指定個数減らす
	for(i=0;i<INVEN_PACK;i++)
	{
		o_ptr = &inventory[i];
		if(!o_ptr->k_idx) continue;
		if(o_ptr->tval != TV_SWEETS || o_ptr->sval != SV_SWEETS_DANGO) continue;
		if(o_ptr->number < num)
		{
			num -= o_ptr->number;
			inven_item_increase(i,-(o_ptr->number));
		}
		else
		{
			inven_item_increase(i,-num);
			num = 0;
		}

		if(!num) break;
	}
	//optimizeはまとめて行う。上のループで減らすたびに処理すると処理されるべきインベントリがずれるため。
	for(i=INVEN_PACK-1;i>=0;i--) inven_item_optimize(i);
	p_ptr->window |= (PW_INVEN);


	return TRUE;
}

