cptr do_cmd_class_power_aux_yukari(int num, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			if(only_info) return format("");

			if (!wall_through_telepo(0)) return NULL;

			break;
		}
	case 1:
		//v1.1.44 ルーチン見直し
		{
			int dam = 10 + plev / 2 + chr_adj / 2;
			int rad = 0;
			monster_type *m_ptr;

			if (only_info) return format(_("損傷:%d * 不定", "dam: %d*undef"), dam);

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if (m_ptr->r_idx == MON_RAN)
				{
					if (m_ptr->mflag & MFLAG_EPHEMERA) msg_print(_("すでに藍を使役中だ。", "Ran's already performing her duty."));
					else msg_print(_("藍は呼び出しに応じない。", "You call Ran, but she doesn't respond."));
					return NULL;
				}
			}

			msg_format(_("藍が回転しながらスキマから飛び出した！", "Ran leaps out of a gap while spinning!"));
			if(!execute_restartable_project(0,0,7,0,0,dam,GF_ARROW,rad))
				msg_format(_("藍「紫様、標的がいませんが。」", "Ran - 'Lady Yukari, I don't see any targets.'"));

		}
	/*
		{
			int tx,ty;
			int dam = 10 + plev/2 + chr_adj/2 ;
			int rad = 0;
			int flg = (PROJECT_STOP | PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL | PROJECT_MADAN);
			monster_type *m_ptr;

			if(only_info) return format("損傷:%d * 不定",dam);

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if(m_ptr->r_idx == MON_RAN)
				{
					if(m_ptr->mflag & MFLAG_EPHEMERA) msg_print("すでに藍を使役中だ。");
					else msg_print("藍は呼び出しに応じない。");
					return NULL;
				}
			}

			if (!get_aim_dir(&dir)) return NULL;

			oonusa_dam = dam;
			tx = px + 99 * ddx[dir];
			ty = py + 99 * ddy[dir];
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			msg_format("藍が回転しながら飛んだ！");
			project(0,  rad, ty, tx, oonusa_dam, GF_ARROW, flg, -1);
			oonusa_dam = 0;
		}
		*/
		break;
	case 2:
		{
			if(only_info) return "";

			if(!cast_monspell_new()) return NULL;
			break;
		}
	case 3:
		{
			int rad = 2 + plev / 16;
			int time = 10+plev/5;
			if(only_info) return format(_("期間:%d 範囲:%d", "dur: %d rad: %d"),time,rad);

			if(p_ptr->tim_general[0])
			{
				msg_print(_("すでに能力を使っている。", "You already are using your ability."));
				return NULL;
			}

			msg_print(_("あなたは境界を操作した。周囲に奇妙な場が形成された・・",
                        "You manipulate boundaries. Strange place forms around you..."));
			set_tim_general(time,FALSE,0,0);


		}
		break;
	case 4:
		{
			int num = 2 + plev / 8;
			int     item;
			int ty,tx;
			int cnt;
			store_type *st_ptr = &town[TOWN_HITOZATO].store[STORE_HOME];
			if(only_info) return format(_("回数:%d", "count: %d"),num);

			if (!get_aim_dir(&dir)) return NULL;
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			else
			{
				tx = px + 99 * ddx[dir];
				ty = py + 99 * ddy[dir];
			}

			for(;num>0;num--)
			{
				object_type *o_ptr;
				object_type forge;
				object_type *q_ptr = &forge;
				char		o_name[MAX_NLEN];

				cnt = 0;
				for(i=0;i<st_ptr->stock_num;i++)
				{
					o_ptr = &st_ptr->stock[i];
					if(!object_is_melee_weapon(o_ptr)) continue;
					cnt += o_ptr->number;
				}

				if(!cnt)
				{
					msg_print(_("スキマを開いたが、何も出てこなかった。",
                                "You open a gap, but nothing comes out."));
					break;
				}

				cnt = randint1(cnt);
				for(item=0;item<st_ptr->stock_num;item++)
				{
					o_ptr = &st_ptr->stock[item];
					if(!object_is_melee_weapon(o_ptr)) continue;
					cnt -= o_ptr->number;
					if(cnt <= 0) break; //このときのitemとo_ptrの武器を投擲する
				}
				if(!object_is_melee_weapon(o_ptr))
				{
					msg_print(_("ERROR:「無限の超高速飛行体」で選定されたアイテムが近接武器ではない",
                                "ERROR: Item picked for 'Infinite Superspeed Flying Object' isn't a melee weapon"));
					break;
				}

				object_copy(q_ptr,o_ptr);
				q_ptr->number = 1;
				object_desc(o_name,q_ptr,OD_NAME_ONLY);
				msg_format(_("スキマから%sが撃ち出された！", "%s flies out of the gap!"),o_name);
				if(do_cmd_throw_aux2(py,px,ty,tx,1,q_ptr,4))
				{
					yukari_dec_home_item(item);
				}

			}
		}
		break;
	case 5:
		{
			if(only_info) return format(_("効果:現実変容", "alter reality"));

			if (p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}

			msg_print(_("夢から醒めるように感覚が曖昧になっていく・・",
                        "Everything becomes vague, as if you were waking up from a dream..."));
			/*:::0ではまずいはず*/
			p_ptr->alter_reality = 1;
			p_ptr->redraw |= (PR_STATUS);
		}
		break;
	case 6:
		{
			int j;
			int num = 2 + plev / 8;
			bool flag_pick = FALSE;
			object_type forge;
			object_type *o_ptr;
			object_type *q_ptr;
			char o_name[MAX_NLEN];

			if(only_info) return format(_("最大:%d個", "up to %d"),num);
			if(use_itemcard)
				msg_print(_("周囲のあちこちにスキマが開いた!", "Gaps open around you!"));
			else
				msg_print(_("あなたはあちこちにスキマを開いた・・", "You open gaps around yourself..."));
			//フロアのアイテムを全て調べる
			for(i=0;i<num;i++)
			{
				int item = 0;
				int item_count_temp = 0;
				//視界内のアイテムをランダムに選定
				for(j=0;j<o_max;j++)
				{
					o_ptr = &o_list[j];
					if (!o_ptr->k_idx) continue;
					if (o_ptr->tval == TV_GOLD) continue;
					if (!projectable(o_ptr->iy,o_ptr->ix,py,px)) continue;

					item_count_temp++;
					if(one_in_(item_count_temp)) item = j;
				}
				if(!item) break;
				flag_pick = TRUE;
				o_ptr = &o_list[item];
				q_ptr = &forge;
				object_copy(q_ptr, o_ptr);
				object_desc(o_name, q_ptr, 0);
				if(!yukari_send_item_to_home(q_ptr))
				{
					msg_print(_("スキマがうまく開かない。拠点がもうアイテムで一杯だ。",
                                "You couldn't properly open a gap. Your base is already full."));
					break;
				}

				msg_format(_("%sがスキマの中に落ちた。", "%s falls into a gap."),o_name);
				floor_item_increase(item, -255);
				floor_item_describe(item);
				floor_item_optimize(item);

			}
			if(!flag_pick)
			{
				msg_print(_("目ぼしい物は見当たらない。", "You don't see anything of note."));
				break;
			}

		}
		break;


	case 7:
		{
			int time = 5 + plev / 2;

			if(only_info) return format(_str_eff_dur,time);

			msg_print(_("強固な結界を張り巡らせた。", "You form a strong barrier around yourself."));
			set_oppose_acid(time, FALSE);
			set_oppose_elec(time, FALSE);
			set_oppose_fire(time, FALSE);
			set_oppose_cold(time, FALSE);
			set_oppose_pois(time, FALSE);

			if(plev > 29) set_shield(time,FALSE);
			if(plev > 39) set_magicdef(time, FALSE);
		}
		break;
	case 8:
		{
			int time = 10+plev/5;
			if(only_info) return format(_str_eff_dur,time);

			if(p_ptr->tim_general[1])
			{
				msg_print(_("すでに能力を使っている。", "You already are using your ability."));
				return NULL;
			}

			msg_print(_("あなたは境界を操作した。", "You manipulate boundaries."));
			set_tim_general(time,FALSE,1,0);


		}
		break;
	case 9:
		{
			/*:::Mega Hack - 魔法書を消費して習得していない魔法を発動する特殊フラグ*/
			if(only_info) return format("");
			flag_spell_consume_book = TRUE;
			do_cmd_cast();
			flag_spell_consume_book = FALSE;

		}
		break;


	case 10:
		{
			int base = plev * 3 + chr_adj * 5;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,base);
			msg_format(_("空間を埋め尽くすようなレーザーが放たれた！", "You fire lasers, filling up the area around you!"));
			project_hack2(GF_YUKARI_STORM,1,base,base);
			break;
		}

	case 11:
		{
			int tmp;
			if(only_info) return format("");

			if(!use_itemcard && p_ptr->chp < 50)
			{
				msg_print(_("体力が少なすぎて使えない。", "You are too low on health."));
				return NULL;
			}
			tmp = p_ptr->csp;
			p_ptr->csp = p_ptr->chp;
			p_ptr->chp = tmp;
			if(p_ptr->csp > p_ptr->msp) p_ptr->csp = p_ptr->msp;
			if(p_ptr->chp > p_ptr->mhp) p_ptr->chp = p_ptr->mhp;
			p_ptr->csp_frac = 0;
			p_ptr->chp_frac = 0;
			p_ptr->redraw |= (PR_HP | PR_MANA);
			redraw_stuff();
			msg_print(_("体力と魔力が入れ替わった。", "You exchange your physical and magical reserves."));
		}
		break;
	case 12:
		{
			if(only_info) return format(_("距離:無制限", "dist: unlim"));
			if (!dimension_door(D_DOOR_YUKARI)) return NULL;
		}
		break;
	case 13:
		{
			int base = 100 + plev * 3;

			if(only_info) return format(_str_eff_dam,base);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("大きく開いたスキマから電車が飛び出してきた！", "A large gap opens, and a train charges out!"));
			fire_beam(GF_TRAIN, dir, base);
			break;
		}
	case 14:
		{
			int i;
			int flag_ran = TRUE;
			int flag_chen = TRUE;
			monster_type *m_ptr;

			if(only_info) return "";

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_RAN) ) flag_ran = FALSE;
				if((m_ptr->r_idx == MON_CHEN) ) flag_chen = FALSE;
			}

			if(flag_ran && summon_named_creature(0, py, px, MON_RAN, PM_EPHEMERA))
			{
				msg_print(_("藍を呼び出した。", "You call Ran."));
			}
			else if(flag_chen && summon_named_creature(0, py, px, MON_CHEN, PM_EPHEMERA))
			{
				msg_print(_("橙を呼び出した。", "You call Chen."));
			}
			else
				msg_print(_("式の召喚に失敗した。", "You fail to summon your shikigami."));

			break;

		}
	case 15:
		{
			int time = 10+plev/5;
			if(only_info) return format(_str_eff_dur,time);

			if(p_ptr->tim_general[2])
			{
				msg_print(_("すでに能力を使っている。", "You already are using this ability."));
				return NULL;
			}

			msg_print(_("あなたは境界を操作した。フロアの気配が変わった・・",
                        "You manipulate boundaries. You sense the atmosphere on the level change..."));
			set_tim_general(time,FALSE,2,0);
		}
		break;
	case 16:
		{
			int dx,dy;
			bool flag_ok = TRUE;
			if(only_info) return format("");

			if(p_ptr->inside_quest
				|| quest[QUEST_OBERON].status == QUEST_STATUS_TAKEN && dungeon_type == DUNGEON_ANGBAND && dun_level == 100
				|| quest[QUEST_SERPENT].status == QUEST_STATUS_TAKEN && dungeon_type == DUNGEON_CHAOS && dun_level == 127)
			{
				msg_print(_("今は使えない。", "You can't use this ability right now."));
				return NULL;
			}

			for(i=1;i<10;i++)
			{
				dx = px + ddx[i];
				dy = py + ddy[i];
				if(!in_bounds(dy,dx) || !cave_clean_bold(dy,dx)) flag_ok = FALSE;
			}

			if(!flag_ok)
			{

				msg_print(_("ここでは使えない。", "You can't use this ability here."));
				return NULL;
			}
			if(use_itemcard)
				msg_print(_("足元に探索拠点への入り口が開いた！", "An entrance to your exploration base appears beneath you!"));
			else
				msg_print(_("あなたは足元にスキマを開いた！", "You open a gap beneath your feet!"));
			cave_set_feat(py, px, f_tag_to_index_in_init("HOME"));

		}
		break;

	case 17:
		{
			int power = 200 + plev * 4;
			if(only_info) return format(_str_eff_power,power);
			mass_genocide(power, TRUE);
		}
		break;
	case 18:
		{
			int y,x;
			if(only_info) return format("");

			if ((p_ptr->inside_quest && is_fixed_quest_idx(p_ptr->inside_quest)) || !dun_level || EXTRA_QUEST_FLOOR)
			{
				msg_print(_("ここでは使えない。", "You can't use this ability here."));
				return NULL;
			}

			msg_print(_("あなたはダンジョンの境界を操作した。", "You manipulate the boundaries of the dungeon."));
			for (y = 1; y < cur_hgt - 1; y++)
			{
				for (x = 1; x < cur_wid - 1; x++)
				{
					if (cave_clean_bold(y,x))
						cave_set_feat(y, x, feat_granite);
					else if(cave_have_flag_bold(y,x,FF_WALL) && !cave_have_flag_bold(y,x,FF_PERMANENT))
						cave_set_feat(y, x, feat_floor);
				}
			}

			p_ptr->redraw |= (PR_MAP);
			p_ptr->window |= (PW_OVERHEAD | PW_DUNGEON);
			handle_stuff();

		}
		break;
	case 19:
		{
			int base = 30;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_format(_("あなたはフロアの全てを計測して演算した！",
                        "You measure and calculate everything on this level!"));
			set_tim_addstat(A_INT,120,time, FALSE);
			set_tim_addstat(A_WIS,120,time, FALSE);
			wiz_lite(FALSE);
			set_foresight(time, FALSE);
			set_radar_sense(time, FALSE);

			break;
		}

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}







