cptr do_cmd_class_power_aux_mamizou(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{
	case 0:
		{
			int num = 3 + plev / 10;
			int dice = 3 + p_ptr->lev / 12;
			int sides = 5 + chr_adj / 8;

			if(only_info) return format(_str_eff_dam_dice_sides_mult,dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("ばら撒いた木の葉が鳥や蛙の形をとって飛んだ。",
                        "You scatter some leaves, which take forms of birds and frogs and fly forth."));
			fire_blast(GF_MISSILE, dir, dice, sides, num, 2,(PROJECT_STOP | PROJECT_KILL | PROJECT_GRID));
		}
		break;
	case 1:
		{
			if(only_info) return format("");
			msg_print(_("あなたは辺りを睨めつけた…", "You carefully look around..."));
			probing();
			break;
		}
	case 2:
		{
			int level = 5 + plev / 2;
			int num = 2 + plev / 12;
			bool flag = FALSE;
			if(only_info) return format(_("召喚レベル:%d", "sum lvl: %d"),level);
			for(;num>0;num--)
			{
				if(summon_specific(-1, py, px, plev, SUMMON_ANIMAL, (PM_EPHEMERA))) flag = TRUE;
			}
			if(flag) msg_format(_("鳥獣の幻が踊った。", "Animal phantoms appear."));
			else msg_format(_("何も現れなかった・・", "Nobody has appeared..."));

		}
		break;

	case 3:
		{
			int x, y;
			int dist = 1 + plev/16;
			int time = 50+plev;
			monster_type *m_ptr;
			char m_name[80];


			if(only_info) return format(_("射程:%d 効果時間:%dターン", "rng: %d dur: %d"),dist,time);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("近くのターゲットを明示的に指定しないといけない。",
                            "You have to pick a nearby target."));
				project_length = 0; //v1.1.76 変身後の特技の射程が短い不具合対応　ここに必要あるのかは不明
				return NULL;
			}

			project_length = 0; //v1.1.76 変身後の特技の射程が短い不具合対応

			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				int r_idx = m_ptr->r_idx;
				int chance;
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				chance = plev + p_ptr->stat_ind[A_INT]+3+p_ptr->stat_ind[A_WIS]+3 - r_ptr->level;
				if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) chance -= 20;
				if(r_ptr->flagsr & RFR_RES_ALL) chance = 0;

				msg_format(_("あなたは%sに化けようと試みた・・", "You attempt to transform into %s..."),m_name);
				if(p_ptr->wizard) msg_format("(chance:%d)",chance);
				if(randint0(100) < chance)
				{
					metamorphose_to_monster(r_idx,time);
				}
				else if(chance < 1)
					msg_format(_("%sにはとても化けられそうにない。", "Doesn't look like you can transform into %s."),m_name);
				else
					msg_print(_("変化に失敗した！", "The transformation failed!"));

			}
		}
		break;


	case 4:
		{
			int rad = 3;
			int base = plev * 10 + chr_adj * 10;

			if(FULL_MOON)
			{
				rad *= 2;
				base *= 2;
			}

			if(only_info) return format(_str_eff_dam_around,base / 2);
			msg_format(_("あなたは太陽に変身した！", "You transform into the sun!"));
			project(0, rad, py, px, base, GF_NUKE, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			break;
		}
		break;


	case 5:
		{
			int dam = 50 + plev * 2 + chr_adj * 10;
			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("巨大な茶釜から蒸気が吐き出された！", "Steam pours forth!"));

			fire_ball(GF_STEAM, dir, dam, -2);
			break;
		}

	case 6:
		{
			int level = plev / 2 + chr_adj;
			int num = 1 + chr_adj / 4;
			bool flag = FALSE;
			if(only_info) return format(_("召喚レベル:%d", "sum lvl: %d"),level);
			for(;num>0;num--)
			{
				if(summon_specific(-1, py, px, plev, SUMMON_TANUKI, (PM_FORCE_PET))) flag = TRUE;
			}
			if(flag) msg_format(_("配下の狸を呼び出した！", "You call your tanuki followers!"));
			else msg_format(_("誰も現れなかった・・", "Nobody has appeared..."));

		}
		break;

	case 7://二ッ岩家の裁き
		{
			int y, x;
			monster_type *m_ptr;
			int time = 3;

			if(only_info) return format(_str_eff_dur_plus_1d,time,time);

			if(p_ptr->tim_general[0])
			{
				msg_print(_("すでに術を実行中だ。", "Your ability already is in effect."));
				return NULL;
			}

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

				if (cave[y][x].m_idx && (m_ptr->ml) )
				{
					char m_name[80];
					int chance = plev + (p_ptr->stat_ind[A_INT]+3) * 2;
					monster_race *r_ptr = &r_info[m_ptr->r_idx];
					monster_desc(m_name, m_ptr, 0);

					msg_format(_("あなたは%sに煙を吹き付けた。", "You blow smoke at %s."),m_name);
					if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) chance = chance*2/3;

					if(r_ptr->flagsr & (RFR_RES_CHAO | RFR_RES_ALL))
					{
						msg_format(_("%sには完全な耐性がある！", "%s is immune!"),m_name);
					}
					else if(p_ptr->magic_num1[0] == cave[y][x].m_idx)
					{
						msg_format(_("既に効いている。", "It's already in effect."));

					}
					else if( chance < r_ptr->level)
					{
						msg_format(_("%sには効きそうにない。", "Doesn't look like you can affect %s."),m_name);
					}
					else if( randint1(chance) < r_ptr->level)
					{
						msg_format(_("%sには効かなかった。", "%s was unaffected."),m_name);
					}
					else
					{
						p_ptr->magic_num1[0] = cave[y][x].m_idx;
						set_tim_general(time+randint1(time),FALSE,0,0);
						lite_spot(m_ptr->fy,m_ptr->fx);
						msg_format(_("%sはコミカルな姿に変身した！", "%s is transformed into a comical form!"),m_name);
						(void)set_monster_csleep(cave[y][x].m_idx, 0);
						anger_monster(m_ptr);
					}
				}
				else
				{
					msg_format(_str_mess_nobody_here);
					return NULL;
				}

			break;
		}
	case 8:
		{
			int dice = plev / 2;
			int sides = 5 + chr_adj / 2;

			if(FULL_MOON) sides *= 2;
			if(only_info) return format(_("損傷:%dd%d*2", "dam: %dd%d*2"),dice,sides);
			msg_print(_("壮大な狸囃子が始まった！", "The magnificient tanuki belly-drumming has begun!"));
			project_hack2(GF_DARK,dice,sides,0);
			project_hack2(GF_SOUND,dice,sides,0);

		}
		break;


	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



