cptr do_cmd_class_power_aux_chemist(int num, bool only_info)
{
	int plev = p_ptr->lev;

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!put_item_into_inven2()) return NULL;
			break;

		}
	case 1:
		{
			if(only_info) return format("");
			display_list_inven2();
			return NULL; //見るだけなので行動順消費なし

		}
	case 2:
		{
			if(only_info) return format("");
			if(!takeout_inven2()) return NULL;
			break;
		}
	case 3:
		{
			int dice = 4 + plev / 4;
			int sides = 7;
			if(only_info) return format(_("回復:%dd%d", "heal: %dd%d"),dice,sides);
			set_cut(0);
			if(plev > 9)set_stun(0);
			set_poisoned(0);
			if(plev > 19)set_image(0);
			hp_player(damroll(dice,sides));
			break;
		}
	case 4: //成分分析
		{
			if (only_info) return format("");
			if (!ident_spell_2(2)) return NULL;
		}
		break;

	case 5:
		{
			object_type forge, *q_ptr = &forge;
			if(only_info) return _("食料を生成", "creates food");
			msg_print(_("食べられる野草などを集めた。", "You gather edible plants in your vicinity."));
			object_prep(q_ptr, lookup_kind(TV_FOOD, SV_FOOD_RATION));
			q_ptr->discount = 99;
			drop_near(q_ptr, -1, py, px);
			break;
		}

	//高級品感知
	case 6:
		{
			if(only_info) return format("");
			msg_print(_("あなたは周囲の調査を始めた・・", "You start surveying your surroundings..."));
			search_specific_object(6);
			break;
		}




	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



