/*:::雷鼓特技 p_ptr->magic_num1[0],[1],p_ptr->magic_num2[0]が歌に使われる*/
//p_ptr->magic_num1[10〜29]が特殊魔法に使われる？
cptr do_cmd_class_power_aux_raiko(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//歌を止める　行動順消費しない
	case 0:
		{
			if(only_info) return "";
			stop_raiko_music();
			return NULL;

		}
		break;
	case 1:
		{
			int dist = 2 + plev / 6;
			int rad = 2 + plev / 20;
			int dice = 4 + plev / 4;
			int sides = 7 + chr_adj / 2;

			if(only_info) return  format(_("射程:%d 損傷:%dd%d", "rng: %d dam: %dd%d"),dist,dice,sides);
			project_length = dist;
			stop_tsukumo_music();
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("太鼓を投げつけた！", "You throw a taiko drum!"));
			fire_ball(GF_SOUND, dir, damroll(dice,sides),rad);

		}
		break;
	case 2://アヤノツヅミ
		{
			if(only_info) return format(_("感知継続", "cont detect"));
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_RAIKO_AYANOTUDUMI, SPELL_CAST);

			break;
		}
		break;
	case 3:
		{
			int base = 10+plev/5;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_format(_("大地を激しく踏み鳴らした！", "You fiercely strike the ground!"));
			earthquake(py,px,5);
			set_afraid(0);
			set_hero(time,FALSE);

			break;
		}
		//v1.1.85 追加で雷雲生成する
	case 4:
		{
			int rad = 3 + plev / 12;
			int base = plev * 12 + chr_adj * 10;
			if(only_info) return format(_str_eff_dam_around,base / 2);
			stop_raiko_music();
			msg_format(_("雷雲が巻き起こり、雷鳴が轟いた！", "Thunderclouds starts swirling, and there is a roar of thunder!"));
			project(0, rad, py, px, base, GF_ELEC, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			project(0, rad/2, py, px, rad, GF_MAKE_STORM, (PROJECT_GRID | PROJECT_HIDE), -1);
			break;
		}
	case 5:
		{
			if(only_info) return "";

			if(!cast_monspell_new()) return NULL;
			break;
		}
	case 6:
		{
			int rad = 4 + plev / 16;
			int base = plev * 8 + adj_general[p_ptr->stat_ind[A_STR]] * 8;
			if(only_info) return format(_str_eff_dam_around,base / 2);
			stop_raiko_music();
			msg_format(_("あなたは壁に向かって思い切り体当りした！", "You slam your body into the wall!"));
			project(0, rad, py, px, base, GF_SOUND, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			break;
		}
	case 7://怒りのデンデン太鼓
		{
			int base = plev * 3;
			int sides = chr_adj * 5;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_RAIKO_DENDEN, SPELL_CAST);

			break;
		}
		break;
	case 8:
		{
			int dam = p_ptr->chp / 3;
			if(dam<1) dam = 1;
			if (dam > 800) dam = 800;

			if(only_info) return format(_str_eff_dam,dam);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("太鼓が火を噴いて飛んだ！", "A taiko drum flies forth, propelled by flames!"));
			fire_rocket(GF_ROCKET, dir, dam,2);
			break;
		}
	case 9:
		{
			int dam = plev * 5 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,dam);
			stop_raiko_music();
			msg_print(_("重低音がダンジョンを揺らした！", "Deep loud sounds shake the dungeon!"));
			project_hack2(GF_SOUND,0,0,dam);
			break;
		}
		break;
	case 10://プリスティンビート
		{
			if(only_info) return format("");
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_RAIKO_PRISTINE, SPELL_CAST);

			break;
		}
		break;

	default:
		if(only_info) return  format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


