cptr do_cmd_class_power_aux_sangetsu(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int dice = 1 + plev / 4;
			int sides = 7;
			int base = plev/2 + chr_adj/2 ;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("熱線を放った。", "You fire a heat ray."));
			fire_beam(GF_FIRE, dir, base + damroll(dice,sides));
			break;
		}
	case 1:
		{
			int x, y;
			int dist = 2 + plev / 5;
			monster_type *m_ptr;
			int power = 20 + plev;
			if(is_daytime()) power += 30;

			if(only_info) return format(_str_eff_range,dist);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("サニーは%sの周囲の景色を歪めた・・", "Sunny distorts the scenery around %s..."),m_name);

				if(r_ptr->flags2 & RF2_SMART || r_ptr->flags3 & RF3_NO_CONF ||
					r_ptr->flagsr & RFR_RES_ALL || randint1(power) < r_ptr->level)
				{
					msg_format(_("しかし効果がなかった。", "There was no effect."));
				}
				else
				{
					msg_format(_("%sは混乱した！", "%s is confused!"),m_name);
					(void)set_monster_confused(cave[y][x].m_idx,  MON_CONFUSED(m_ptr)+10 + randint1(plev/3));
				}
				set_monster_csleep(cave[y][x].m_idx, 0);
				anger_monster(m_ptr);
			}

			break;
		}

	case 2://静寂1 tim_general[0]を使う
		{
			int time = 10+plev/5;
			int rad = plev / 5;
			if(only_info) return format(_("期間:%d 範囲:%d", "dur: %d rad: %d"),time,rad);
			if(p_ptr->tim_general[0] || p_ptr->tim_general[1])
			{
				msg_print(_("すでに能力を使っている。", "This ability already is in effect."));
				return NULL;
			}

			msg_print(_("ルナは指を鳴らした。周囲の音が消えた。", "Luna clicks her fingers. It suddenly becomes silent."));
			set_tim_general(time,FALSE,0,0);

		}
		break;
	case 3://静寂解除
		{
			if(only_info) return format("");
			if(!p_ptr->tim_general[0] && !p_ptr->tim_general[1])
			{
				msg_print(_("能力を使っていない。", "You haven't used the ability."));
				return NULL;
			}

			msg_print(_("ルナは能力を解除した。", "Luna cancels her ability."));
			set_tim_general(0,TRUE,0,0);
			set_tim_general(0,TRUE,1,0);

		}
		break;
	case 4:
		{
			//v1.1.34 サニーの「光を屈折させて離れた場所の光景を見る」能力を追加。
			//まず特殊フラグを立ててからflying_dist値を閉じたドアやカーテンに遮られるようにして再計算する。
			//次にこのflying_dist値が設定されたグリッドのみを啓蒙、モンスター感知する。
			//最後にフラグを戻してflying_dist値を再計算する。
			if (only_info) return format(_("範囲:%d移動グリッド", "%d spaces"), MONSTER_FLOW_DEPTH);

			if (p_ptr->blind)
			{
				msg_print(_("サニーは目が見えない。", "Sunny can't see."));
				return NULL;
			}

			msg_print(_("離れた場所の映像がサニーの目の前に広がった……",
                        "Images of distant areas unfold before Sunny..."));
			flag_sunny_refraction = TRUE;
			update_flying_dist();
			wiz_lite(FALSE);
			detect_monsters_normal(255);
			flag_sunny_refraction = FALSE;
			update_flying_dist();

		}
		break;

	case 5:
		{
			int rad = 2 + plev / 24;
			int damage = plev  + chr_adj / 2 ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("三人で弾幕を降らせた！", "The three of you make it rain danmaku!"));
			cast_meteor(damage, rad, GF_METEOR);
			break;
		}
	case 6:
		{
			int time;
			int base = 15 + p_ptr->lev/5;

			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_format(_("サニーは身構えた。", "Sunny prepares to deflect attacks."));
			set_tim_reflect(time, FALSE);

			break;
		}

	case 7:
		{
			int dam = 100+plev*4;
			if(only_info) return format(_str_eff_dam,dam);
			if(!futo_can_place_plate(py,px) || cave[py][px].special)
			{
				msg_print(_("ここには爆弾を置けない。", "You can't set up a bomb here."));
				return NULL;
			}
			msg_print(_("スターは爆弾を仕掛けた。", "Star sets up a bomb."));
			/*:::Mega Hack - cave_type.specialを爆弾のカウントに使用する。*/
			cave[py][px].info |= CAVE_OBJECT;
			cave[py][px].mimic = feat_bomb;
			cave[py][px].special = 10;
			while(one_in_(5)) cave[py][px].special += randint1(20);
			note_spot(py, px);
			lite_spot(py, px);
		}
		break;
	case 8:
		{
			int base = 5+plev/5;
			if(is_daytime()) base *= 2;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("サニーは光を操って三人の姿を消した。", "Sunny manipulates light, concealing the three of you."));
			set_tim_superstealth(base + randint1(base),FALSE, SUPERSTEALTH_TYPE_OPTICAL);
			break;
		}
	case 9:
		{
			int dice = plev/2 ;
			int sides = 10 + chr_adj / 3;
			int rad = 5;

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_LITE,dir,damroll(dice,sides),rad,_("三人で巨大な光球を作り出した！", "The three of you create a huge sphere of light!"))) return NULL;

			break;
		}
	case 10:
		{
			int base = 10+plev/5;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_format(_("スターはサニーとルナを激励した！", "Star encourages Sunny and Luna!"));
			set_hero(time,FALSE);
			set_tim_addstat(A_STR,4,time,FALSE);
			set_tim_addstat(A_CON,4,time,FALSE);
			set_tim_addstat(A_DEX,4,time,FALSE);

			break;
		}
	case 11:
		{

			int m_idx;

			if(only_info) return format("");
			if (!target_set(TARGET_KILL)) return NULL;
			m_idx = cave[target_row][target_col].m_idx;
			if (!m_idx) return NULL;
			if (!player_has_los_bold(target_row, target_col)) return NULL;
			if (!projectable(py, px, target_row, target_col)) return NULL;
			msg_print(_("ルナは指を鳴らした。", "Luna clicks her fingers."));
			dispel_monster_status(m_idx);

			break;
		}
	case 12:
		{
			int dam = 40 + plev*3 + chr_adj*2;

			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) dam += 100;

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("サニーたちは眩く光った！", "Sunny shines dazzlingly!"));
			project_hack2(GF_LITE,0,0,dam);
			lite_room(py,px);
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) sunny_charge_sunlight(-500);
			break;
		}

	case 13://静寂2 tim_general[1]を使う
		{
			int time = 30;
			if(only_info) return format(_str_eff_dur,time);
			if(p_ptr->tim_general[1])
			{
				msg_print(_("すでに能力を使っている。", "You already have used this ability."));
				return NULL;
			}

			msg_print(_("ルナはフロア全体の音に干渉した。", "Luna muffles all sounds on this level."));
			set_tim_general(time,FALSE,1,0);
			p_ptr->tim_general[0]=0;
		}
		break;
	case 14:
		{
			int dam = plev * 4 + chr_adj * 10;
			int rad = 6;
			if(only_info) return format(_("損傷:最大%d*3", "dam: up to %d*3"),dam/2);
			msg_print(_("三人で全力の一撃を放った！", "The three of you combine your powers and strike!"));
			project(0, rad, py, px, dam, GF_LITE, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			if(p_ptr->chp>=0) project(0, rad, py, px, dam, GF_DARK, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			if(p_ptr->chp>=0) project(0, rad, py, px, dam, GF_METEOR, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
		}
		break;


	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





