/*:::八橋特技 p_ptr->magic_num1[0],[1],p_ptr->magic_num2[0]が歌に使われる*/
cptr do_cmd_class_power_aux_yatsuhashi(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//歌を止める　行動順消費しない
	case 0:
		{
			if(only_info) return "";
			stop_tsukumo_music();
			return NULL;

		}
		break;
	case 1:
		{
			int power = 50 + plev * 3;
			if(only_info) return format(_str_eff_power,power);

			stop_tsukumo_music();

			if(plev < 30)
			{
				if (!get_aim_dir(&dir)) return NULL;
				msg_print(_("か細い琴の音が響いた。", "A faint koto melody resounds."));
				slow_monster(dir,power);
				confuse_monster(dir,power);
			}
			else
			{
				msg_print(_("物悲しい琴の音が響き渡った・・", "Sad sounds of koto resound..."));
				slow_monsters(power);
				confuse_monsters(power);
			}
			break;
		}
	case 2://
		{
			if(only_info) return format("");
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_TSUKUMO_ECHO2, SPELL_CAST);
			p_ptr->update |= PU_BONUS;
			p_ptr->redraw |= (PR_STATUS);
			break;
		}
	case 3://天の詔琴
		{
			int dice = 8 + plev / 4;
			int sides = 10 + chr_adj / 3;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_TSUKUMO_NORIGOTO, SPELL_CAST);
			break;
		}
	case 4://弦楽「嵐のアンサンブル」
		{
			int base = plev * 2 + chr_adj * 2;
			if(only_info) return format(_("損傷:1d%d継続", "dam: 1d%d cont"),base);
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_TSUKUMO_STORM, SPELL_CAST);

			break;
		}
		break;
	case 5://浄瑠璃世界 concentを使う
		{
			if(only_info) return format("");
			stop_tsukumo_music();
			do_cmd_concentrate(0);
			if(p_ptr->concent == 12)
			{
				msg_print(_("あなたたちは七色の光に包まれた！", "You are enveloped in rainbow light!"));
				hp_player(plev * 6);
				player_gain_mana(plev * 2);
				set_stun(0);
				set_cut(0);
				set_poisoned(0);
				set_image(0);
				restore_level();
				set_alcohol(0);
				set_asthma(0);

				reset_concentration(FALSE);
			}
			break;

		}
		break;

	case 6:
	{
		int x, y;
		int power = plev * 3 + chr_adj * 5;
		monster_type *m_ptr;
		monster_race *r_ptr;
		char m_name[80];

		if (only_info) return format(_str_eff_power, power);

		if (!get_aim_dir(&dir)) return NULL;
		if (dir != 5 || !target_okay() || !projectable(target_row, target_col, py, px))
		{
			msg_print(_("ターゲットモンスターを明示的に指定しないといけない。",
                        "You have to explicitly target a monster."));
			return NULL;
		}
		y = target_row;
		x = target_col;

		m_ptr = &m_list[cave[y][x].m_idx];

		if (!m_ptr->r_idx || !m_ptr->ml)
		{
			msg_print(_("そこには何もいない。", "There's nobody here."));
			return NULL;
		}
		r_ptr = &r_info[m_ptr->r_idx];

		stop_tsukumo_music();

		monster_desc(m_name, m_ptr, 0);
		if (r_ptr->level < plev * 3 / 2)
		{
			msg_format(_("%sが相手では今ひとつ演奏に身が入らなかった。",
                        "You couldn't find the enthusiasm to play your performance against %s."), m_name);
			break;
		}

		msg_format(_("%sへ向けた反抗の葬送曲を奏でた！",
                    "You perform a defiant funeral march directed at %s!"), m_name);
		project(0, 0, y, x, power, GF_DEC_ALL, (PROJECT_KILL | PROJECT_JUMP), -1);
	}
	break;

	case 7://人琴ともに亡ぶ
		{
			if(only_info) return format("");
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_TSUKUMO_JINKIN, SPELL_CAST);
			break;
		}
	case 8://エコーチェンバー
		{
			if(only_info) return format("");
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_TSUKUMO_ECHO, SPELL_CAST);
			break;
		}

	default:
		if(only_info) return  format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}

