/*:::雛特技 p_ptr->magic_num1[0]が厄値として使われる*/
//magic_num1[1]を雛人形無人販売所の稼ぎとして使う
//magic_num1[2]を流刑人形のIDXとして使う
cptr do_cmd_class_power_aux_hina(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int yaku = p_ptr->magic_num1[0];
	int yaku_max = HINA_YAKU_MAX;

	switch(num)
	{
	case 0:
		{
			int i,x,y;
			if(only_info) return format(_("厄:%d", "msft: %d"),yaku);
			screen_save();

			for(i=1;i<20;i++) Term_erase(16, i, 255);
			y = 1;
			x = 18;

			if(yaku > HINA_YAKU_LIMIT3)
			{
				c_put_str(TERM_RED,format(_("現在の厄:%d/%d", "Current misfortune: %d/%d"),yaku,yaku_max), y++, x);
				c_put_str(TERM_RED,format(_("これ以上の厄を扱いきれない！このままでは大変なことになる！",
                                            "You hardly can control it anymore! Something terrible might happen!")), y++, x);
			}
			else if(yaku > HINA_YAKU_LIMIT2)
			{
				c_put_str(TERM_ORANGE,format(_("現在の厄:%d/%d", "Current misfortune: %d/%d"),yaku,yaku_max), y++, x);
				c_put_str(TERM_ORANGE,format(_("厄が漏れ出し、周囲に溢れ出ている…",
                                                "Your misfortune is leaking, starting to overflow around you...")), y++, x);
			}
			else if(yaku > HINA_YAKU_LIMIT1)
			{
				c_put_str(TERM_YELLOW,format(_("現在の厄:%d/%d", "Current misfortune: %d/%d"),yaku,yaku_max), y++, x);
				c_put_str(TERM_YELLOW,format(_("あなたは厄を少し持て余し気味だ。",
                                                "You have some trouble keeping it in check.")), y++, x);
			}
			else
				c_put_str(TERM_WHITE,format(_("現在の厄:%d/%d", "Current misfortune: %d/%d"),yaku,yaku_max), y++, x);
			y++;

			if(yaku > HINA_YAKU_LIMIT3)
			{
				c_put_str(TERM_ORANGE,format(_("厄の負担が体を蝕んでいる。",
                                                "The misfortune is eroding your body.")), y++, x);
				c_put_str(TERM_WHITE,format(_("周囲のものに無差別に災いが降りかかっている。",
                                                "Calamities are happening all around you.")), y++, x);
				c_put_str(TERM_ORANGE,format(_("今のあなたの状態は人里で「厄神異変」と呼ばれているらしい。",
                                                "People refer to your current state as 'Misfortune God Incident'.")), y++, x);
			}

			if(yaku > HINA_YAKU_LIMIT2+5000)
			{
				c_put_str(TERM_ORANGE,format(_("あなたは建物に入れてもらえない。",
                                                "You're no longer allowed to enter buildings.")), y++, x);
			}

			if(yaku > HINA_YAKU_LIMIT2)
				c_put_str(TERM_YELLOW,format(_("あなたは反感を受けている。", "You are aggravating other beings.")), y++, x);
			else if(yaku > HINA_YAKU_LIMIT1 && yaku >= 1500)
				c_put_str(TERM_WHITE,format(_("あなたは隠密能力が低下している。(-%d)",
                                                "Your stealth is lowered. (-%d)"),yaku/1500), y++, x);

			if(yaku > 9999)
			{
				c_put_str(TERM_L_GREEN,format(_("あなたは暗黒・地獄攻撃を完全に無効化する。",
                                                "You are completely immune to darkness and nether")), y++, x);
			}
			if(yaku > 14999)
			{
				c_put_str(TERM_L_GREEN,format(_("あなたは異界の悍ましい狂気への耐性をもつ。",
                                                "You resist insanity caused by otherworldly horrors.")), y++, x);
			}
			if(yaku > 19999)
			{
				c_put_str(TERM_L_GREEN,format(_("あなたは敵から受けたボルト魔法を跳ね返す。",
                                                "You reflect bolt spells from your enemies.")), y++, x);
			}

			if(p_ptr->do_martialarts)
			{
				if(yaku > 443) c_put_str(TERM_WHITE,format(_("あなたは隣接攻撃に%dのダイスボーナスと(+%d,+%d)の修正を得る。",
                                                            "You get +%d dice bonus and (+%d, +%d) for melee attacks."),yaku/999,yaku/444,yaku/666), y++, x);
			}
			else
			{
				if(yaku > 1200) c_put_str(TERM_WHITE,format(_("あなたは隣接攻撃に%dのダイスボーナスと(+%d,+%d)の修正を得る。",
                                                            "You get +%d dice bonus and (+%d, +%d) for melee attacks."),yaku/4800,yaku/1200,yaku/1800), y++, x);
			}

			if(yaku > 200) c_put_str(TERM_WHITE,format(_("あなたはACに%dの修正を得る。",
                                                        "You get +%d to AC."),yaku/200), y++, x);

			if(yaku > 99) c_put_str(TERM_WHITE,format(_("あなたに触れた者は1d%dのダメージを受ける。",
                                                        "Those who touch you receive 1d%d damage."),yaku/100), y++, x);
			if(yaku > 10000) c_put_str(TERM_L_GREEN,format(_("あなたに触れた者は混乱することがある。",
                                                            "Those who touch you might get confused.")), y++, x);
			if(yaku > 20000) c_put_str(TERM_L_GREEN,format(_("あなたに触れた者は朦朧とすることがある。",
                                                            "Those who touch you might get stunned.")), y++, x);

			if(yaku > 27000) c_put_str(TERM_L_BLUE,format(_("あなたは秩序の勢力の天敵だ。",
                                                            "You are a great bane of lawfully aligned beings.")), y++, x);
			else if(yaku > 18000) c_put_str(TERM_L_GREEN,format(_("あなたは秩序の勢力に対して特に恐るべき力を発揮する。",
                                                                    "You are especially deadly against lawfully aligned beings.")), y++, x);
			if(yaku > 18000) c_put_str(TERM_L_BLUE,format(_("あなたは人間の天敵だ。", "You are a great bane of humans.")), y++, x);
			else if(yaku > 12000) c_put_str(TERM_L_GREEN,format(_("あなたは人間に対して特に恐るべき力を発揮する。",
                                                                    "You are especially deadly against humans.")), y++, x);

			inkey();
			screen_load();

			return NULL; //行動順を消費しない
		}
		break;

	case 1:
		{
			int food;
			if(only_info) return format("");

			if(use_itemcard)
			{
				if(remove_all_curse())
				{
					msg_print(_("カードが厄を吸い取った。", "The card absorbs misfortune."));
					set_food(PY_FOOD_MAX - 1);
				}
			}
			else
			{
				if(p_ptr->food >= PY_FOOD_FULL)
				{
					msg_print(_("活力は十分だ。", "You are already sustained."));
					return NULL;
				}
				if(!yaku)
				{
					msg_print(_("活力にする厄が全くない…", "You don't have enough misfortune to sustain yourself..."));
					return NULL;
				}

				food = PY_FOOD_MAX - 1 - p_ptr->food;
				if(food > yaku * 30) food = yaku * 30;

				set_food(p_ptr->food + food);

				hina_gain_yaku(-(food/30));
			}
		}
		break;


	case 2:
		{
			char o_name[MAX_NLEN];
			int i;
			int gain = 0;
			if(only_info) return format("");

			for (i = INVEN_RARM; i < INVEN_TOTAL; i++)
			{
				object_type *o_ptr = &inventory[i];
				if (!o_ptr->k_idx) continue;
				if (!object_is_cursed(o_ptr)) continue;
				object_desc(o_name, o_ptr, OD_OMIT_INSCRIPTION);

				if (o_ptr->curse_flags & TRC_PERMA_CURSE)
				{
					msg_format(_("%sの呪いはあまりにも強い！呪いを吸いきれなかった！",
                                "The curse on %s is too strong! You couldn't fully absorb it!"),o_name);
					o_ptr->curse_flags &= (TRC_CURSED | TRC_HEAVY_CURSE | TRC_PERMA_CURSE);
					gain += 1000 + randint1(1000);
				}
				else if (o_ptr->curse_flags & TRC_HEAVY_CURSE)
				{
					msg_format(_("%sの重い呪いを吸い取った！", "You absorb the heavy curse on %s!"),o_name);
					gain += 500 + randint1(500);
					o_ptr->curse_flags = 0L;
				}
				else
				{
					msg_format(_("%sの呪いを吸い取った。", "You absorb the curse on %s."),o_name);
					gain += 200 + randint1(200);
					o_ptr->curse_flags = 0L;
				}
				//未鑑定の場合簡易鑑定
				if(!object_is_known(o_ptr))
				{
					o_ptr->ident |= (IDENT_SENSE);
					o_ptr->feeling = value_check_aux1(o_ptr);
				}
				p_ptr->update |= PU_BONUS;
				p_ptr->window |= (PW_INVEN | PW_EQUIP);


			}
			if(gain)
				hina_gain_yaku(gain);
			else
				msg_print(_("厄を吸収できるものを装備していない。", "You don't have anything equipped you could absorb misfortune from."));

		}
		break;

	case 3:
		{
			int dice = 12;
			int sides = 10 + plev / 5;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("厄を放った。", "You release misfortune."));
			fire_ball_hide(GF_YAKU, dir, damroll(dice,sides),0);
			break;
		}
	//厄感知
	case 4:
		{
			int rad = 20 + plev / 5;
			if(only_info) return format(_("範囲:%d+α", "rad: %d+@"),rad);
			msg_print(_("あなたは厄を探った・・", "You look for misfortune..."));
			search_specific_object(5);

			detect_traps(rad,TRUE);
			if(plev>29) detect_monsters_evil(rad);

			break;
		}

	case 5:
		{
			cptr q,s;
			int item;
			object_type *o_ptr;
			int dam;

			if(only_info) return format("");
			item_tester_hook = item_tester_hook_broken_amulet;

			q = _("どのアミュレットを投げますか? ", "Throw which amulet?");
			s = _("壊れたアミュレットがない。", "You don't have broken amulets.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;

			if (item >= 0) o_ptr = &inventory[item];
			else o_ptr = &o_list[0 - item];

			dam = count_bits(o_ptr->curse_flags) * 100;
			if(o_ptr->curse_flags & TRC_PERMA_CURSE) dam += 800;
			else if(o_ptr->curse_flags & TRC_HEAVY_CURSE) dam += 200;
			if(object_is_artifact(o_ptr)) dam += 800;
			else if(object_is_ego(o_ptr)) dam += 200;
			else dam += 100;

			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("投げつけたアミュレットが弾けた！", "The thrown amulet shatters!"));
			fire_ball(GF_SHARDS, dir, dam,3);

			if (item >= 0)
			{
				inven_item_increase(item, -1);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -1);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}

		}
		break;

	case 6:
		{
			int dam = 200 + plev * 6;
			int rad = 4 + plev / 15;
			if(only_info) return format(_str_eff_dam_around,dam/2);

			msg_print(_("厄があなたの周りを渦巻いた！", "Misfortune swirls around you!"));
			project(0,rad,py,px,dam,GF_YAKU,PROJECT_KILL,-1);
			break;
		}
	case 7:
		{
			int base = 10+plev/5;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);

			time = base + randint1(base);
			set_tim_eyeeye(time,FALSE);
			break;
		}

	case 8:
		{
			int y, x, base;
			monster_type *m_ptr;

			base = 10 + p_ptr->lev / 5;
			if(only_info) return format(_str_eff_dur,base);

			if(yaku < 300)
			{
				msg_print(_("この特技を使う厄が足りない。", "You don't have enough misfortune to use this special ability."));
				return NULL;
			}
			if (!get_aim_dir(&dir)) return NULL;

			x = target_col;
			y = target_row;

			if(dir != 5 || !target_okay() || !projectable(y,x,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("厄が渦巻いて%sを捕えた！", "%s is caught in a whirlwind of misfortune!"),m_name);

				project(0, 0, m_ptr->fy, m_ptr->fx, base, GF_NO_MOVE, PROJECT_KILL | PROJECT_JUMP | PROJECT_HIDE, -1);
				hina_gain_yaku(-300);

				//p_ptr->magic_num1[2] = cave[y][x].m_idx;
				//set_tim_general(base,FALSE,0,0);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;
		}

	case 9:
		{

			if(only_info) return format("");

			if (!(p_ptr->special_defense & SD_HINA_NINGYOU))
			{
				msg_print(_("あなたは厄を撒き散らし始めた…", "You start spreading misfortune..."));

				p_ptr->special_defense |= SD_HINA_NINGYOU;
				p_ptr->redraw |= (PR_STATUS);
			}
			else
			{
				msg_print(_("あなたは厄を撒き散らすのを止めた。", "You stop spreading misfortune."));

				p_ptr->special_defense &= ~(SD_HINA_NINGYOU);
				p_ptr->redraw |= (PR_STATUS);
			}

			break;
		}



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


