cptr do_cmd_class_power_aux_doremy(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;
	bool in_dream = (IN_DREAM_WORLD);

	switch(num)
	{

	case 0:
		{
			int x, y;
			char m_name[160];
			monster_type *m_ptr;
			int food = 5000;
			int power = 50 + plev;
			if(only_info) return format(_str_eff_power,power);

			if(p_ptr->food >= PY_FOOD_MAX) food = 0;
			else if(p_ptr->food + food >= PY_FOOD_MAX) food = PY_FOOD_MAX - 1 - p_ptr->food;

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (!cave[y][x].m_idx || !(m_ptr->ml) )
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}

			monster_desc(m_name,m_ptr,0);
			if(in_dream)
			{
				if(genocide_aux(cave[y][x].m_idx,power,TRUE,0,""))
				{
					msg_format(_("%sの夢を美味しく頂いた。", "The dream of %s was delicious."),m_name);
					if(food) set_food(p_ptr->food + food);
				}
			}
			else
			{
				monster_race *r_ptr = &r_info[m_ptr->r_idx];

				if (!MON_CSLEEP(m_ptr))
				{
					msg_format(_("%sは起きている。", "%s is awake."),m_name);
					return NULL;
				}
				else if(r_ptr->flags2 & (RF2_EMPTY_MIND))
				{
					msg_format(_("%sの夢はあなたの賞味できるようなものではなかった。",
                                "Doesn't look like you can enjoy the dreams of %s."),m_name);
					break;
				}
				else if(m_ptr->mflag & MFLAG_SPECIAL)
				{
					msg_format(_("%sの夢はもう賞味済みだ。", "You're no longer interested in dreams of %s."),m_name);
					break;
				}
				else if(r_ptr->flags2 & (RF2_WEIRD_MIND))
				{
					msg_format(_("%sの夢はほとんど味がしない・・", "The dream of %s is almost tasteless..."),m_name);
					food /= 10;
				}
				else if(r_ptr->flags3 & (RF3_HUMAN | RF3_DEMIHUMAN) || is_gen_unique(m_ptr->r_idx))
				{
					msg_format(_("%sの夢を美味しく頂いた。", "The dream of %s was delicious."),m_name);
				}
				else
				{
					msg_format(_("%sの夢はちょっと薄味で美味しくない。", "The dream of %s wasn't that tasty."),m_name);
					food /= 3;
				}
				if(food) set_food(p_ptr->food + food);
				m_ptr->mflag |= MFLAG_SPECIAL; //特殊フラグを立てて二度目以降を無効に
				(void)set_monster_csleep(cave[y][x].m_idx, 0);

			}
		}
		break;

	case 1:
	case 3:
	case 10:
		{
			int x, y;
			int dice,sides;
			int power;
			int typ;
			monster_type *m_ptr;

			if(num == 1 && !in_dream)
			{
				power = 50 + plev * 4;
				if(only_info) return format(_str_eff_power,power);
			}
			else if(num == 1)
			{
				typ = GF_REDEYE;
				dice = 9 + plev / 3;
				sides = 8 + chr_adj / 6;

			}
			else if(num == 3)
			{
				typ = GF_NIGHTMARE;
				dice = 6 + plev / 4;
				sides = 13 + chr_adj / 4;

				if(in_dream) dice *= 2;

			}
			else
			{
				typ = GF_COSMIC_HORROR;

				dice = plev;
				sides = 20 + chr_adj / 3;

				if(in_dream) dice = dice * 3 / 2;


			}

			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				if(r_ptr->flags2 & RF2_EMPTY_MIND)
				{
					msg_format(_("%sは精神を持たないようだ。", "%s doesn't have a mind."),m_name);
					break;
				}

				if(num == 1 && !in_dream)
				{
					msg_format(_("%sを夢の世界へ誘った・・", "You invite %s to the world of dreams..."),m_name);
					if(plev > 24) project(0,0,y,x,power,GF_STASIS,(PROJECT_KILL|PROJECT_JUMP),-1);
					else	project(0,0,y,x,power,GF_OLD_SLEEP,(PROJECT_KILL|PROJECT_JUMP),-1);
				}
				else
				{
					if(num == 1) msg_format(_("%sへ悪夢を見せた。", "You show %s a nightmare."),m_name);
					else if(num == 3) msg_format(_("%sへ愁夢を見せた。", "You show %s a dream of anxiety."),m_name);
					else msg_format(_("%sへ狂夢を見せた。", "You show %s a lunatic dream."),m_name);

					project(0,0,y,x,damroll(dice,sides),typ,(PROJECT_KILL|PROJECT_JUMP),-1);
				}
			}
		}
		break;

	case 2:
		{
			int rad = 25 + plev / 2;
			if (only_info) return format(_str_eff_area,rad);
			msg_print(_("辺りの様子を分析した・・", "You analyze your surroundings..."));
			map_area(rad);
			detect_all(rad);
			detect_monsters_normal(rad);
			probing();
			break;
		}

	case 4:
		{
			int x, y;
			int dist = 7;
			int dam = plev * 2 + 100;
			monster_type *m_ptr;
			char m_name[80];

			if(only_info) return format(_str_eff_range_dam,dist,dam);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("近くのターゲットを明示的に指定しないといけない。",
                            "You have to pick a nearby target."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			monster_desc(m_name, m_ptr, 0);

			if(!in_dream && !MON_CSLEEP(m_ptr))
			{
				msg_format(_("%sは眠っていない。", "%s isn't sleeping."),m_name);
				return NULL;
			}
			else
			{
				int r_idx = m_ptr->r_idx;
				monster_race *r_ptr = &r_info[m_ptr->r_idx];

				if(r_ptr->flags2 & RF2_EMPTY_MIND)
				{
					msg_format(_("%sは精神を持たないようだ。", "%s doesn't have a mind."),m_name);
					break;
				}

				msg_format(_("%sの夢を取り込もうとした・・", "You try to capture the dream of %s..."),m_name);
				project(0,0,y,x,dam,GF_MISSILE,(PROJECT_KILL|PROJECT_JUMP),-1);
				if(!m_ptr->r_idx)
				{
					metamorphose_to_monster(r_idx,-1);
				}
			}
		}
		break;

	case 5:
		{
			if(only_info) return format("");
			if(in_dream)
			{
				msg_print(_("あなたは自分の夢の修復を試みた・・", "You attempt to repair your dreams..."));
				player_gain_mana(plev/10+randint1(plev/10));
				hp_player(plev/3+randint1(plev/3));
			}
			else
			{
				msg_print(_("あなたは寝息を立て始めた・・", "You start to doze off a bit..."));
				player_gain_mana(1+randint1(plev/12));
				hp_player(plev/7+randint1(plev/7));
			}
		}
		break;



	case 6:
		{
			int power = 50 + plev * 2;
			if(in_dream) power += 90 + chr_adj * 2;
			if(only_info) return format(_str_eff_power,power);

			if(in_dream)
			{
				msg_print(_("周囲に混迷をもたらした。", "You spread confusion around yourself."));
				project_hack2(GF_CONFUSION,0,0,power);
			}
			else
			{
				msg_print(_("錯綜した幻覚を生み出した。", "You create a beguiling illusion."));
				stasis_monsters(power);
			}

		}
		break;

	case 7:
		{
			if(only_info) return format(_("距離:無制限", "dist: unlim"));
			if (!dimension_door(D_DOOR_DREAM)) return NULL;
		}
		break;

	case 8:
	{
		int dice = 5 + plev / 2;
		int sides = 10 + chr_adj / 5;

		if (in_dream) dice *=2;

		if (only_info) return format(_str_eff_dam_dice_sides, dice, sides);
		if (!get_aim_dir(&dir)) return NULL;

		msg_print(_("重苦しい悪夢を練り上げた...", "You create a heavy nightmare..."));
		fire_ball(GF_NIGHTMARE, dir, damroll(dice, sides), 3);

		break;
	}


	case 9:
		{
			if(only_info) return format(_("効果:現実変容", "eff: alter"));
			if (p_ptr->inside_arena)
			{
				msg_print(_("今その特技は使えない。", "You can't use this ability right now."));
				return NULL;
			}

			msg_print(_("周囲の景色がぼやけた・・", "Scenery around you starts to waver..."));
			/*:::0ではまずいはず*/
			p_ptr->alter_reality = 1;
			p_ptr->redraw |= (PR_STATUS);
		}
		break;

	case 11:
		{
			int dice = 8;
			int base = 8;
			if (only_info) return format(_str_eff_dur_plus_1d, base, dice);
			set_multishadow(base + randint1(dice), FALSE);
			break;
		}
	case 12:
		{
			int rad = 2;
			int base = p_ptr->lev * 15 + chr_adj * 25;
			if (only_info) return format(_str_eff_dam_around, base / 2);

			msg_format(_("あなたはこの場を暗黒の宇宙空間へと変えた！",
                        "You change this area to a black cosmic space!"));
			if (in_dream)
			{
				project_hack2(GF_DARK, 0, 0, base / 2);
			}
			else
			{
				project(0, rad, py, px, base, GF_DARK, PROJECT_KILL, -1);
			}
			break;
		}




	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



