cptr do_cmd_class_power_aux_clownpiece(int num, bool only_info)
{
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int dir;

	switch(num)
	{

	case 0:
		{
			int level = plev + 10;
			int num = 1 + plev / 16;
			bool flag = FALSE;
			if(only_info) return format(_("召喚レベル:%d", "sum lvl: %d"),level);
			for(;num>0;num--)
			{
				if(summon_specific(-1, py, px, plev, SUMMON_FAIRY, (PM_FORCE_PET | PM_ALLOW_GROUP))) flag = TRUE;
			}
			if(flag) msg_format(_("仲間を呼んだ！", "You call your friends!"));
			else msg_format(_("誰も現れなかった・・", "Nobody appears..."));

		}
		break;
	case 1:
		{
			int dice = 3 + plev / 4;
			int sides = 8;
			int base = 20 + plev * 2 + chr_adj * 2;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			fire_beam(GF_MANA, dir, base + damroll(dice,sides));
			break;
		}
	case 2:
		{
			int power = 50 + p_ptr->lev * 4;
			if(power < 100) power = 100;
			if (only_info) return format(_str_eff_power,power);
			msg_format(_("狂気の光が周囲を染め上げた！", "Maddening light spreads around you!"));
			project_hack2(GF_REDEYE,0,0,power);
			confuse_monsters(power);
			turn_monsters(power);
			if (plev > 29) stun_monsters(power);
			if (plev > 39) project_hack(GF_BERSERK, power);
			lite_room(py, px);
		}
		break;
	case 3:
		{
			int damage = 50 + p_ptr->lev * 6 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("数条の光線が前方を薙ぎ払った！", "Several rays of light sweep in front of you!"));
			fire_spark(GF_LITE,dir,damage,2);
		}
		break;
	case 4:
		{
			int num = 4 + (plev-3) / 11;
			int dam = 200 + plev * 2 + chr_adj * 2;
			int rad = 2 + plev / 12;
			int i;
			if(only_info) return format(_("損傷:最大%d * %d", "dam: up to %d*%d"),dam/2,num);
			msg_print(_("熱波が周囲を焼き払った！", "Heat waves scorch everything around you!"));
			for (i = 0; i < num; i++) project(0, rad, py, px, dam, GF_FIRE, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			break;
		}
		break;
	case 5:
		{
			int rad, dam;

			dam = p_ptr->magic_num1[0] - p_ptr->magic_num1[1];

			if (dam > 9999) dam = 9999;

			rad = 1 + dam / 1200;

			if (only_info) return format(_("損傷:最大%d", "dam: up to %d"),dam / 2);

			if (dam< 1000)
			{
				msg_print(_("この技はもっとダメージを受けないと使えない。", "You have to take more damage in order to use this ability."));
				return NULL;
			}

			msg_format(_("怨みのパワーを解き放った！", "You release the power of your grudges!"));
			project(0, rad, py, px, dam, GF_NETHER, PROJECT_JUMP | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM, -1);

			p_ptr->magic_num1[1] = p_ptr->magic_num1[0];

			break;
		}


	case 6:
		{
			int dam = plev * 10 + chr_adj * 10;
			if(dam < 400) dam = 400;

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("地獄の劫火が地面から噴き出した！", "Hellfire blasts out of the ground!"));
			project_hack2(GF_HELL_FIRE,0,0,dam);
			break;
		}
	case 7:
		{
			int x, y;
			int dist = 7;
			int num = 3;
			int rad = 5;
			monster_type *m_ptr;
			int dice = 10;
			int sides = plev * 2;

			if(only_info) return format(_("射程:%d 損傷:%dd%d*%d", "rng: %d dam: %dd%d*%d"),dist,dice,sides,num);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("そこを狙うことはできない。", "You cannot aim there."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sを目掛けて巨大隕石が落ちた！", "A huge meteor falls straight on top of %s!"),m_name);
				for(;num>0;num--)
				{
					project(0,rad,y,x,damroll(dice,sides),GF_METEOR,(PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM),-1);
					if(!m_ptr->r_idx) break;
				}
			}

			break;
		}




	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



