/*:::メルラン特技 p_ptr->magic_num1[0],[1],p_ptr->magic_num2[0]が歌に使われる*/
cptr do_cmd_class_power_aux_merlin(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

		//歌を止める　行動順消費しない
	case 0:
		{
			if(only_info) return "";
			stop_singing();
			return NULL;

		}
		break;
	case 1:
		{
			int rad = 2 + plev / 16;
			int dice = 3 + plev / 4;
			int sides = 9;
			int base = plev + chr_adj * 2;

			if(only_info) return format(_("半径:%d 損傷:%d+%dd%d", "rad: %d dam: %d+%dd%d"),rad, base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("トランペットから爆音が放たれた！", "Your trumpet makes a loud sound!"));
			fire_ball(GF_SOUND, dir, base + damroll(dice,sides),rad);
			break;
		}
	case 2:
		{
			int base = 10+plev/5;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("あなたはトランペットを高らかに吹き鳴らした！", "You raise your trumpet and blow!"));
			time = base + randint1(base);

			if(one_in_(4))
				set_shero(time,FALSE);
			else
				set_hero(time,FALSE);
			set_shield(time,FALSE);
			break;
		}
	case 3:
		{
			int base = 10+plev/5;
			int time;
			if (only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("陽気な音楽がダンジョンに響き渡った！", "Cheerful music resounds through the dungeon!"));
			time = base + randint1(base);

			set_tim_addstat(A_STR,1+plev/12,time,FALSE);
			set_tim_addstat(A_CON,1+plev/12,time,FALSE);
			set_tim_addstat(A_DEX,1+plev/12,time,FALSE);
			set_fast(time,FALSE);

			aggravate_monsters(0,TRUE);

			break;
		}

	case 4:
		{
			int dam = plev * 4 + chr_adj * 5;
			if(only_info) return format(_str_eff_dam,dam);
			msg_print(_("三姉妹が集まり、破壊的な騒音を起こした！", "You come together with your sisters and make destructive noise!"));
			project_hack2(GF_SOUND,0,0,dam);
			aggravate_monsters(0,FALSE);
			new_class_power_change_energy_need = 150;
			msg_print(_("姉と妹は帰って行った。", "Your sisters leave."));
			break;
		}
	case 5:
		{
			if(only_info) return  format("");
			do_spell(TV_BOOK_MUSIC, MUSIC_NEW_MERLIN_SOLO, SPELL_CAST);

			break;
		}
	case 6:
	{
		int dam = plev * 3 + chr_adj * 3;
		if (only_info) return format(_str_eff_dam, dam);
		msg_print(_("三姉妹が集まり、幻惑的な光を放った！", "You come together with your sisters and release bedazzling light!"));
		project_hack2(GF_RAINBOW, 0, 0, dam);
		new_class_power_change_energy_need = 150;
		msg_print(_("姉と妹は帰って行った。", "Your sisters leave."));

		break;
	}


	case 7:
		{
			int x, y;
			int dist = 5;
			int rad = 6;
			int dice = 15+chr_adj/2;
			int sides = plev;
			monster_type *m_ptr;

			if(only_info) return format(_("損傷:%dd%d 射程:%d", "dam: %dd%d rng: %d"),dice,sides,dist);

			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("三姉妹が集まり、%sのための葬送曲を奏でた！", "You come together with your sisters to play a dirge for %s!"),m_name);
				project(0,rad,y,x,damroll(dice,sides),GF_DISP_ALL,(PROJECT_JUMP | PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL),-1);
				new_class_power_change_energy_need = 150;
				msg_print(_("姉と妹は帰って行った。", "Your sisters leave."));
			}
			break;
		}

	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



