cptr do_cmd_class_power_aux_alice(int num, bool only_info)
{
	int dir,dice,sides,base,damage,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			if(only_info) return format("");
			if(!put_item_into_inven2()) return NULL;

			p_ptr->update |= PU_BONUS;
			update_stuff();
			break;

		}
	case 1:
		{
			if(only_info) return format("");
			if(!takeout_inven2()) return NULL;
			p_ptr->update |= PU_BONUS;
			update_stuff();
			break;
		}
	case 2:
		{
			dice = 2 + plev / 3;
			sides = 8;
			base = plev + chr_adj * 2;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("上海人形がビームを放った！", "Shanghai doll fires a beam!"));
			fire_beam(GF_LITE, dir, base + damroll(dice,sides));
			break;
		}
	case 3:
		{
			bool flag = FALSE;
			int max = 1 + p_ptr->lev / 10;
			if(only_info) return format(_("最大:%d体", "up to %d"),max);

			for(i=0;i<max;i++) if(summon_named_creature(0, py, px, MON_ALICE_DOLL, PM_FORCE_PET)) flag = TRUE;
			if(flag) msg_print(_("人形を放った。", "You release dolls."));
			else msg_print(_("人形の作成に失敗した。", "You failed to create dolls."));

			break;
		}
	case 4:
	{
		int power = plev / 2;
		int range = 3 + plev / 12;
		int x, y;
		monster_type *m_ptr;

		if (only_info) return format(_("射程:%d 効果:%d", "rng: %d pow: %d"), range,power);

		project_length = range;
		if (!get_aim_dir(&dir)) return NULL;
		if (dir != 5 || !target_okay() || !projectable(target_row, target_col, py, px))
		{
			msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                        "You have to explicitly pick a target in sight."));
			return NULL;
		}
		y = target_row;
		x = target_col;

		m_ptr = &m_list[cave[y][x].m_idx];

		if (!m_ptr->r_idx || !m_ptr->ml)
		{
			msg_print(_("そこには何もいない。", "There's nobody here."));
			return NULL;
		}
		else
		{
			char m_name[80];
			monster_desc(m_name, m_ptr, 0);
			msg_format(_("%sの周りに魔力で強化した糸を張り巡らせた。",
                        "Wires imbued with mana stretch around %s."), m_name);
			project(0, 0, m_ptr->fy, m_ptr->fx, power, GF_NO_MOVE, PROJECT_JUMP | PROJECT_KILL, -1);
		}
	}
	break;


	case 5:
	case 9:
		{
			int     item;
			cptr    q, s;
			object_type *o_ptr;
			monster_race *r_ptr;
			int typ;
			int rad;

			if(only_info) return format("");

			item_tester_tval = TV_FIGURINE;


			q = _("どの人形を投げますか? ", "Throw which figurine?");
			s = _("人形を持っていない。", "You don't have figurines.");
			if (!get_item(&item, q, s, (USE_INVEN | USE_FLOOR))) return NULL;

			if (item >= 0)	o_ptr = &inventory[item];
			else o_ptr = &o_list[0 - item];

			r_ptr = &r_info[o_ptr->pval];

			if(r_ptr->flags1 & RF1_FORCE_MAXHP)
				damage = r_ptr->hdice * r_ptr->hside;
			else
				damage = damroll(r_ptr->hdice,r_ptr->hside);
			if(num == 4)
			{
				damage /= 2;
				if(damage > 1600) damage = 1600;
				if(!damage) damage = 1;
				typ = GF_FIRE;
				rad = 3;
			}
			else
			{
				damage /= 3;
				if(damage > 3200) damage = 3200;
				if(!damage) damage = 1;
				typ = GF_MANA;
				rad = 6;

			}

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("人形を投げつけた！", "You throw the figurine!"));
			fire_ball(typ, dir, damage,rad);
			if (item >= 0)
			{
				inven_item_increase(item, -1);
				inven_item_describe(item);
				inven_item_optimize(item);
			}
			else
			{
				floor_item_increase(0 - item, -1);
				floor_item_describe(0 - item);
				floor_item_optimize(0 - item);
			}

			break;
		}
	case 6:
		{
			monster_type *m_ptr;
			int xx,yy;
			int damage = 30 + plev * 2 + chr_adj;
			bool flag = FALSE;
			if(only_info) return format(_("損傷:一体につき%d", "dam: %d each"),damage);
			msg_format(_("あなたは人形たちに起爆指令を出した！", "You order your dolls to detonate!"));

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (m_ptr->r_idx != MON_ALICE_DOLL) continue;
				if (!is_pet(m_ptr)) continue;
				flag = TRUE;
				xx = m_ptr->fx;
				yy = m_ptr->fy;
				delete_monster_idx(i);
				project(0,3,yy,xx,damage,GF_FIRE,PROJECT_KILL | PROJECT_GRID | PROJECT_JUMP | PROJECT_ITEM,-1);

			}
			if(!flag) msg_print(_("しかし爆発させる人形がいなかった・・", "There were no dolls to blow up..."));

		}
		break;

	case 7:
		{
			int range = 2 + (plev-32) / 8;
			int x, y;
			monster_type *m_ptr;

			if(range < 2) range = 2; //paranoia

			if(only_info) return format(_str_eff_range,range);

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);
				msg_format(_("人形たちが%s目掛けて飛びかかった！", "Your dolls fly forth at %s!"),m_name);
				py_attack(m_ptr->fy,m_ptr->fx,0);

			}
		}
		break;



	case 8:
		{
			dice = plev / 2;
			sides = 10 + chr_adj / 10;
			base = plev * 3 + chr_adj * 3;

			if(dice < 15) dice = 15;
			if(base < 100) base = 100;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("蓬莱人形がビームを放った！", "Hourai doll fires a beam!"));
			fire_spark(GF_DARK, dir, base + damroll(dice,sides),1);
			break;
		}
	case 10:
		{
			int dam = plev + chr_adj;
			int x, y;
			monster_type *m_ptr, *m2_ptr;
			bool flag = FALSE;

			if(only_info) return format(_("損傷:%d * 不定", "dam: %d * undef"),dam);

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				msg_print(_("惨劇の舞台の幕が上がった！", "The curtain rises on the stage of tragedy!"));
				for (i = 1; (i < m_max && m_ptr->r_idx); i++)
				{
					m2_ptr = &m_list[i];
					if (m2_ptr->r_idx != MON_ALICE_DOLL) continue;
					if (!is_pet(m2_ptr)) continue;
					if(!projectable(m2_ptr->fy,m_ptr->fx,y,x)) continue;
					flag = TRUE;
					project(i,0,y,x,dam,GF_BANKI_BEAM2,(PROJECT_BEAM | PROJECT_KILL | PROJECT_THRU),-1);
					p_ptr->csp -= 3;
					if(p_ptr->csp <= 70) break;
				}
				if(!flag) msg_print(_("・・気がした。", "...Or that was your imagination."));
			}
		}
		break;
	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





/*:::サニー専用技*/
class_power_type class_power_sunny[] =
{
	{8,6,20,FALSE,TRUE,A_STR,0,2,_("アグレッシブライト", "Aggresive Light"),
		_("閃光属性のビームを放つ。夜間は威力が落ちる。",
        "Fires a beam of light. Less powerful during night.")},
	{15,5,25,FALSE,TRUE,A_INT,0,0,_("幻惑T", "Illusion I"),
		_("敵一体を混乱させる。賢い敵には効かない。夜間は効果が落ちる。",
        "Confuses an enemy. Does not affect smart enemies. Less powerful during night.")},
	{20,10,40,FALSE,TRUE,A_INT,0,0,_("ルチルフレクション", "Rutile Flection"),
		_("光を屈折させて離れた場所を視認する。ただし閉じたドアやカーテンで遮られた所を見ることはできない。",
        "Refracts light, allowing you to observe distant areas. Does not let you see through closed doors/curtains/etc.")},

	{24,20,35,FALSE,TRUE,A_CON,0,4,_("サンシャインブラスト", "Sunshine Blast"),
		_("今いる部屋を明るくし、視界内全てに閃光属性攻撃を行う。ただし日光の蓄積が不十分な場合は光に弱いモンスターにしかダメージを与えられない。夜間は威力が落ちる。",
        "Illuminates nearby area and hit everything in sight with light. Unless you've accumulated enough sunshine, deals damage only to monsters vulnerable to light. Less powerful during night.")},
	{32,30,50,FALSE,TRUE,A_INT,0,0,_("幻惑U", "Illusion II"),
		_("一定時間、周囲の敵を惑わせる。視界に頼らない敵に対しては効果が薄い。夜間は効果時間が短くなる。",
        "Temporarily makes nearby enemies unable to perceive you. Less effective against enemies that do not rely on vision. Duration is shorter during night.")},
	{40,45,70,FALSE,TRUE,A_STR,0,7,_("フェイタルフラッシュ", "Fatal Flash"),
		_("部屋を明るくし、隣接した敵一体に閃光属性ダメージを与え、朦朧、混乱させる。夜間は効果が落ちる。",
        "Illuminates nearby area, and deals light element damage to an adjacent enemy, stunning and confusing them. Less powerful during night.")},

	{99,0,0,FALSE,FALSE,0,0,0,"dummy",	""},
};

cptr do_cmd_class_power_aux_sunny(int num, bool only_info)
{
	int dir,dice,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			dice = 3 + p_ptr->lev / 5;
			sides = 5;
			base = p_ptr->lev / 2 + chr_adj / 2;

			if(is_daytime())
			{
				sides *= 2;
				base *= 2;
			}
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) base += p_ptr->lev;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("ビームを放った！", "You fire a beam!"));
			fire_beam(GF_LITE, dir, base + damroll(dice,sides));
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) sunny_charge_sunlight(-100);

			break;
		}
	case 1:
		{
			int x, y;
			int dist = 2 + plev / 5;
			monster_type *m_ptr;
			int power = 20 + plev;
			if(is_daytime()) power += 30;

			if(only_info) return format(_str_eff_range,dist);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("あなたは%sの周囲の景色を歪めた・・", "You distort the scenery around %s..."),m_name);

				if(r_ptr->flags2 & RF2_SMART || r_ptr->flags3 & RF3_NO_CONF ||
					r_ptr->flagsr & RFR_RES_ALL || randint1(power) < r_ptr->level)
				{
					msg_format(_("しかし効果がなかった。", "There was no effect."));
				}
				else
				{
					msg_format(_("%sは混乱した！", "%s is confused!"),m_name);
					(void)set_monster_confused(cave[y][x].m_idx,  MON_CONFUSED(m_ptr)+10 + randint1(plev/3));
				}
				set_monster_csleep(cave[y][x].m_idx, 0);
				anger_monster(m_ptr);
			}

			break;
		}
	case 2:
		{
			//v1.1.34 サニーの「光を屈折させて離れた場所の光景を見る」能力を追加。
			//まず特殊フラグを立ててからflying_dist値を閉じたドアやカーテンに遮られるようにして再計算する。
			//次にこのflying_dist値が設定されたグリッドのみを啓蒙、モンスター感知する。
			//最後にフラグを戻してflying_dist値を再計算する。
			if (only_info) return format(_("範囲:%d移動グリッド", "%d spaces"), MONSTER_FLOW_DEPTH);

			if (p_ptr->blind)
			{
				msg_print(_("目が見えない！", "You can't see!"));
				return NULL;
			}

			msg_print(_("離れた場所の映像があなたの目の前に広がった……", "Images of distant locations unfold before your eyes..."));
			flag_sunny_refraction = TRUE;
			update_flying_dist();
			wiz_lite(FALSE);
			detect_monsters_normal(255);
			flag_sunny_refraction = FALSE;
			update_flying_dist();

		}
		break;



	case 3:
		{
			int	typ = GF_LITE_WEAK;
			int dam = 40 + plev + chr_adj;
			if(!is_daytime()) dam /= 2;
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1)
			{
				typ = GF_LITE;
				dam += plev * 3 / 5;
			}

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("あなたは眩く光った！", "You emit dazzling light!"));
			project_hack2(typ,0,0,dam);
			lite_room(py,px);
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) sunny_charge_sunlight(-250);

			break;
		}
	case 4:
		{
			int base = 5+plev/5;
			if(is_daytime()) base *= 2;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			msg_format(_("あなたは光を操って姿を消した！", "You conceal your form by manipulating light!"));
			set_tim_superstealth(base + randint1(base),FALSE, SUPERSTEALTH_TYPE_OPTICAL);
			break;
		}
	case 5:
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage = plev * 5 + chr_adj * 5;
			if(!is_daytime()) damage /= 2;
			if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) damage = damage * 3 / 2;

			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sに近寄り、突然太陽のように光った！", "Suddenly, light as bright as sun flashes in front of %s!"),m_name);
				lite_room(py,px);

				if(!(r_ptr->flagsr & (RFR_RES_LITE | RFR_RES_ALL)))
				{
					if(!(r_ptr->flags3 & RF3_NO_CONF))
					{
						msg_format(_("%sは混乱した！", "%s is confused!"),m_name);
						(void)set_monster_confused(cave[y][x].m_idx,  MON_CONFUSED(m_ptr)+10 + randint1(plev/2));
					}
					if(!(r_ptr->flags3 & RF3_NO_STUN))
					{
						msg_format(_("%sは朦朧とした！", "%s is stunned!"),m_name);
						(void)set_monster_stunned(cave[y][x].m_idx,  MON_STUNNED(m_ptr)+10 + randint1(plev/2));
					}
				}
				project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_LITE,PROJECT_KILL,-1);

				if (p_ptr->magic_num1[0] > SUNNY_CHARGE_SUNLIGHT_1) sunny_charge_sunlight(-1000);
			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}



	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


/*:::ルナ専用技*/
