cptr do_cmd_class_power_aux_lunar(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int dam = 10 + plev * 4 + chr_adj * 3;
			if(is_daytime()) dam /= 2;

			if(only_info) return format(_str_eff_dam,dam/2);
			msg_print(_("月光が降り注いだ・・", "Moonlight pours down..."));
			project(0, 1, py, px, dam,GF_DARK, PROJECT_JUMP | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM, -1);

			break;
		}
	case 1://静寂1 tim_general[0]を使う
		{
			int time = 10+plev/5;
			int rad = plev / 5;
			if(only_info) return format(_("期間:%d 範囲:%d", "dur: %d rad: %d"),time,rad);
			if(p_ptr->tim_general[0] || p_ptr->tim_general[1])
			{
				msg_print(_("すでに能力を使っている。", "This ability already is in effect."));
				return NULL;
			}

			msg_print(_("あなたは指を鳴らした。周囲の音が消えた。", "You move your fingers in circles. Everything around you becomes silent."));
			set_tim_general(time,FALSE,0,0);

		}
		break;
	case 2://静寂解除
		{
			if(only_info) return format("");
			if(!p_ptr->tim_general[0] && !p_ptr->tim_general[1])
			{
				msg_print(_("能力を使っていない。", "You haven't used your ability."));
				return NULL;
			}

			msg_print(_("あなたは能力を解除した。", "You dispel your ability."));
			set_tim_general(0,TRUE,0,0);
			set_tim_general(0,TRUE,1,0);

		}
		break;

	case 3:
		{
			int damage = 30 + p_ptr->lev * 2 + chr_adj;
			if(is_daytime()) damage /= 2;

			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("月光の壁が前方を薙ぎ倒した。", "A wall of moonlight sweeps in front of you."));
			fire_spark(GF_DARK,dir,damage,2);

			break;
		}
	case 4:
		{
			int base = p_ptr->lev * 5;
			int sides = chr_adj * 6;
			if(is_daytime()) base /= 2;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_DARK, dir, base + randint1(sides), 3,_("月光が束になって渦を巻いた！", "Dense moonlight swirls around!"))) return NULL;
			break;
		}

	case 5://静寂2 tim_general[1]を使う
		{
			int time = 30;
			if(only_info) return format(_str_eff_dur,time);
			if(p_ptr->tim_general[1])
			{
				msg_print(_("すでに能力を使っている。", "This ability already is in effect."));
				return NULL;
			}

			msg_print(_("あなたはフロア全体の音に干渉した。", "You block out all sounds on this level."));
			set_tim_general(time,FALSE,1,0);
			p_ptr->tim_general[0]=0;
		}
		break;


	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


