cptr do_cmd_class_power_aux_star(int num, bool only_info)
{
	int dir,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int base = 20+p_ptr->lev;
			int sides = chr_adj * 2;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(!fire_ball_jump(GF_METEOR, dir, base + randint1(sides), 0,_("星のかけらが落ちた。", "A star shard falls down."))) return NULL;
			break;
		}
	case 1:
		{
			int dice = 4 + plev / 10;
			int sides = 2 + chr_adj / 20;
			int num = 8 + plev / 9 ;
			if(only_info) return format(_("損傷:%dd%d * %d", "dam: %dd%d * %d"),dice,sides,num);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("星屑が降り注いだ。", "Stardust rains down."));
			fire_blast(GF_LITE, dir, dice, sides, num, 3, 0);
			break;
		}
	case 2:
		{
			int damage = plev * 3 + chr_adj * 5 / 3;

			if(only_info) return format(_str_eff_dam,damage);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("星の光が流れた。", "Starlight shines forth."));
			fire_beam(GF_LITE, dir, damage);
			break;
		}
	case 3: //爆弾設置　布都の皿と同じ扱いにする
		{
			int dam = 100+plev*4;
			if(only_info) return format(_str_eff_dam,dam);
			if(!futo_can_place_plate(py,px) || cave[py][px].special)
			{
				msg_print(_("ここには爆弾を置けない。", "You cannot set up a bomb here."));
				return NULL;
			}
			msg_print(_("爆弾を仕掛けた。", "You set up a bomb."));
			/*:::Mega Hack - cave_type.specialを爆弾のカウントに使用する。*/
			cave[py][px].info |= CAVE_OBJECT;
			cave[py][px].mimic = feat_bomb;
			cave[py][px].special = 10;
			while(one_in_(5)) cave[py][px].special += randint1(20);

			note_spot(py, px);
			lite_spot(py, px);

		}
		break;
	case 4:
		{
			base = plev * 5;
			sides = chr_adj * 6;

			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("あなたは眩い煌きを放った！", "You fire dazzling glitter!"));
			fire_rocket(GF_PSY_SPEAR, dir, base+randint1(sides),2);
			break;
		}


	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




