//-Mega Hack-
//六壬神火と貴竜の矢の止まるビームなどの表現のためPROJECT_FUTOを使う。
cptr do_cmd_class_power_aux_futo(int num, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int charge = 20;
			if(only_info) return "";
			if(!futo_can_place_plate(py,px))
			{
				msg_print(_("ここには皿を置けない。", "You can't place a plate here."));
				return NULL;
			}

			if(p_ptr->au < charge)
			{
				msg_print(_("皿代がない・・", "You don't have enough money..."));
				return NULL;
			}
			else
			{
				p_ptr->au -= charge;
				p_ptr->redraw |= PR_GOLD;
			}
			msg_print(_("皿を置いた。", "You set down a plate."));
			/* Create a glyph */
			cave[py][px].info |= CAVE_OBJECT;
			cave[py][px].mimic = feat_plate;
			note_spot(py, px);
			lite_spot(py, px);

		}
		break;
	case 1:
		{
			int dam = 15 + plev*3/2 + chr_adj;
			//int ty,tx;

			if(only_info) return  format(_str_eff_dam,dam);


			//v1.1.44 再帰処理のしすぎでフリーズしたので処理を見直した
			msg_print(_("火球を放った！", "You throw a flame sphere!"));
			if (!execute_restartable_project(1, 0, 0, 0, 0, dam, GF_FIRE, 0))
				msg_print(_("しかしターゲットがいなかった。", "However, there were no appropriate targets."));

			/*
			if(futo_determine_target(py,px,&ty,&tx))
			{
				msg_print("火球を放った！");
				project(0,0,ty,tx,dam,GF_FIRE,( PROJECT_BEAM | PROJECT_GRID | PROJECT_ITEM | PROJECT_KILL | PROJECT_FUTO),-1);
			}
			else
			{
				msg_print("火球を出したがターゲットがいなかった。");
			}
			*/

		}
		break;
	case 2:
		{
			int dam = 10 + plev * 3 / 2 + chr_adj / 2;
			int charge = 50;

			if(use_itemcard) charge = 0;

			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;

			if(p_ptr->au < charge)
			{
				msg_print(_("皿代がない・・", "You don't have enough money..."));
				return NULL;
			}
			else
			{
				p_ptr->au -= charge;
				p_ptr->redraw |= PR_GOLD;
			}

			msg_print(_("皿を投げた！", "You throw a plate!"));
			fire_rocket(GF_THROW_PLATE, dir, dam,0);
		}
		break;
	case 3:
	{
		int i;
		u32b mode = PM_FORCE_PET;
		bool flag_exist = FALSE;
		if (only_info) return "";

		for (i = 1; i < m_max; i++)
		{
			monster_type *m_ptr = &m_list[i];
			if (!m_ptr->r_idx) continue;
			if ((m_ptr->r_idx == MON_ANCIENT_SHIP)) flag_exist = TRUE;
		}
		if (flag_exist)
		{
			msg_print(_("すでに舟を呼び出している。", "You already have called your ship."));
			return NULL;
		}
		if (summon_named_creature(0, py, px, MON_ANCIENT_SHIP, mode))
			msg_print(_("宙に浮く舟が現れた。", "A ship floating in the air appears."));
		else
			msg_print(_("舟は出てこなかった。", "No ship has appeared."));

		break;

	}

	case 4:
		{
			int range = plev;
			int dam = plev * 4;
			if(only_info) return format(_("距離:%d 損傷:〜%d", "dist: %d dam: ~%d"),range,dam);
			if (p_ptr->anti_tele)
			{
				msg_format(_("今はうまく移動できないようだ。", "You can't properly move in this manner right now."));
				return NULL;
			}
			if (!dimension_door(D_DOOR_FUTO)) return NULL;
			break;
		}
	case 5:
		{
			int x,y;
			int dam = plev * 2 + chr_adj * 5 / 3;
			int rad = plev / 6;
			bool flag = FALSE;
			if(only_info) return format(_("効果範囲:%d 損傷:%d", "rad: %d dam: %d"),rad,dam);

			for(y=py-rad;y<=py+rad;y++)
			{
				for(x=px-rad;x<=px+rad;x++)
				{
					if (!in_bounds(y, x)) continue;
					if(distance(py, px, y, x) > rad) continue;
					if(!is_plate_grid(&cave[y][x])) continue;
					if(!projectable(py,px,y,x)) continue;
					futo_break_plate(y,x);
					flag = TRUE;
					project(0,1,y,x,dam,GF_WATER,(PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM | PROJECT_JUMP),-1);
				}
			}
			if(!flag) msg_print(_("近くに皿がないので何も起こらなかった。", "There were no plates nearby; nothing happens."));
		}
		break;

	case 6:
	{
		int charge = 500;
		int dam = 110 + plev * 4 + chr_adj * 3;
		int rad = 2 + plev / 40;
		int plate_num = 1 + (plev+5)/10;
		int j;

		if(only_info) return format(_str_eff_dam_around,dam/2);

		if(p_ptr->au < charge)
		{
			msg_print(_("皿代がない・・", "You don't have enough money..."));
			return NULL;
		}
		else
		{
			p_ptr->au -= charge;
			p_ptr->redraw |= PR_GOLD;
		}

		msg_print(_("数枚の皿が旋風を巻き起こした！", "A whirlwind of plates surrounds you!"));
		project(0,rad,py,px,dam,GF_WINDCUTTER,(PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM | PROJECT_JUMP),-1);
		for(i=0;i<plate_num;i++)
		{
			int attempt = 100;
			int dy,dx;

			for(j=0;j<attempt;j++)
			{
				scatter(&dy, &dx, py, px, rad, 0);
				if(!futo_can_place_plate(dy,dx)) continue;

				cave[dy][dx].info |= CAVE_OBJECT;
				cave[dy][dx].mimic = feat_plate;
				note_spot(dy, dx);
				lite_spot(dy, dx);

				break;
			}
		}
	}
	break;
	case 7:
		{
			object_type *o_ptr;
			int mult;
			int dam;

			if(inventory[INVEN_LARM].tval == TV_BOW || inventory[INVEN_RARM].tval == TV_BOW)
			{
				if(inventory[INVEN_LARM].tval == TV_BOW)
					o_ptr = &inventory[INVEN_LARM];
				else
					o_ptr = &inventory[INVEN_RARM];

				mult = bow_tmul(o_ptr->tval,o_ptr->sval);
				if (p_ptr->xtra_might) mult++;
				dam = (plev/3 + o_ptr->to_d + chr_adj / 5) * mult;
				if(dam < 1) dam=1;
			}
			else dam = 0;

			if(only_info) return  format(_str_eff_dam,dam);
			if(!dam)
			{
				msg_print(_("弓を装備していない。", "You're not holding a bow."));
				return NULL;
			}
			if (!get_aim_dir(&dir)) return NULL;

			//v1.1.44 再帰処理のしすぎでフリーズしたので処理を見直した
			msg_print(_("光の矢を放った。", "You fire an arrow of light."));
			execute_restartable_project(1, dir, 0, 0, 0, dam, GF_DISP_ALL, 5);

/*

			msg_print("光の矢を放った。");
			tx = px + 99 * ddx[dir];
			ty = py + 99 * ddy[dir];
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			project(0, 5, ty, tx, dam, GF_DISP_ALL, flg, -1);
*/
		}
		break;
	case 8:
	{
		int x,y;
		int charge = 5000;
		int dam = plev * 2 + chr_adj *2;
		if(only_info) return format(_("損傷:1d%d", "dam: 1d%d"),dam);

		if(p_ptr->au < charge)
		{
			msg_print(_("皿代がない・・", "You don't have enough money..."));
			return NULL;
		}
		else
		{
			p_ptr->au -= charge;
			p_ptr->redraw |= PR_GOLD;
		}

		msg_print(_("あなたは大量の皿を投げ放った！", "You throw a lot of plates!"));
		project_hack2(GF_SHARDS,1,dam,0);

		for(y=py-MAX_RANGE;y<=py+MAX_RANGE;y++)
		{
			for(x=px-MAX_RANGE;x<=px+MAX_RANGE;x++)
			{
				if (!in_bounds(y, x)) continue;
				if(!one_in_(6)) continue;
				if(!projectable(py,px,y,x)) continue;
				if(distance(py, px, y, x) > MAX_RANGE) continue;
				if(!futo_can_place_plate(y,x)) continue;

				cave[y][x].info |= CAVE_OBJECT;
				cave[y][x].mimic = feat_plate;
				note_spot(y, x);
				lite_spot(y, x);

			}
		}
	}
	break;


	case 9:
	{
		int range = 9;
		int x, y, tx, ty;
		int count_plate = 0;
		int count_enemy = 0;

		if(only_info) return format(_("効果範囲:%d", "eff rad: %d"),range);

		for(y=py-range;y<=py+range;y++)
		{
			for(x=px-range;x<=px+range;x++)
			{
				if (!in_bounds(y, x)) continue;
				if(!projectable(py,px,y,x)) continue;
				if(distance(py, px, y, x) > range) continue;

				if(cave[y][x].m_idx)
				{
					count_enemy++;

					if(one_in_(count_enemy))
					{
						ty = y;
						tx = x;
					}
				}

				if(is_plate_grid(&cave[y][x]))
				{
					count_plate++;
					futo_break_plate(y,x);
				}
			}
		}

		if(!count_enemy)
		{
			msg_print(_("しかし周囲に敵がいなかった。", "However, there were no enemies nearby."));
		}
		else
		{
			int dam,typ,rad;
			cptr desc;
			char m_name[80];
			monster_type *m_ptr;
			m_ptr = &m_list[cave[ty][tx].m_idx];
			monster_desc(m_name, m_ptr, 0);

			if(count_plate < 5)
			{
				if(one_in_(3)) desc = _("タライ", "A basin");
				else if(one_in_(2)) desc = _("花瓶", "A vase");
				else desc = _("オタマ", "A ladle");
				dam = damroll(3,3);
				rad = 0;
				typ = GF_MISSILE;
			}
			else if(count_plate < 10)
			{
				if(one_in_(3)) desc = _("植木鉢", "A flowerpot");
				else if(one_in_(2)) desc = _("鬼瓦", "Onigawara");
				else desc = _("一升瓶", "A large bottle");
				dam = damroll(7,7);
				rad = 0;
				typ = GF_MISSILE;
			}
			else if(count_plate < 20)
			{
				if(one_in_(2)) desc = _("タンス", "A chest of drawers");
				else desc = _("信楽焼の狸", "A tanuki statue");
				dam = damroll(10,10);
				rad = 0;
				typ = GF_MISSILE;
			}
			else if(count_plate < 30)
			{
				if(one_in_(2))desc = _("要石", "A keystone");
				else  desc = _("墓石", "A tombstone");
				dam = damroll(16,16);
				rad = 0;
				typ = GF_ARROW;
			}
			else if(count_plate < 60)
			{
				desc = _("廃電車", "An abandoned train");
				dam = damroll(33,33);
				rad = 2;
				typ = GF_MISSILE;
			}
			else if(count_plate < 100)
			{
				desc = _("隕石", "A meteor");
				dam = damroll(50,50);
				rad = 3;
				typ = GF_METEOR;
			}
			else if(count_plate < 200)
			{
				desc = _("未確認飛行物体", "An unidentified flying object");
				dam = damroll(66,66);
				rad = 4;
				typ = GF_METEOR;
			}
			else
			{
				desc = _("聖輦船", "Seirensen");
				dam = damroll(88,88);
				rad = 7;
				typ = GF_DISINTEGRATE;
			}
#ifdef JP
			msg_format("%sの頭上から%sが降ってきた！",m_name,desc);
#else
            msg_format("%s falls on top of %s!",desc,m_name);
#endif
			project(0,rad,ty,tx,dam,typ,(PROJECT_JUMP|PROJECT_GRID|PROJECT_ITEM|PROJECT_KILL),-1);
			if(count_plate >= 60)
				project(0,rad,ty,tx,dam/8, GF_DEC_ALL, (PROJECT_JUMP | PROJECT_HIDE | PROJECT_KILL), -1);


		}

	}
	break;



	case 10:
	{
		int x, y;
		int count_plate = 0;
		int dam = plev * 6 + chr_adj * 10;

		if(only_info) return format(_("損傷:%d+α", "dam: %d+@"),dam);

		for(y=py-MAX_RANGE;y<=py+MAX_RANGE;y++)
		{
			for(x=px-MAX_RANGE;x<=px+MAX_RANGE;x++)
			{
				if (!in_bounds(y, x)) continue;
				if(!projectable(py,px,y,x)) continue;
				if(distance(py, px, y, x) > MAX_RANGE) continue;
				if(!is_plate_grid(&cave[y][x])) continue;
				count_plate++;
				futo_break_plate(y,x);
			}
		}

		dam += count_plate * 7;
		if(dam > 9999) dam = 9999;
		msg_print(_("辺り一面が火の海となった！", "Everything around you turns into a sea of flames!"));
		project_hack2(GF_LAVA_FLOW,1,4,0);
		project_hack2(GF_FIRE,0,0,dam);


	}
	break;

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




