cptr do_cmd_class_power_aux_kanako(int num, bool only_info)
{
	int dir,dice,sides,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int rank;

	if(use_itemcard) rank = 5;
	else rank = kanako_rank();

	switch(num)
	{
	case 0:
		{
			if(only_info) return "";
			msg_format("%s",kanako_comment());
			return NULL; //行動順消費しない
		}
		break;
	case 1:
		{
			int num = 1 + plev / 5 + rank * 2;
			bool flag = FALSE;
			if(only_info) return "";
			if(num > 20) num = 20;
			for(i=0;i<num ;i++)
			{
				if ((summon_specific(-1, py, px, plev, SUMMON_SNAKE, (PM_FORCE_PET))))flag = TRUE;
			}
			if(flag)
				msg_print(_("蛇を呼び出した！", "You summon snakes!"));
			else
				msg_print(_("蛇は現れなかった。", "No snakes appear."));
		}
		break;

	case 2:
		{
			int dist = MIN(15,(3 + rank * 2));

			dice = 4 + plev / 6;
			sides = 6 + chr_adj / 5;
			base = 30 + plev * rank / 2;

			if(only_info) return  format(_("射程:%d 損傷:%d+%dd%d", "rng; %d dam: %d+%dd%d"),dist,base,dice,sides);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_format(_("カードから大きな柱が飛び出した！", "A large pillar flies out of the card!"));
			else
				msg_format(_("御柱を投げ放った！", "You throw an onbashira!"));
			fire_beam(GF_TRAIN, dir, base + damroll(dice,sides));

		}
		break;
	case 3:
		{
			int rad = 10 + plev / 3;

			if(only_info) return  format("");
			if(use_itemcard)
				msg_format(_("筒粥を食べた。", "You ate some tube porridge."));
			else
				msg_format(_("あなたは筒粥を炊き始めた・・", "You start preparing tube porridge..."));

			if(rank > 6)
			{
				wiz_lite(FALSE);
			}
			else
			{
				if(rank > 0) detect_objects_normal(rad);
				if(rank > 4) map_area(rad);

			}
			detect_objects_gold(rad);
			if(rank > 1) detect_traps(rad, TRUE);
			if(rank > 2) detect_monsters_normal(rad);
//筒粥神事って食べるんだっけ？調べ直しとく
			if (p_ptr->food < PY_FOOD_FULL) set_food(p_ptr->food + 3000);
		}
		break;
	case 4:
		{
			int dist = MIN(15,(6 + rank * 2));
			if(only_info) return  format(_str_eff_range,dist);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("眼前に道が開かれた。", "Path opens in front of you."));
			if(rank > 4) project_hook(GF_KILL_WALL, dir, 0, (PROJECT_BEAM | PROJECT_THRU | PROJECT_GRID | PROJECT_DISI));
			project_hook(GF_KILL_TRAP, dir, 0, (PROJECT_BEAM | PROJECT_GRID | PROJECT_ITEM | PROJECT_HIDE));
		}
		break;

	case 5:
		{
			bool flag = FALSE;
			int num;
			int i;
			dice = 1;
			sides = p_ptr->lev + chr_adj / 2;
			num = rank * rank / 3 + 4;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);

			msg_format(_("雨のように矢を降らせた！", "Arrows start falling like rain!"));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_MISSILE,damroll(dice,sides),1, 0,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_str_mess_cant_find_enemies);
					break;
				}
			}
		}
		break;
	case 6:
		{
			int power = 10 + rank * rank * 10;
			if (only_info) return format(_str_eff_power, power);

			if (rank > 5) msg_print(_("あなたは細い植物の蔓をかざした！", "You hold out a thin plant vine!"));
			else if (rank > 3) msg_print(_("あなたは神々しく光った！", "You radiate divine light!"));
			else msg_print(_("あなたは偉そうに見得を切った！", "You make a grand gesture!"));

			project_hack2(GF_DEC_ATK, 0, 0, power);
			project_hack(GF_DEC_MAG, power);

			break;
		}



	case 7:
		{
			int time = 30;
			int dec_hp;

			if(only_info) return format(_str_eff_dur,time);

			if(one_in_(3)) msg_print(_("御柱が飛来し背に装着された！", "You position onbashira behind your back!"));
			else if(one_in_(2)) msg_print(_("皆の信仰の力が流れ込んでくる！", "You feel everyone's faith flowing through you!"));
			else msg_print(_("あなたの体は信仰の力で七色に輝いた！", "You body glows in colors of rainbow due to the power of faith!"));

			if(rank > 0)
			{
				set_afraid(0);
				set_hero(time,FALSE);
			}
			if(rank > 1 && rank < 7)
			{
				set_tim_addstat(A_STR,5,time,FALSE);
			}
			if(rank > 2)
			{
				set_protevil(time,FALSE);
			}
			if(rank > 3)
			{
				set_shield(time,FALSE);
			}
			if(rank > 4)
			{
				set_oppose_acid(time, FALSE);
				set_oppose_elec(time, FALSE);
				set_oppose_fire(time, FALSE);
				set_oppose_cold(time, FALSE);
				set_oppose_pois(time, FALSE);
			}
			if(rank > 5)
			{
				set_magicdef(time, FALSE);
			}
			if(rank > 6)
			{
				dec_hp = p_ptr->mhp - p_ptr->chp;
				set_tim_addstat(A_STR,107,time,FALSE);
				set_tim_addstat(A_DEX,107,time,FALSE);
				set_tim_addstat(A_CON,107,time,FALSE);
				p_ptr->chp = p_ptr->mhp - dec_hp;
				p_ptr->redraw |= PR_HP;
				handle_stuff();
			}
		}
		break;

	case 8:
		{
			int num = 1 + rank / 2;
			bool flag = FALSE;
			if(only_info) return "";
			if(rank == 7) num += 2;
			for(i=0;i<num ;i++)
			{
				if ((summon_specific(-1, py, px, plev * 3 / 2, SUMMON_DEITY, (PM_FORCE_PET)))) flag = TRUE;
			}
			if(flag)
				msg_print(_("神々があなたの呼びかけに応えた！", "Deities respond to your call!"));
			else
				msg_print(_("誰も現れなかった。", "Nobody has appeared."));
		}
		break;


	case 9:
		{
			int rad = 1 + (rank + 1) / 2;
			int dam = 1 + rank * rank * 5;
			if(only_info) return format(_("損傷:%d*不定", "dam: %d*undef"),dam);
			msg_print(_("巨大な御柱が流星のように天から降り注いだ！", "Massive onbashira fall from above like meteors!"));
			cast_meteor(dam, rad,GF_METEOR);

		}
		break;

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}





/*:::妹紅専用技*/
