cptr do_cmd_class_power_aux_mokou(int num, bool only_info)
{
	int dir,base,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int dam = (p_ptr->lev * 8 + chr_adj * 10 + 300);
			if(only_info) return format(_("損傷:最大%d", "dam: up to %d"),dam/2);

			mokou_resurrection();

			break;
		}
	case 1:
		{
			int time;
			base = 15 + plev / 2;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			msg_print(_("燃え盛る炎の翼が背に現れた。", "Blazing wings appear behind your back."));
			lite_room(py,px);
			set_tim_levitation(time,FALSE);

		}
		break;
	case 2:
		{
			int dam = 30 + plev * 4 + chr_adj * 3 ;
			int rad = 1 + plev / 24;

			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_print(_("カードから火の鳥が現れて飛んで行った！", "A phoenix flies out of the card!"));
			else
				msg_print(_("火の鳥が飛んだ！", "A phoenix flies forth!"));
			fire_rocket((plev > 39)?GF_FIRE:GF_NUKE, dir, dam, rad);
		}
		break;
	case 3:
		{
			int power = 50 + plev * 2;
			if(only_info) return format(_str_eff_power,power);
			msg_print(_("ダンジョンが異質な雰囲気に包まれた。", "The atmosphere in the dungeon suddenly feels very unnatural."));
			stasis_monsters(power);
			break;

		}
	case 4:
		{
			int dam = plev * 15 + chr_adj * 15;
			int rad = plev / 10;

			if(only_info) return format(_("範囲:%d 損傷:中心地で%d ", "rad: %d dam: up to %d"),rad,dam);

			msg_print(_("火炎の大旋風を巻き起こした！", "You are engulfes in a whirlwind of flames!"));
			project(PROJECT_WHO_SUICIDAL_FIRE, rad, py, px, dam,GF_FIRE, PROJECT_JUMP | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM, -1);
			inven_damage(set_fire_destroy, 2);

			break;
		}
	case 5:
		{
			int xx,yy;
			int num = plev / 5;
			int rad = plev / 10;
			if(num < 3) num = 3;
			if(rad < 2) rad = 2;
			if(only_info) return format(_("個数:最大%d ", "amt: up to %d"),num);

			msg_print(_("発火の符を撒き散らした！", "You scatter incendiary seals around!"));
			for(i=0;i<num;i++)
			{
				scatter(&yy,&xx,py,px,rad,0);
				if(!cave_clean_bold(yy,xx)) continue;
				cave[yy][xx].info |= CAVE_OBJECT;
				cave[yy][xx].mimic = feat_explosive_rune;
				note_spot(yy, xx);
				lite_spot(yy, xx);
			}
		}
		break;
	case 6:
		{
			int y, x;
			monster_type *m_ptr;
			monster_race *r_ptr;

			int damage = plev * 3 + chr_adj * 5;
			if(damage < 100) damage = 100;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (cave[y][x].m_idx && (m_ptr->ml) )
			{
				char m_name[80];
				r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sに不死鳥の羽が突き刺さった。", "You stab %s with a phoenix feather."),m_name);

				if(!(r_ptr->flags1 & RF1_UNIQUE) && !(r_ptr->flags7 & RF7_UNIQUE2) && (r_ptr->flags3 & RF3_UNDEAD)
					&& (randint1(r_ptr->level) + 20 < plev))
				{
					damage = m_ptr->hp + 1;
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_MISSILE,PROJECT_KILL,-1);
				}
				else
				{
					project(0,0,m_ptr->fy,m_ptr->fx,damage,GF_FIRE,PROJECT_KILL,-1);
				}

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}

			break;
		}
	case 7:
		{
			int damage = (p_ptr->lev * 6 + chr_adj * 10);
			if(only_info) return format(_str_eff_dam,damage);
			if (p_ptr->anti_tele)
			{
				msg_format(_("今はうまく移動できないようだ。", "You can't properly move in this manner right now."));
				return NULL;
			}
			if (!dimension_door(D_DOOR_MOKOU)) return NULL;
			break;
		}

	case 8:
		{
			int dam = plev * 12 + chr_adj * 20;
			if(dam < 600) dam = 600;
			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("爆音と共に周囲が炎に包まれた！", "There is a sound of explosion, and everything around you is engulfed in flames!"));
			project_hack2(GF_FIRE,0,0,dam);
			break;
		}
	case 9:
		{
			int time,percentage;
			base = 25 + plev / 2;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);

			mokou_resurrection();
			time = base + randint1(base);
			msg_print(_("あなたはより強くなって蘇った！", "You feel stronger than before after being reborn!"));
			percentage = p_ptr->chp * 100 / p_ptr->mhp;
			time = base + randint1(base);
			set_tim_addstat(A_STR,110,time,FALSE);
			set_tim_addstat(A_INT,110,time,FALSE);
			set_tim_addstat(A_WIS,110,time,FALSE);
			set_tim_addstat(A_DEX,110,time,FALSE);
			set_tim_addstat(A_CON,110,time,FALSE);
			set_tim_addstat(A_CHR,110,time,FALSE);
			set_fast(time, FALSE);
			set_hero(time,FALSE);
			set_tim_speedster(time, FALSE);
			p_ptr->chp = p_ptr->mhp * percentage / 100;
			p_ptr->redraw |= (PR_HP | PR_MANA);
			redraw_stuff();

		}
		break;
	case 10:
		{
			int dam = plev * 5 + chr_adj * 5;

			if(only_info) return format(_("損傷:毎時最大%d+1d%d ", "dam: up to %d+1d%d"),dam/2,dam/2);
			p_ptr->magic_num2[0] = 1;//フラグ

			break;
		}



	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


