cptr do_cmd_class_power_aux_ichirin(int num, bool only_info)
{
	int dir,dice,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int x, y;
			int dist = 2 + plev / 15;
			monster_type *m_ptr;

			dice = plev / 3;
			sides = 10 + chr_adj / 10;
			base = MAX(p_ptr->to_d[0],p_ptr->to_d[1]);
			if(only_info) return format(_("射程:%d 損傷:%d+%dd%d", "rng: %d dmg: %d+%dd%d"),dist,base,dice,sides);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sの上に雲山の拳が落ちた！", "Unzan's fist crashes down on %s!"),m_name);
				project(0,0,y,x,damroll(dice,sides)+base,GF_ARROW,PROJECT_KILL,-1);
			}

			break;
		}


	case 1:
		{
			dice = 3 + p_ptr->lev / 5;
			sides = 10;
			base = p_ptr->lev + chr_adj;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("雲山が鋭い眼光を放った！", "Unzan stares intently!"));
			fire_beam(GF_LITE, dir, base + damroll(dice,sides));
			break;
		}

	case 2:
		{
			int y, x;
			int rad = 3 + plev / 16;

			base = plev * 3 + chr_adj * 2 - 10;
			if(base < 50) base = 50;

			if(only_info) return format(_("損傷:最大%d", "dam: up to %d"),base);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			if(in_bounds(y,x) && cave_have_flag_bold(y, x, FF_PROJECT))
			{
				if(use_itemcard)
					msg_format(_("暴風が起こった！", "A tornado occurs!"));
				else
					msg_format(_("雲山が暴風を起こした！", "Unzan causes a tornado!"));
				project(0, rad, y, x, base, GF_TORNADO, PROJECT_KILL , -1);

			}
			else
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			break;
		}
	case 3:
		{
			int dam = 30 + plev * 3 + chr_adj * 2 ;
			int rad = 1 + plev / 40;

			if(only_info) return format(_str_eff_dam,dam);
			if (!get_aim_dir(&dir)) return NULL;

			if(use_itemcard)
				msg_format(_("カードから拳のような雲が現れ飛んでいった！", "A fist-shaped cloud flies out of the card!"));
			else
				msg_print(_("雲山の拳が飛んだ！", "Unzan's fist flies forth!"));
			fire_rocket(GF_FORCE, dir, dam, rad);
		}
		break;
	case 4:
		{
			int dam = plev * 7 + chr_adj * 5;
			int rad = 1;

			if(only_info) return format(_str_eff_dam,dam/2);

			msg_print(_("雲山と共に激しく回転した！", "You spin intensely together with Unzan!"));
			project(0, rad, py, px, dam, GF_TRAIN, (PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM), -1);

			break;
		}
	case 5:
		{
			int x, y;
			int dist = plev / 4;
			int rad = 2;
			monster_type *m_ptr;

			dice = plev / 2;
			sides = 10;
			base = MAX(p_ptr->to_d[0],p_ptr->to_d[1]) * 3 + chr_adj * 3;
			if(only_info) return format(_("射程:%d 損傷:%d+%dd%d", "rng: %d dmg: %d+%dd%d"),dist,base,dice,sides);
			project_length = dist;

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_print(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				msg_format(_("雲山の巨大な拳が大地を穿った！", "Unzan's massive fist smashes the earth!"));
				project(0,rad,y,x,damroll(dice,sides)+base,GF_METEOR,(PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM),-1);
			}

			break;
		}
	case 6:
		{
			int y, x;
			monster_type *m_ptr;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("あなたと雲山は気合を溜めた…", "You and Unzan focus..."),m_name);
				py_attack(y,x,HISSATSU_UNZAN);
			}

			break;
		}
	case 7:
		{
			if(only_info) return format("");
			set_action(ACTION_KATA);
			p_ptr->special_defense |= KATA_MUSOU;
			p_ptr->update |= (PU_BONUS);
			p_ptr->update |= (PU_MONSTERS);
			msg_format(_("あなたは仁王立ちで敵の攻撃を待ち構えた！", "You stand steadfast, prepared to receive any enemy attacks!"));

		}
		break;


	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}




