cptr do_cmd_class_power_aux_sumireko(int num, bool only_info)
{
	int dir,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int inven2_num = calc_inven2_num();

	switch(num)
	{

	case 0:
		{
			int mult = 1 + (plev + 10)/ 20;
			if(only_info) return format(_("倍率:%d", "mult: %d"),mult);
			if (!do_cmd_throw_aux(mult, FALSE, -2)) return NULL;
		}
		break;
	case 1:
		{
			int dist = 4 + plev / 8;
			int dice = 8 + plev / 2;
			int sides = 5;
			if(only_info) return format(_("損傷:%dd%d 射程:%d", "dam: %dd%d rng: %d"),dice,sides,dist);
			project_length = dist;
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("指を鳴らすと空気が燃え上がった。", "You click your fingers, and air bursts into flames."));
			fire_beam(GF_FIRE, dir, damroll(dice,sides));
			break;
		}
	case 2:
		{
			if(only_info) return format("");
			project_length = 2 + plev / 24;
			if (!get_aim_dir(&dir)) return NULL;
			project_hook(GF_ATTACK, dir, HISSATSU_NO_AURA, PROJECT_STOP | PROJECT_KILL);
			break;
		}
	case 3:
	{
		int dist = 5 + plev / 10;
		if (only_info) return format(_("距離:%d", "dist: %d"), dist);

		if (!teleport_to_specific_dir(dist, 7, 0)) return NULL;

		new_class_power_change_energy_need = 50;
		break;
	}

	case 4:
		{
			int j;
			bool flag_pick = FALSE;
			bool flag_max = TRUE;
			object_type forge;
			object_type *o_ptr;
			object_type *q_ptr;
			char o_name[MAX_NLEN];

			if(only_info) return format(_("最大:%d個", "up to %d"),inven2_num);
			//フロアのアイテムを全て調べる
			for(i=0;i<inven2_num;i++)
			{
				int item = 0;
				int item_count_temp = 0;
				if(inven_add[i].k_idx) continue;
				flag_max = FALSE;
				//視界内のアイテムをランダムに選定
				for(j=0;j<o_max;j++)
				{
					o_ptr = &o_list[j];
					if (!o_ptr->k_idx) continue;
					if (!projectable(o_ptr->iy,o_ptr->ix,py,px)) continue;

					item_count_temp++;
					if(one_in_(item_count_temp)) item = j;
				}
				if(!item) break;
				//選ばれたアイテムを追加インベントリに1つだけ格納し床上アイテムを減らす。所持品重量は考慮しない。
				flag_pick = TRUE;
				o_ptr = &o_list[item];
				q_ptr = &forge;
				object_copy(q_ptr, o_ptr);
				q_ptr->number = 1;
				distribute_charges(o_ptr, q_ptr, 1);
				object_desc(o_name, q_ptr, 0);
				msg_format(_("%sを吸い寄せた。", "You drag %s in."),o_name);
				object_wipe(&inven_add[i]);
				object_copy(&inven_add[i], q_ptr);
				floor_item_increase(item, -1);
				floor_item_describe(item);
				floor_item_optimize(item);

				p_ptr->update |= PU_BONUS;
			}
			if(!flag_pick)
			{
				msg_print(_("近くには動かせる物がないようだ・・", "There's nothing in sight you can move..."));
				break;
			}
			else if(flag_max)
			{
				msg_print(_("これ以上の数は扱いきれない。", "You cannot abosrb more items."));
				return NULL;
			}


		}
		break;
	case 5:
		{
			if(only_info) return format("");
			display_list_inven2();
			return NULL; //見るだけなので行動順消費なし
		}
		break;
	case 6:
		{
			object_type *o_ptr;
			bool flag_drop = FALSE;
			char o_name[MAX_NLEN];
			if(only_info) return format("");
			for(i=0;i<inven2_num;i++)
			{
				if(!inven_add[i].k_idx) continue;
				flag_drop = TRUE;
				o_ptr = &inven_add[i];
				object_desc(o_name, o_ptr, 0);
				msg_format(_("%sが落ちた。", "%s falls on the ground."),o_name);
				drop_near(o_ptr,0,py,px);
				object_wipe(&inven_add[i]);
				p_ptr->update |= PU_BONUS;
			}
			if(!flag_drop)
			{
				msg_print(_("降ろすものがない。", "You don't have anything to drop."));
				return NULL;
			}
		}
		break;
	case 7:
		{
			int ty,tx;
			bool thrown = FALSE;
			object_type *o_ptr;
			if(only_info) return format("");

			for(i=0;i<inven2_num;i++)
				if(inven_add[i].k_idx) thrown = TRUE;
			if(!thrown)
			{
				msg_print(_("飛ばすものがない。", "You don't have anything to throw."));
				return NULL;
			}

			if (!get_aim_dir(&dir)) return NULL;
			if ((dir == 5) && target_okay())
			{
				tx = target_col;
				ty = target_row;
			}
			else
			{
				tx = px + 99 * ddx[dir];
				ty = py + 99 * ddy[dir];
			}
			msg_print(_("周囲のアイテムを標的目掛けて一斉に飛ばした！", "You launch all the items floating around you at the target!"));

			for(i=0;i<inven2_num;i++)
			{
				if(!inven_add[i].k_idx) continue;
				thrown = TRUE;
				o_ptr = &inven_add[i];
				p_ptr->update |= PU_BONUS;

				if(do_cmd_throw_aux2(py,px,ty,tx,1,o_ptr,0))
					object_wipe(&inven_add[i]);
			}
		}
		break;
	case 8:
		{
			if(only_info) return format("");
			msg_print(_("あなたはスプーンを取り出し、精神を集中した・・", "You take out a spoon and gather your mind..."));
			p_ptr->csp += (4 + randint1(plev/10));
			if (p_ptr->csp >= p_ptr->msp)
			{
				p_ptr->csp = p_ptr->msp;
				p_ptr->csp_frac = 0;
			}
			p_ptr->redraw |= (PR_MANA);
		}
		break;

	case 9:
		{
			int rad = 2 + plev / 40;
			int damage = plev / 2 + 35 ;
			if(only_info) return format(_("損傷:%d*(10+1d10)", "dam: %d*(10+1d10)"),damage);
			msg_format(_("念動力の爆発を起こした！", "You cause a psychokinetic explosion!"));
			cast_meteor(damage, rad, GF_TELEKINESIS);

			break;
		}
	case 10:
		{
			int y, x;
			monster_type *m_ptr;
			int damage = plev * 5;

			if(only_info) return format(_str_eff_dam,damage);

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			y = target_row;
			x = target_col;

			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx)
			{
				if (!cave_naked_bold(y, x))
				{
					msg_print(_("そこには何かがあるようだ。やめておこう。",
                                "Something's already there. Better not do it."));
					return NULL;
				}
				msg_print(_("念動力で岩塊や土塊を積み重ねた。", "You stack rocks and soil with psychokinetic power."));
				cave_set_feat(y, x, f_tag_to_index_in_init("RUBBLE"));
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sへ向けて瓦礫が降り注いだ！", "Debris comes crashing upon %s!"),m_name);
				project(0,0,y,x,damage,GF_ARROW,PROJECT_KILL,-1);
			}

			break;
		}


	case 11:
		{
			int base = plev * 4;
			if(base < 150) base = 150;
			if(only_info) return format(_("損傷:%d+1d%d", "dam: %d+1d%d"),base,base);
			if(use_itemcard)
				msg_format(_("カードをなぞるとそれに従って周囲のものが吹き飛んだ！", "You trace a pattern on the card, and everything around you gets thrown around in the same pattern!"));
			else
				msg_format(_("周りの一切合財を引き寄せて掻き回して吹っ飛ばした！", "You drag everything around you together, spin it, then throw it aside!"));
			project_hack2(GF_DISTORTION,1,base,base);
			break;
		}
	case 12:
		{
			bool flag = FALSE;
			int i;
			int dice = plev / 5;
			int sides = 10 + chr_adj / 4;
			int num = 7;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);
			msg_print(_("全身全霊の超能力を放った！", "You put your entire body and soul into unleashing your supernatural powers!"));
			for(i=0;i<num;i++)
			{
				if(fire_random_target(GF_MANA, damroll(dice,sides),2, 0,0))flag = TRUE;
				if(i==0 && !flag)
				{
					msg_format(_str_mess_cant_find_enemies);
					break;
				}
			}
			break;
		}


	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}

	return "";
}

