cptr do_cmd_class_power_aux_shinmyou2(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int str_adj = adj_general[p_ptr->stat_ind[A_STR]];

	switch(num)
	{
	case 0:
		{
			int damage = 0;
			int range = 2 + plev / 20;

			damage = calc_weapon_dam(0) / 3;
			if (only_info) return format(_str_eff_range_dam,range,damage);

			if(!p_ptr->num_blow[0])
			{
				msg_print(_("ERROR:num_blow[0]が0", "ERROR: num_blow[0] is 0"));
				return NULL;
			}

			project_length = range;
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("鋭い突きを放った！", "You make a sharp thrust!"));
			fire_beam(GF_MISSILE, dir, damage);
			break;
		}
	case 1:
		{
			monster_type *m_ptr;
			int damage = 10 + plev + str_adj * 2;

			if(only_info) return format(_str_eff_dam,damage);

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !target_okay() || !projectable(target_row,target_col,py,px))
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}

			m_ptr = &m_list[cave[target_row][target_col].m_idx];

			if (!m_ptr->r_idx)
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sに針を引っ掛けた！", "You hook %s with a needle!"),m_name);
				project(0,0,target_row,target_col,damage,GF_TRAIN,PROJECT_KILL,-1);
			}

			break;
		}



	case 3:
		{
			int dam = 40 + plev * 4 + chr_adj * 2;
			int rad = 1 + plev / 16;

			if(only_info) return format(_str_eff_dam_around,dam/2);

			msg_print(_("足元から水と魚が噴き出した！", "Water and fish spout out from beneath your feet!"));

			project(0, rad, py, px, rad, GF_WATER_FLOW, PROJECT_GRID, -1);
			project(0, rad, py, px, dam, GF_WATER, (PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM), -1);

			break;
		}
		//v1.1.59　専用性格にて追加
	case 4:
		{
			int snum = plev / 7;
			int i;
			bool flag = FALSE;

			if (only_info) return format("");


			for (i = 0; i<snum; i++)
			{
				if (summon_specific(-1, py, px, plev+20, SUMMON_GENSOUKYOU, (PM_FORCE_FRIENDLY | PM_ALLOW_UNIQUE))) flag = TRUE;
			}
			if (flag) msg_format(_("あなたの呼びかけに幻想郷の住人たちが応えた！", "Residents of Gensoukyou answer your call!"));
			else msg_format(_("誰もあなたの呼びかけに応えなかった...", "Nobody answers your call..."));

		}
		break;

	case 5:
	{
		int i;
		int damage = 0;
		int num_blow = p_ptr->num_blow[0];
		int range = 2 + plev / 20;
		int flag_attack_done = FALSE;

		if (num_blow < 1) num_blow = 1;

		damage = calc_weapon_dam(0) / num_blow;
		if (only_info) return format(_("射程:%d 損傷:%d*%d", "rng: %d dam: %d*%d"), range, damage,num_blow);

		msg_print(_("目を狙って突きかかった！", "You thrust, aiming for the eyes!"));
		for (i = 0; i < num_blow; i++)
		{
			if(fire_random_target(GF_CONFUSION, damage, 2, 0, range)) flag_attack_done = TRUE;
		}
		if (!flag_attack_done)
			msg_print(_("しかし標的がいなかった。", "However, there were no appropriate targets."));
		break;
	}


	case 6:
	case 2:
		{
			int y, x;
			monster_type *m_ptr;

			if(only_info) return format("");
			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if (!m_ptr->r_idx || !m_ptr->ml)
			{
				msg_format(_str_mess_nobody_here);
				return NULL;
			}
			else if(num == 2)
			{
				char m_name[80];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sへ目にも留まらぬ連撃を放った！", "You attack %s at a blinding speed!"),m_name);
				py_attack(y,x,0);
				if(m_ptr->r_idx)
				{
					int delay = plev + randint1(plev);
					if(r_ptr->flags1 & RF1_UNIQUE || r_ptr->flags7 & RF7_UNIQUE2) delay /= 2;
					if(r_ptr->flags2 & RF2_GIGANTIC) delay -= 30;
					if(r_ptr->flags2 & RF2_POWERFUL) delay -= 20;
					if(delay < 0) delay = 0;
					m_ptr->energy_need += delay;

					if(!delay)
						msg_format(_("%sは全く怯んでいない！", "%s does not flinch!"),m_name);
					else if(delay < 30)
						msg_format(_("%sの動きが一瞬止まった！", "%s hesitates for a slight moment!"),m_name);
					else
						msg_format(_("%sは攻撃に怯んでいる！", "%s flinches from your attack!"),m_name);
				}
			}
			else
			{
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sへ猛然と斬りかかった！", "You furiously cut %s!"),m_name);
				py_attack(y,x,HISSATSU_SHINMYOU);
			}

			break;
		}
	case 7: //輝針剣
		{
			int dice = p_ptr->lev / 5;
			int sides = 3 + chr_adj / 5;
			int num = p_ptr->lev / 10 + adj_general[p_ptr->stat_ind[A_CHR]] / 6;
			if(p_ptr->mimic_form == MIMIC_GIGANTIC) dice *= 2;
			if(only_info) return format(_str_eff_dam_dice_sides_mult_,dice,sides,num);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("眩く輝く%s刃を放った！", "You throw a %sglimmering blade!"),(p_ptr->mimic_form==MIMIC_GIGANTIC)?_("巨大な", "giant "):"");
			fire_blast(GF_PSY_SPEAR, dir, dice, sides, num, 2,(PROJECT_BEAM | PROJECT_KILL | PROJECT_GRID | PROJECT_ITEM));

			break;
		}

	case 8:
		{
			int base = 12;
			int sides = 12;

			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			msg_print(_("あなたは小槌で自分の頭を叩いた。", "You hit yourself on the head with your mallet."));
			if(!set_mimic(base+randint1(base), MIMIC_SHINMYOU, FALSE)) return NULL;
			break;
		}



	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



