// p_ptr->magic_num1[0]をグリーンアイドモンスターのターゲットIDXに使用
// p_ptr->magic_num1[1]を積怨返しのダメージカウントに使用　←parsee_damage_countを使うことにした
// p_ptr->magic_num1[4]を丑の刻参りのターゲットモンスターm_idxに、magic_num2[4]を呪いカウントに使う
cptr do_cmd_class_power_aux_parsee(int num, bool only_info)
{
	int dir,dice,sides,i;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{
	case 0:
		{
			int power;
			monster_type *m_ptr;
			monster_race *r_ptr;
			if(only_info) return format(_("効力:不定", "pow: undef"));

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !projectable(target_row,target_col,py,px) || !cave[target_row][target_col].m_idx)
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			m_ptr = &m_list[cave[target_row][target_col].m_idx];
			r_ptr = &r_info[m_ptr->r_idx];

			power = (r_ptr->level - plev);
			if(power < 1)
			{
				msg_print(_("何だか妬む気持ちにならない。", "For some reason, you can't bring yourself to feel jealous."));
				return NULL;
			}
			power = power * 10 + plev * 2;
			msg_print(_("あなたの目が緑色に爛々と輝いた...", "Your eyes glimmer green..."));
			fire_ball_hide(GF_OLD_CONF,dir,power,0);
			fire_ball_hide(GF_TURN_ALL,dir,power,0);

			break;
		}
	case 1:
		{
			int power, target_num=0;
			if(only_info) return format(_("効力:不定", "pow: undef"));
			for (i = 1; i < m_max; i++)
			{
				monster_type *m_ptr;
				m_ptr = &m_list[i];
				if(!m_ptr->r_idx) continue;
				if(!m_ptr->ml || !projectable(py, px, m_ptr->fy, m_ptr->fx)) continue;
				if (!is_hostile(m_ptr)) continue;
				target_num++;
			}
			if(!target_num)
			{
				msg_format(_("誰もいない・・", "Nobody's here..."));
				return NULL;
			}
			power = 20 + target_num * 20;
			msg_format(_("嫉妬の念が周囲の者を狂わせた・・", "You instill jealous thoughts into those around you..."));
			confuse_monsters(power);
			break;
		}

	case 2:
	{
		int power = p_ptr->lev * 2 + chr_adj * 2;
		int y, x;
		monster_type *m_ptr;
		monster_race *r_ptr;

		if (only_info) return format(_str_eff_power, power);
		if (!get_rep_dir2(&dir)) return NULL;
		if (dir == 5) return NULL;

		y = py + ddy[dir];
		x = px + ddx[dir];
		m_ptr = &m_list[cave[y][x].m_idx];

		if (cave[y][x].m_idx && (m_ptr->ml))
		{
			char m_name[80];
			r_ptr = &r_info[m_ptr->r_idx];

			monster_desc(m_name, m_ptr, 0);
			msg_format(_("あなたは%sに近寄って囁きかけた…", "You lean close to %s and whisper..."), m_name);
			(void)set_monster_csleep(cave[y][x].m_idx, 0);

			if (r_ptr->flags3 & RF3_NO_CONF) power /= 2;
			if (MON_CONFUSED(m_ptr) || MON_MONFEAR(m_ptr)) power *= 2;

			if (r_ptr->flags2 & RF2_EMPTY_MIND || r_ptr->flagsr & RFR_RES_ALL)
				msg_format(_("%sは無反応だ。", "%s does not respond."), m_name);
			else if (r_ptr->flags1 & (RF1_UNIQUE | RF1_QUESTOR))
				msg_format(_("%sはあなたの声に耳を貸さない！", "%s won't listen to you!"), m_name);
			else if (m_ptr->mflag & MFLAG_SPECIAL)
				msg_format(_("%sにはすでに効いている。", "%s is already under the effect."), m_name);
			else if (r_ptr->level > randint1(power) || is_pet(m_ptr))
				msg_format(_("%sは惑わされなかった。", "%s didn't fall for it."), m_name);
			else
			{
				msg_format(_("%sの目に緑色の光が宿った！", "The eyes of %s glow green!"), m_name);
				m_ptr->mflag |= MFLAG_SPECIAL;
			}
		}
		else
		{
			msg_format(_str_mess_nobody_here);
			return NULL;
		}
		break;
	}
	case 3:
		{
			int i,cnt=0;
			monster_type *m_ptr;
			bool flg_g_mon = FALSE;
			if(only_info) return "";
			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_GREEN_EYED)) flg_g_mon = TRUE;
				if((m_ptr->r_idx == MON_PARSEE) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt)
			{
				msg_format(_("すでに術を実行している。", "This technique already is in effect."),num);
				return NULL;
			}
			if(flg_g_mon)
			{
				msg_format(_("既にフロアに怪物を呼んでいる。", "You already have called a monster to this level."));
				return NULL;
			}

			if(summon_named_creature(0, py, px, MON_PARSEE, PM_EPHEMERA))
				msg_print(_("あなたの幻影が現れた。", "Your phantom appears."));
			else
				msg_print(_("術に失敗した。", "The technique failed."));


			break;
		}
	case 4:
		{
			int rad = 2;
			if(p_ptr->lev > 39) rad = 3;
			dice = (5 + p_ptr->lev / 10) * (100 + (p_ptr->mhp - p_ptr->chp) * 600 / p_ptr->mhp) / 100;
			sides = 20 + chr_adj/ 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("心張り裂ける一撃を放った。", "You deliver a heart-rending strike."));
			fire_ball(GF_REDEYE, dir, damroll(dice,sides), rad);
			break;
		}

	case 5:
	{
		if (only_info) return format("");

		msg_print(_("あなたの怨嗟の念が大地に満ちた・・", "Your grudge permeates the earth..."));
		num_repro += MAX_REPRO;
		break;
	}

	case 6:

	{
		int power = plev * 7;
		int target_m_idx;
		monster_type *m_ptr;
		char m_name[160];
		if (only_info) return format(_str_eff_power, power);

		target_m_idx = p_ptr->magic_num1[4];

		if (p_ptr->health_who && m_list[p_ptr->health_who].ml)
		{
			int tmp_m_idx;
			//技を使うところの視界内に誰かがいると儀式大失敗で太古の怨念発動
			tmp_m_idx = check_player_is_seen();
			if (tmp_m_idx)
			{
				int count = 0;
				int i;
				monster_desc(m_name, &m_list[tmp_m_idx], 0);

				msg_format(_("呪いの儀式を%sに見られてしまった！呪いが逆流した！",
                            "%s has seen you perform the curse ritual! The curse turns on you!"),m_name);

				activate_ty_curse(FALSE, &count);

				//カウントリセット
				p_ptr->magic_num1[4] = 0;
				p_ptr->magic_num2[4] = 0;
				break;

			}

			m_ptr = &m_list[p_ptr->health_who];
			monster_desc(m_name, m_ptr, 0);

			if (p_ptr->health_who == target_m_idx)
			{

				msg_print(_("あなたは一心不乱に藁人形に釘を打ち付けている...",
                            "You singlemindedly hammer nails into straw dolls..."));
				p_ptr->magic_num2[4] += 1;

				if (p_ptr->magic_num2[4] == 7)
				{
					monster_desc(m_name, m_ptr, 0);

					msg_format(_("%sに対する呪いが成就した！",
                                "You complete your curse against %s!"), m_name);
					project(0, 0, m_ptr->fy, m_ptr->fx, power, GF_DEC_ALL,  PROJECT_JUMP | PROJECT_KILL | PROJECT_HIDE, -1);
					anger_monster(m_ptr);
					set_monster_csleep(target_m_idx, 0);

					//カウントリセット
					p_ptr->magic_num1[4] = 0;
					p_ptr->magic_num2[4] = 0;
				}
				else
				{
					//ときどき起きる
					if (one_in_(6))
					{
						if (set_monster_csleep(target_m_idx, 0))
							msg_format(_("%sは目を覚ました。", "%s wakes up."),m_name);
					}

				}

			}
			else
			{
				msg_format(_("あなたは%sの名前を書いた藁人形に釘を打ち付け始めた。",
                            "You start hammering nails into a straw doll with '%s' written on it."),m_name);
				p_ptr->magic_num1[4] = p_ptr->health_who;
				p_ptr->magic_num2[4] = 1;

			}
		}
		else
		{
			msg_print(_("呪いの標的がいない。", "There's no target to curse."));
			return NULL;
		}

		break;
	}

	case 7:
		{
			int i;
			bool flg_success = FALSE;
			bool flg_g_mon = FALSE;
			bool flg_parsee = FALSE;
			monster_type *m_ptr;
			if(only_info) return format("");

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_GREEN_EYED)) flg_g_mon = TRUE;
				if((m_ptr->r_idx == MON_PARSEE) && m_ptr->mflag & MFLAG_EPHEMERA) flg_parsee = TRUE;
			}
			if(flg_g_mon)
			{
				msg_format(_("既にフロアに怪物を呼んでいる。", "You already have called a monster to this level."));
				return NULL;
			}
			else if(flg_parsee)
			{
				msg_format(_("今は怪物を呼べない。", "You can't summon the monster right now."));
				return NULL;
			}

			if (!get_aim_dir(&dir)) return NULL;
			if(dir != 5 || !projectable(target_row,target_col,py,px) || !cave[target_row][target_col].m_idx)
			{
				msg_print(_("視界内のターゲットを明示的に指定しないといけない。",
                            "You have to pick a target in sight."));
				return NULL;
			}
			p_ptr->magic_num1[0] = cave[target_row][target_col].m_idx;

			if(summon_named_creature(0, py, px, MON_GREEN_EYED, PM_EPHEMERA)) flg_success = TRUE;

			if(flg_success)	msg_print(_("緑目の怪物が現れた！", "A green-eyed monster appears!"));
			else msg_print(_("怪物は現れなかった。。", "The monster didn't appear..."));

		break;
		}

	case 8:
	{
		int base = 5;
		if (only_info) return format(_("範囲:%d 期間:%d+1d%d", "rad: %d dur: %d+1d%d"),MAX_SIGHT,base,base);

		set_tim_general(base+randint1(base), FALSE, 0, 0);

	}
	break;

	///mod160319 積怨返しのダメージカウントを外部変数にした
	case 9:
		{
			int damage = parsee_damage_count;
			int rad;
			if(damage > 9999) damage = 9999;
			rad = 4 + damage / 2000;

			if(only_info) return format(_("損傷:最大%d", "dam: up to %d"),damage / 2);

			if(damage < 1)
			{
				msg_print(_("まだ恨みが積もっていない。", "You haven't accumulated grudges yet."));
				return NULL;
			}
			if(damage > 1000) msg_format(_("積もりに積もった恨みの念が大爆発を起こした！",
                                            "All of your accumulated resentful thoughts cause a massive explosion!"));
			else msg_format(_("恨みの念が炎となって燃え上がった！", "Your resentful thoughts turn into flame and flare up!"));
			project(0, rad, py, px, damage, GF_HELL_FIRE, PROJECT_KILL | PROJECT_ITEM | PROJECT_GRID, -1);
			parsee_damage_count = 0L ; //Be happy
			break;
		}

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}



