//連撃カウントとしてconcent使用
//p_ptr->magic_num1[0]を消費行動力として使用

cptr do_cmd_class_power_aux_meirin(int num, bool only_info)
{
	int dir;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];
	int cons = p_ptr->concent;
	int use_energy = p_ptr->magic_num1[0];

	switch(num)
	{

	case 0://気配察知
		{
			int rad = 15 + plev / 5;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("気の流れを感じ取った・・", "You start sensing the flow of Ki..."));
			if(plev > 19) map_area(rad);
			if(plev > 29) detect_monsters_normal(rad);
			else detect_monsters_living(rad);
		}
		break;
	case 1:
		{
			int time;
			int heal;
			heal = plev / 5 + 5;
			if(only_info) return format(_("回復:%d+1d%d", "heal: %d+1d%d"),heal,heal);
			time = 2 + randint1(3);
			msg_print(_("気を高めた。", "You raise your Ki."));
			hp_player(heal + randint1(heal));
			set_stun(p_ptr->stun - 10);
			set_cut(p_ptr->cut - 10);
			set_afraid(0);
			set_hero(time,FALSE);
			set_tim_addstat(A_STR,(2 + plev / 10),time,FALSE);

		}
		break;
	case 2:
		{
			int rad = 1 + plev / 30;
			int dice = 1;
			int sides = 50 + chr_adj * 5;
			int base = plev * 2;
			if(base < 30) base = 30;

			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			if(use_itemcard)
				msg_format(_("カードから虹色の弾が放たれた！", "A rainbow-colored bullet shoots out of the card!"));
			else
				msg_format(_("気弾を放った！", "You fire a bullet of Ki!"));
			fire_rocket(GF_FORCE, dir, base + damroll(dice,sides), rad);
			break;
		}

	case 3:
		{
			int base = plev + chr_adj;
			int num = 3 + plev / 24 + chr_adj / 12;
			int rad = 2 + plev / 15;
			int i;
			if(only_info) return format(_("損傷:最大%d * %d", "dam: up to %d*%d"),base/2,num);
			msg_print(_("虹色の竜巻を巻き起こした！", "You cause a rainbow-colored tornado!"));
			for (i = 0; i < num; i++) project(0, rad, py, px, base, GF_WINDCUTTER, PROJECT_KILL | PROJECT_ITEM, -1);
			break;

		}
		break;
	case 4:
		{
			int len = 3 + (plev-25) / 6;
			int damage = 50 + plev * 2;
			if(only_info) return format(_str_eff_range_dam,len,damage);
			if (!rush_attack2(len,GF_MISSILE,damage,0)) return NULL;

			//連撃カウント増加、行動消費減少
			new_class_power_change_energy_need = use_energy;
			do_cmd_concentrate(0);

			break;
		}

	case 5:
		{
			int time;
			int base = 15;
			if(only_info) return format(_("期間:1d%d + %d", "dur: 1d%d + %d"),base,base);
			time = base + randint1(base);
			msg_print(_("虹色の気を纏った！", "You shroud yourself in rainbow Ki!"));
			set_oppose_elec(time, FALSE);
			set_oppose_fire(time, FALSE);
			set_oppose_cold(time, FALSE);
			set_shield(time,FALSE);
		}
		break;
	case 6:
		{
			int y, x;
			int dam = (plev + chr_adj + p_ptr->to_d[0]) * (7 + cons) / 7;
			if(only_info) return format(_str_eff_dam,dam);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];

			if (d_info[dungeon_type].flags1 & DF1_NO_MELEE)
			{
				msg_print(_("なぜか攻撃できない。", "You couldn't attack for some reason."));
				break;
			}
			else if (cave[y][x].m_idx)
			{
				msg_print(_("天を駆けるような飛び蹴りを放った！", "You launch a flying kick, as if leaping straight into the sky!"));
				project(0,0,y,x,dam,GF_ARROW,PROJECT_KILL,-1);
			}
			else
			{
				msg_print(_("その方向にはモンスターはいません。", "There's no monster in this direction."));
				return NULL;
			}

			if (cave[y][x].m_idx)
			{
				int i;
				int ty = y, tx = x;
				int oy = y, ox = x;
				int m_idx = cave[y][x].m_idx;
				monster_type *m_ptr = &m_list[m_idx];
				monster_race *r_ptr = &r_info[m_ptr->r_idx];

				char m_name[80];

				if(!(r_ptr->flags2 & RF2_GIGANTIC) || cons > 9)
				{


					monster_desc(m_name, m_ptr, 0);

					for (i = 0; i < 5; i++)
					{
						y += ddy[dir];
						x += ddx[dir];
						if (cave_empty_bold(y, x))
						{
							ty = y;
							tx = x;
						}
						else break;
					}
					if ((ty != oy) || (tx != ox))
					{
						msg_format(_("%sを吹き飛ばした！", "%s got thrown away!"), m_name);
						cave[oy][ox].m_idx = 0;
						cave[ty][tx].m_idx = m_idx;
						m_ptr->fy = ty;
						m_ptr->fx = tx;

						update_mon(m_idx, TRUE);
						lite_spot(oy, ox);
						lite_spot(ty, tx);

						if (r_info[m_ptr->r_idx].flags7 & (RF7_LITE_MASK | RF7_DARK_MASK))
							p_ptr->update |= (PU_MON_LITE);
					}
				}
			}
		}
		break;

	case 7:
		{
			int rad = 5;
			int dice = 1;
			int sides = p_ptr->csp * 2;
			int base = p_ptr->csp;
			if(only_info) return format(_str_eff_dam_base_dice_sides,base,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("巨大な気弾を放った！", "You fire a huge bullet of Ki!"));
			fire_rocket(GF_FORCE, dir, base + damroll(dice,sides), rad);
			break;
		}

	case 8:
		{
			int time;
			int base = 5;
			if(only_info) return format(_str_eff_dur_plus_1d,base,base);
			time = base + randint1(base);
			if(use_itemcard)
				msg_print(_("力が溢れてくる！", "You feel power overflowing!"));
			else
				msg_print(_("気を爆発的に高めた！", "Your Ki rises drastically!"));
			do_res_stat(A_STR);
			set_tim_addstat(A_STR,110,time,FALSE);
			set_afraid(0);
			set_hero(time,FALSE);
			if(!use_itemcard) do_cmd_concentrate(1);

		}
		break;
	case 9:
		{
			int y, x;
			int dam = (plev + chr_adj * 2 + p_ptr->to_d[0]) * (10 + cons) / 10 * p_ptr->csp / 120;
			if(only_info) return format(_str_eff_dam,dam);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];

			if (cave[y][x].m_idx)
			{
				int m_idx = cave[y][x].m_idx;
				monster_type *m_ptr = &m_list[m_idx];
				char m_name[80];
				monster_desc(m_name, m_ptr, 0);

				msg_format(_("%sの体から虹色の光が漏れ出し、大爆発を起こした！",
                            "Rainbow light flows out of %s, and causes an explosion!"),m_name);
				project(0,7,y,x,dam,GF_FORCE,PROJECT_KILL,-1);
			}
			else
			{
				msg_print(_("その方向にはモンスターはいません。", "There's no monster in that direction."));
				return NULL;
			}

		}
		break;


	default:
		if(only_info) return format(_str_unimp);
		msg_format(_str_unimp_error,num);
		return NULL;
	}

	if(!use_itemcard && (use_energy < 20 || use_energy > 100))
	{
		msg_format(_("ERROR:use_energy計算値がおかしい(%d)",
                    "ERROR: Weird calculation for use_energy (%d)"),use_energy);
		new_class_power_change_energy_need = 100;

	}

	return "";
}



