//要石設置座標と地震吸収回数にp_ptr->magic_num1[0-3]を使う
cptr do_cmd_class_power_aux_tenshi(int num, bool only_info)
{
	int dir,dice,sides,base;
	int plev = p_ptr->lev;
	int chr_adj = adj_general[p_ptr->stat_ind[A_CHR]];

	switch(num)
	{

	case 0:
		{
			int rad = 5 + plev / 3;
			if(only_info) return format(_str_eff_area,rad);

			msg_print(_("剣を地面に突き立てた。", "You drive your sword into the ground."));
			earthquake(py,px,rad);

			break;
		}
	case 1:
		{
			int damage = (20 + plev * 2  + chr_adj * 2);
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_print(_("レーザーを放った。", "You fire a laser."));
			fire_beam(GF_LITE, dir, damage);

			break;
		}
	case 2:
	{
			int y, x;

			int damage = 20 + plev * 3;
			monster_type *m_ptr;

			if(plev > 29) damage += plev * 2;
			if(only_info) return format(_str_eff_dam,damage);

			if (!get_rep_dir2(&dir)) return NULL;
			if (dir == 5) return NULL;

			y = py + ddy[dir];
			x = px + ddx[dir];
			m_ptr = &m_list[cave[y][x].m_idx];

			if(!cave_have_flag_bold(y,x,FF_MOVE) && !cave_have_flag_bold(y,x,FF_CAN_FLY))
			{
				msg_print(_("その場所に対しては使えない。", "You can't use it in that location."));
				return NULL;
			}
			if(plev < 30) msg_print(_("石柱が空から降ってきた。", "A stone pillar falls down from above."));
			else msg_print(_("大岩が空から降ってきた。", "A large rock falls down from above."));

			if (cave[y][x].m_idx)
			{
				project(0,0,y,x,damage,GF_ARROW,PROJECT_KILL,-1);
			}
			else if(plev > 29 && cave_naked_bold(y, x))
			{
				cave_set_feat(y, x, f_tag_to_index_in_init("RUBBLE"));
			}

			break;
		}
	case 3:
		{
			if(only_info) return format("");

			if(p_ptr->magic_num1[0])
			{
				msg_format(_("このフロアにはすでに要石を設置している。", "There's already a keystone set on this level."));
				return NULL;
			}
			else if(!cave_have_flag_bold(py,px,FF_FLOOR))
			{
				msg_format(_("ここに要石を設置することはできない。", "You cannot set a keystone here."));
				return NULL;
			}

			msg_format(_("あなたは要石を設置した。要石は地面に沈み込んでいった・・",
                        "You set a keystone. It sinks into the ground..."));
			p_ptr->magic_num1[0] = py;
			p_ptr->magic_num1[1] = px;

		}
		break;
	case 4:
		{
			if(only_info) return format("");
			if(!p_ptr->magic_num1[0])
			{
				msg_format(_("このフロアにはまだ要石を設置していない。", "There's no keystone set on this level."));
				return NULL;
			}

			msg_print(_("このフロアの要石を大地から引っこ抜いた！", "You draw the keystone on this level out of the ground!"));
			kanameishi_break();

		}
		break;

	case 5:
		{
			dice = p_ptr->lev / 3;
			sides = 20 + chr_adj / 2;
			if(only_info) return format(_str_eff_dam_dice_sides,dice,sides);

			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("剣から熱線が放たれた！", "Heat ray shoots out of your sword!"));
			fire_spark(GF_PLASMA,dir, damroll(dice,sides),1);

			break;
		}
	case 6:
		{
			int time;
			base = plev/2;
			sides = plev/2;

			if(base < 15) base = 15;
			if(sides < 15) sides = 15;

			if(only_info) return format(_str_eff_dur_plus_1d,base,sides);
			time = base + randint1(sides);
			if(use_itemcard)
				msg_format(_("体が鉄のように丈夫になった気がする！", "You feel your body turn as hard as iron!"));
			else
				msg_format(_("あなたは気合を入れた！", "You focus intensely!"));
			set_shield(time, FALSE);
			set_resist_magic(time,FALSE);
			break;
		}
		break;
	case 7:
		{
			int max_kaname = (plev - 20) / 3;
			int i,cnt=0;
			monster_type *m_ptr;

			if(max_kaname < 3) max_kaname = 3;

			if(only_info) return format(_("最大:%d", "up to %d"),max_kaname);

			for (i = 1; i < m_max; i++)
			{
				m_ptr = &m_list[i];
				if (!m_ptr->r_idx) continue;
				if((m_ptr->r_idx == MON_KANAME) && (m_ptr->mflag & MFLAG_EPHEMERA)) cnt++;
			}
			if(cnt >= max_kaname)
			{
				msg_format(_("これ以上配置できない。", "You cannot place any more."),num);
				return NULL;
			}

			for(i=0;i<(max_kaname / 3);i++)(void)summon_named_creature(0, py, px, MON_KANAME, PM_EPHEMERA);

		}
		break;
	case 8:
		{
			int dam = plev * 3 + chr_adj * 5;

			if(only_info) return format(_str_eff_dam,dam);
			msg_format(_("大地が広範囲に隆起した！", "The ground around you suddenly rises upwards!"));
			project_hack2(GF_GRAVITY,0,0,dam);
			break;
		}
	case 9:
		{
			int rad = 3;
			if(only_info) return format(_str_eff_area,rad);
			if(!dun_level)
			{
				msg_format(_("ここでは使えない。", "You cannot use it here."));
				return NULL;
			}

			msg_print(_("地殻変動が起こった！", "A localized earthquake happens!"));
			destroy_area(py,px,rad,FALSE,FALSE,TRUE);
		}
		break;
	case 10:
		{
			int damage = p_ptr->lev * 9 + chr_adj * 10;
			if(damage < 400) damage = 400;
			if(only_info) return format(_str_eff_dam,damage);
			if (!get_aim_dir(&dir)) return NULL;
			msg_format(_("緋想の剣から凝縮された気質が放たれた！",
                        "You release condensed temperament from Sword of Hisou!"));
			fire_spark(GF_FORCE,dir,damage,2);

			break;
		}
		break;

	default:
		if(only_info) return _str_unimp;
		msg_format(_str_unimp_error,num);
		return NULL;
	}
	return "";
}


